/*

IMAGE <= For information on the coloring and lighting

BUFFER A <= For information on the particle simulation & animation


BUFFER A
******************************************************

MAIN FEATURES:


*/

//a struct for holding ball data during collisio tests
struct Ball{
    int id;
    float r;
    vec2 pos;
    vec2 vel;

};



//global collision flag
float collided = 0.;

vec4 wallCollision(inout vec2 pos,inout vec2 vel){
    //just test if we hit a wall but use ASP since the x axis
    //is stretched compared to y. (maybe not for all screens I don't know)
    //if the particle collides with any wall we set teh collision flag to = 1.
    vec2 tmp = pos + vel;    
    if(tmp.y + RADIUS > 1.){pos.y = 1.-RADIUS*1.01;vel.y=-vel.y;
    collided = 1.;}
    else if(tmp.y-RADIUS < -1.){pos.y = -1.+RADIUS*1.01;vel.y=-vel.y;
    collided = 1.;}
    if(tmp.x + RADIUS > ASP){pos.x = ASP-RADIUS*1.01;vel.x=-vel.x;
    collided = 1.;}
    else if(tmp.x-RADIUS < -ASP){pos.x = -ASP+RADIUS*1.01;vel.x=-vel.x;
    collided = 1.;}
    return vec4(pos,vel);

}
//we check if the ball is overlapping any other particle
vec4 ballOverlap(inout vec2 pos, inout vec2 vel, vec2 fragCoord){
    int thisID = int(floor(fragCoord.x));

    for(int i = 0; i < BALL_NUM; i++){

        if(i == thisID){continue;}//skip when we get the same particle
        Ball b;

        //load second particle and create an object to hold it
        vec4 stuff = load0(vec2(float(i)+0.5,0.5));
        b.pos = stuff.xy;
        b.vel = stuff.zw;

        //check overlapp
        float dist = length(pos-b.pos);
        float diff = dist-RADIUS*2.;
        //if overlap
        if(diff < 0.){
            //basically omve things away from each other in the
            //direction opposite the normal by the amount of overlap
            float halfDiff = 0.999*(diff);
            vec2 normal = normalize(pos-b.pos);
            pos -= normal*halfDiff;
            //set collided flag
            collided = 1.;

        }
    }
    return vec4(0.);
}

//check particle collision, 
//This is mostly the same as above, I probably could combine them.
vec4 ballCollision(inout vec2 pos, inout vec2 vel, vec2 fragCoord){
    int thisID = int(floor(fragCoord.x));

    for(int i = 0; i < BALL_NUM; i++){

        if(i == thisID){continue;}//skip same ball
        Ball b;
        vec4 stuff = load0(vec2(float(i)+0.5,0.5));

        b.r = RADIUS;
        b.pos = stuff.xy;
        b.vel = stuff.zw;

        float dist = length(pos-b.pos);
        float diff = dist-RADIUS*2.;

        if(diff < 0.){
            //if overlap just exchange velocities
            vel = b.vel;
            collided = 1.;
        }
    }
    return vec4(0.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = UV01;
    fragColor = vec4(0.0,0.0,1.0,1.0);

    //what we do on the very first frame
    if(iFrame < 1){
        //if the pixels coordinate is in the region for storing
        //particle position and velocity informaiton
        if(fragCoord.x < float(BALL_NUM) && fragCoord.y == 0.5){

            //set it's data to a calculated point on the screen
            //and a small random velocity between -0.005 and .005 for xy
            fragColor = vec4(
            mod(fragCoord.x,floor(2.*ASP/(RADIUS*2.)))*RADIUS*2.-1.*ASP+0.1,
            1.-RADIUS-floor(fragCoord.x/floor(2.*ASP/(RADIUS*2.)))*RADIUS*2.,
            rnd(fragCoord.x+901.)*0.01-0.005,
            rnd(fragCoord.x)*0.01)-0.005;
        }
        //if the pixels coordinate is in the region for storing
        //particle animation information, set it to 0
        else if(fragCoord.x < float(BALL_NUM) && fragCoord.y == 1.5){
            fragColor.x = 0.;
        }

   }
   //if we are beyond the first frame, 
   else{
       //as long as we are withing the region of the particles, 
       //regardless of which part, get the posision and velocity
       //(even if it doesn't exist, doesn't really matter
        if(fragCoord.x < float(BALL_NUM) && fragCoord.y < 3.5){
            vec2 pos = load0(vec2(fragCoord.x,0.5)).xy;
            vec2 old_vel = load0(vec2(fragCoord.x,0.5)).zw;
            //save access to the old velocity since the new one will
            //change
            vec2 vel = old_vel;

            //we send the velocity through collisions with particles
            //and with walls, as well as checking for overlaps
            ballCollision(pos,vel,fragCoord);;
            ballOverlap(pos,vel,fragCoord);
            wallCollision(pos,vel);
            //after all that we have a final velocity whcih we 
            //add to position
            pos += vel;

            //now, if our pixels position is on the pos and vel of the
            //particle, we can now update
            if(fragCoord.y == .5){
                fragColor = vec4(pos,vel);
            }
            //otherwise, if the pixel is over the animaton segment
            //for a given particle, we do the animation stuff
            else if(fragCoord.y == 1.5){
                //get collision data to know if are already reacting
                //to a collision or not
                vec2 col_data = load0(vec2(fragCoord.x,1.5)).xy;
                //get animaiton data to know where we are in an animation
                //if we are in one at all.
                vec2 anim_data = load0(vec2(fragCoord.x,1.5)).zw;

                //if collided but it was a while ago, reset
                if(col_data.y == 1. && (iTime-anim_data.x) > DURATION){

                    fragColor = vec4(0.,0.,0.,atan(vel.x,vel.y));
                    //store current angle so in image both old and new
                    //are the same

                }
                //new collision, set flag to 1 and set start time
                else if(collided == 1. && col_data.y == 0.){

                    fragColor.x = col_data.x;
                    fragColor.y = 1.;//collision now = 1 (true)
                    fragColor.z = iTime;//the time of the collision 
                    //this was used for rotating arrows in Image
                    //it's unused right now.
                    fragColor.w = atan(old_vel.x,old_vel.y);
                    //store old angle so in main we can animnate betwen
                    //old and new current angle.

                }    
                //if already collided, update animation
                else if(col_data.y == 1.){
                    //just update how far we are in the animation
                    //from 0 to 1.
                    fragColor.x = (iTime-anim_data.x) / DURATION;
                    //everything else is just refreshing
                    fragColor.y = col_data.y;//refresh collision flag
                    fragColor.z = anim_data.x; //refresh ( I think always 0)
                    fragColor.w = anim_data.y; //refresh angle

                }
                //if nothing happened just refresch all
                else if(collided == 0. && col_data.y == 0.){
                    fragColor.x = col_data.x;
                    fragColor.y = col_data.y;
                    fragColor.z = anim_data.x;
                    fragColor.w = anim_data.y;
                }
            }
        }
    }
}

