// CC0: Spiral "domain mapping"
// Was tinkering with getting a spiral domain mapping to work.
// While there are distortions I think it it turned out well enough to be useful to me


#define RESOLUTION  iResolution
#define TIME        iTime
#define PI          3.141592654
#define TAU         (2.0*PI)


// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
float mod1(inout float p, float size) {
  float halfsize = size*0.5;
  float c = floor((p + halfsize)/size);
  p = mod(p + halfsize, size) - halfsize;
  return c;
}

float spiralLength(float b, float a) {
  // https://en.wikipedia.org/wiki/Archimedean_spiral
  return 0.5*b*(a*sqrt(1.0+a*a)+log(a+sqrt(1.0+a*a)));
}

void spiralMod(inout vec2 p, float a) {
  vec2 op     = p;
  float b     = a/TAU;
  float  rr   = length(op);
  float  aa   = atan(op.y, op.x);
  rr         -= aa*b;
  float nn    = mod1(rr, a);
  float sa    = aa + TAU*nn;
  float sl    = spiralLength(b, sa);
  p           = vec2(sl, rr);
}


// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

// License: Unknown, author: Unknown, found: don't remember
float hash(float co) {
  return fract(sin(co*12.9898) * 13758.5453);
}

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

float circle(vec2 p, float r) {
  return length(p) - r;
}

// https://iquilezles.org/articles/distfunctions2d
float vesica(vec2 p, float r, float d) {
  p = abs(p);
  float b = sqrt(r*r-d*d);
  return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                           : length(p-vec2(-d,0.0))-r;
}

// https://iquilezles.org/articles/smin
float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

float eye(vec2 p, float h) {
  float a  = mix(0.0, 0.85, smoothstep(0.995, 1.0, cos(TAU*(TIME+h)/5.0)));
  const float b = 4.0;
  float rr = mix(1.6, b, a);
  float dd = mix(1.12, b, a);

  vec2 p0 = p;
  p0 = p0.yx;
  float d0 =  vesica(p0, rr, dd);
  float d5 = d0;

  vec2 p1 = p;
  p1.y -= 0.28;
  float d1 = circle(p1, 0.622);
  d1 = max(d1,d0);

  vec2 p2 = p;
  p2 -= vec2(-0.155, 0.35);
  float d2 = circle(p2, 0.065);

  vec2 p3 = p;
  p3.y -= 0.28;
  p3 = toPolar(p3);
  float n3 = mod1(p3.x, 0.05);
  float d3 = abs(p3.x)-0.0125*(1.0-length(p1));

  vec2 p4 = p;
  p4.y -= 0.28;
  float d4 = circle(p4, 0.285);

  d3 = max(d3,-d4);

  d1 = pmax(d1,-d2, 0.0125);
  d1 = max(d1,-d3);

  float t0 = abs(0.9*p.x);
  t0 *= t0;
  t0 *= t0;
  t0 *= t0;
  t0 = clamp(t0, 0.0, 1.0);
  d0 = abs(d0)-mix(0.0125, -0.0025, t0);


  float d = d0;
  d = pmin(d, d1, 0.0125);
  return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q  = fragCoord/RESOLUTION.xy;
  vec2 p  = -1. + 2. * q;
  p.x     *= RESOLUTION.x/RESOLUTION.y;

  float aa = 2.0/RESOLUTION.y;
  vec3 col = vec3(1.0);

  float a = 0.25;

  vec2 sp = p;
  spiralMod(sp, a);
  sp.x += -TIME*0.2;
  vec2 msp = sp;
  float nsp = mod1(msp.x, a);

  float z = a*0.4;

  float dd = length(msp)-z;

  float h = hash(nsp+123.4);

  float de = eye(msp/z,h)*z;
  de -= 0.25*aa;

  col = mix(vec3(1.0-abs(2.0*sp.y/a)), vec3(0.0), smoothstep(aa, -aa, de));
  col *= smoothstep(0.0, 0.4, length(p));

  col = sRGB(col);
  fragColor = vec4(col, 1.0);
}

/*
I, Michel Rouzic, have been researching this problem for years and at last I have just (in February 2022) solved it.
This directly calculates, in one pass, a visually fully accurate approximation of a Gaussian-blurred polygon,
in this case a polygon with 4 (easy to modify) sides which can be concave or convex.
This works by calculating the Gaussian-weighted area of the polygon around the coordinate of the pixel being rendered.
Each side of the polygon forms a subtriangle with the pixel, and each subtriangle is split into two right triangles.
Their weights are calculated mainly according to the approximation of an integral, and depending on the angle at the
pixel some other operations have to be done. Then all the weights are added together and multiplied by the colour.

The Gaussian blurring gets no smaller than 0.8 pixels wide because that's what I consider the best Gaussian blurring
for a sharp antialiasing. I made this so that I could render perfectly antialiased polygons (even on the CPU)
efficiently without any multisampling. I think this method is a pretty big deal due to how efficient and practically
perfect it is.

Bonuses include a fast approximation of the error function (erf) which you can reuse whenever you need erf() and 
a way to convert linear pixel values to sRGB without using pow() (although I'm not sure this is more efficient).
*/

float sq(float x)
{
    return x * x;
}

float erf_fast(float x)
{
	float y, xa = abs(x);

	// erf(x) ~= 1 - exp(<polynomial>) for x >= 0, max error 1/4249
	// 2 mad, 1 mul, 1 +-, 1 copysign, 1 exp = ~9 FR
	y = ((-0.06388*xa - 0.66186)*xa - 1.123613)*xa;
	y = 1. - exp(y);
	y *= sign(x);

	return y;
}

float erf_right_triangle_acute_integral(float x, float y)
{
	float x2 = x*x, y2 = y*y;
	float v = (((((-1.6663128e-05*y2 + 5.104393e-06)*x2 +
			0.0005496131*y2 - 5.30433e-05)*x2 +
			(0.0001584783*y2 - 0.00741157237)*y2 - 0.0018265954)*x2 +
			(-0.003881739*y2 + 0.0523013844)*y2 + 0.04582956)*x2 +
			((-0.00368418*y2 + 0.03692744)*y2 - 0.1996548)*y2 - 0.50360028)*x2 +
			((-0.0012717*y2 - 0.0101518)*y2 + 0.0109084)*y2 - 1.836892;
	return exp(v) * x2 * y;	// 25 FR
}

float calc_right_triangle_pixel_weight(vec2 rp)
{
	vec2 rpa;
	bool use_obtuse;
	float slope, acute, obtuse;

	rpa = abs(rp);

	// Pick method
	use_obtuse = rpa.y > rpa.x;
	if (use_obtuse)			// if we use the obtuse method
	{
		// Swap axes
		float t = rp.x;
		rp.x = rp.y;
		rp.y = t;
	}

	// Prepare the arguments (slope and clamped x)
	slope = abs(rp.x) < 1e-5 ? 0. : rp.y / rp.x;
	slope = clamp(slope, -1., 1.);
	rp.x = clamp(rp.x, -3., 3.);

	acute = erf_right_triangle_acute_integral(rp.x, slope);
	obtuse = 0.25 * erf_fast(rp.y) * erf_fast(rp.x) - acute;
	acute = abs(acute) * sign(slope);
	obtuse = abs(obtuse) * sign(slope);

	return use_obtuse ? obtuse : acute;
}

float calc_subtriangle_pixel_weight(vec2 p0, vec2 p1)
{
	vec2 rot, r0, r1, np;
	float weight;

	// Rotate points
	rot = normalize(p1 - p0);
	r0.x = rot.x*p0.y - rot.y*p0.x;
	r0.y = rot.x*p0.x + rot.y*p0.y;
	r1.x = r0.x;
	r1.y = rot.x*p1.x + rot.y*p1.y;

	// Calc weights
	weight = calc_right_triangle_pixel_weight(r1);
	weight -= calc_right_triangle_pixel_weight(r0);

	return weight;
}

vec3 draw_tetragon(vec2 p[4], vec3 col, float rad, vec3 pv, vec2 pf)
{
	float weight;

	// Transform polygon coordinates
	p[0] = (p[0] - pf) * rad;
	p[1] = (p[1] - pf) * rad;
	p[2] = (p[2] - pf) * rad;
	p[3] = (p[3] - pf) * rad;

	// Calculate weight for each subtriangle
	weight = calc_subtriangle_pixel_weight(p[0], p[1]);
	weight += calc_subtriangle_pixel_weight(p[1], p[2]);
	weight += calc_subtriangle_pixel_weight(p[2], p[3]);
	weight += calc_subtriangle_pixel_weight(p[3], p[0]);

	// Apply weight to colour
	pv += weight * col;

	return pv;
}

float lsrgb(float l)	// converts a [0.0, 1.0] linear value into a [0.0, 1.0] sRGB value
{
	float x, line, curve;

	// 13 FR every time + 2 FR once
	line = l * 12.92;	// 1 FR
	x = sqrt(l);	// 4 FR
	curve = ((((0.455*x - 1.48)*x + 1.92137)*x - 1.373254)*x + 1.51733216)*x - 0.0404733783;	// 5 FR + 2 FR once, error 0.145 sRGB units

	return l <= 0.0031308 ? line : curve;	// 3 FR
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 rp, p[4] = vec2[] ( vec2(0., 3.), vec2(1.8, -2.), vec2(0., 0.), vec2(-1.8, -2.) );

    // Pixel to world coordinates
    float scrscale = 18. / iResolution.y;
    vec2 uv = (fragCoord-iResolution.xy*0.5) * scrscale;

    // Tetragon rotation
    float th = iTime * 6.2831853 / 40.;
    float costh = cos(th);
    float sinth = sin(-th);

    for (int i=0; i < 4; i++)
    {
        rp.x = p[i].x * costh - p[i].y * sinth;
        rp.y = p[i].x * sinth + p[i].y * costh;
        p[i] = rp;
    }

    // Varying blurriness
    float rad = sq(1.-abs(cos(iTime * 6.2831853 / 16.))) * 6.;
    float min_rad = 0.8*scrscale;
    rad = length(vec2(rad, min_rad));

    // Time varying pixel color
    vec3 pv = vec3(0.);
    pv = draw_tetragon(p, vec3(1.), 1./rad, pv, uv);

    // Output to screen
    fragColor = vec4(lsrgb(pv.r), lsrgb(pv.g), lsrgb(pv.b), 1.);
}

#define PI     3.14159265
// This code all desperately needs to be cleaned, but this is just a quick sketch

float magnitude(in vec2 v){
    return sqrt(pow(v.x, 2.) + pow(v.y, 2.));
}

float ring_offset(in float time){
    return time;
}

float ring_num_float(in vec2 v, in float ringSize, in float time){
    return (magnitude(v) + ring_offset(time)*ringSize)/ringSize;
}

int ring_num(in vec2 v, in float ringSize, in float time){
    return int(floor(ring_num_float(v, ringSize, time)));
}

bool should_print_circle_line(in vec2 v, in float ringSize, in float time, in float lineSize){
    float ringNum = mod(ring_num_float(v, ringSize, time), 1.0);
    return ringNum < (lineSize/2.0) || ringNum > (1.0 - lineSize/2.0);
}

vec4 background_colour(in vec2 v, in float ringSize, in float lineSize, in float time){
    // Params
    vec4 colours[6] = vec4[6](
        vec4(255.0, 128.0, 0.0, 255.0)/255.0,    // Orange
        vec4(0.0, 0.0, 204.0, 255.0)/255.0,      // Dark Blue
        vec4(0.0, 204.0, 0.0, 255.0)/255.0,      // Green
        vec4(102.0, 0.0, 204.0, 255.0)/255.0,    // Purple
        vec4(255.0, 51.0, 255.0, 255.0)/255.0,   // Pink
        vec4(102.0, 178.0, 255.0, 255.0)/255.0); // Light Blue
    int coloursLength = 6;

    return should_print_circle_line(v, ringSize, time, lineSize) ?
           vec4(0.0, 0.0, 0.0, 1.0) : colours[ring_num(v, ringSize, time) % coloursLength];
}

vec4 colour_fallthrough(in vec4 colourOne, in vec4 colourTwo){
    // Calculates how colourTwo should show through colourOne
    // Assumes colourTwo.w == 1.0
    return vec4(colourOne.w * colourOne.xyz + (1.0-colourOne.w) * colourTwo.xyz, 1.0);
}

float angle_distance(in float a1, in float a2){
    // I desperately need to make this function nicer
    a1 = mod(a1 + PI, 2.0*PI) - PI;
    a2 = mod(a2 + PI, 2.0*PI) - PI;
    return min(min(abs(a2-a1), a2 + 2.0*PI - a1), a1 + 2.0*PI - a2);
}

bool is_stem(in int numPetals, in float stemRefAngle, in float stemRefAngleTip, in float pointAngle, in float ringNumber, in float tolerance){
    bool isStem = false;
    float piDiv = (2.0*PI)/float(numPetals);
    bool tipEnabled = mod(ringNumber, 1.0) <= 0.5;

    for(int i = 0; i < numPetals; i++){
        float refAngle = stemRefAngle + float(i) * piDiv;
        float refAngleTip = stemRefAngleTip + float(i) * piDiv;
        isStem = isStem || angle_distance(refAngle, pointAngle) < tolerance;
        isStem = isStem || (tipEnabled && angle_distance(refAngleTip, pointAngle) < tolerance);
    }
    return isStem;
}

bool is_in_ball(in int numPetals, in vec2 coords, in int ringNumberInt, in float time, in float ringSize, in float stemRefAngleTip, in float angleToMatch){
    bool isInBall = false;

    float piDiv = (2.0*PI)/float(numPetals);

    float ballMidRing = float(ringNumberInt)+0.5 - ring_offset(time);
    float ballRadius = tan(angleToMatch)*ballMidRing*ringSize;

    for(int i = 0; i < numPetals; i++){
        float refAngleTip = stemRefAngleTip + float(i) * piDiv;
        vec2 ballCentre = vec2(cos(refAngleTip)*ballMidRing*ringSize, sin(refAngleTip)*ballMidRing*ringSize);
        isInBall = isInBall || magnitude(coords-ballCentre) <= ballRadius;
    }

    return isInBall;
}
vec4 petals(in vec2 coords, in float ringSize, in float time){
    // Params
    int numPetals = 3;

    // Setting up the petal mask
    float ringNumber = ring_num_float(coords, ringSize, time);
    int ringNumberInt = int(floor(ringNumber));

    vec4 petalMask = vec4(1.0, 1.0, 1.0, 1.0);
    petalMask = petalMask * float(ringNumber >= 1.0);

    // Is this part of a petal stem?
    float pointAngle = atan(coords.y, coords.x);
    float stemRefAngle = PI/3.0 - (float(ringNumberInt)-1.0) * 0.2 + iTime * (0.1 + 0.03 * (float(ringNumberInt) - ring_offset(time)));
    float stemRefAngleTip = PI/3.0 - (float(ringNumberInt)-2.0) * 0.2 + iTime * (0.1 + 0.03 * (float(ringNumberInt-1) - ring_offset(time)));
    float stemRefAngleOverhangTip = PI/3.0 - (float(ringNumberInt)-3.0) * 0.2 + iTime * (0.1 + 0.03 * (float(ringNumberInt-2) - ring_offset(time)));

    bool tipEnabled = mod(ringNumber, 1.0) <= 0.5;
    float twoPiOnThree = (2.0 * PI)/3.0;

    bool isStem = is_stem(numPetals, stemRefAngle, stemRefAngleTip, pointAngle, ringNumber, 0.05);
    petalMask = isStem ? petalMask : vec4(0.0, 0.0, 0.0, 0.0);

    // What about the petal stems line?

    bool isStemLine = is_stem(numPetals, stemRefAngle, stemRefAngleTip, pointAngle, ringNumber, 0.06);
    petalMask = !isStem && isStemLine ? vec4(0.0, 0.0, 0.0, 1.0) : petalMask;

    // Finally, what about the ball_ends of the petals    
    bool isInBall = is_in_ball(numPetals, coords, ringNumberInt, time, ringSize, stemRefAngleTip, 0.05);
    petalMask = isInBall ? vec4(1.0, 1.0, 1.0, 1.0) : petalMask;

    bool isInBallLine = is_in_ball(numPetals, coords, ringNumberInt, time, ringSize, stemRefAngleTip, 0.06);
    petalMask = isInBallLine && !isInBall && !isStem ? vec4(0.0, 0.0, 0.0, 1.0) : petalMask;

    // And what is the ball is too large for 1 stripe, but can fit in two?
    bool isInBall2 = is_in_ball(numPetals, coords, ringNumberInt-1, time, ringSize, stemRefAngleOverhangTip, 0.05);
    petalMask = isInBall2 ? vec4(1.0, 1.0, 1.0, 1.0) : petalMask;

    bool isInBallLine2 = is_in_ball(numPetals, coords, ringNumberInt-1, time, ringSize, stemRefAngleOverhangTip, 0.06);
    petalMask = isInBallLine2 && !isInBall && !isInBall2 && !isStem ? vec4(0.0, 0.0, 0.0, 1.0) : petalMask;

    return petalMask;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Params
    float ringSize = 50.0;
    float lineSize = 0.05;


    vec2 centre = iResolution.xy/2.0;


    // Adjusted coordinates
    vec2 centreAdjustedCoords = fragCoord - centre;
    float time = iTime * 0.1 + 1.0;


    // Output to screen
    fragColor = colour_fallthrough(petals(centreAdjustedCoords, ringSize, time),
    background_colour(centreAdjustedCoords, ringSize, lineSize, time));
}

vec3 rotateVector(vec3 p, float q, float q2) {
    p = vec3(p.x*cos(q) - p.y*sin(q), p.x*sin(q) + p.y*cos(q), p.z);
    p = vec3(p.x*cos(q2)+p.z*sin(q2), p.y, -p.x*sin(q2)+p.z*cos(q2));
    return p;
}

float sdSphere(vec3 p, float s, vec3 pos)
{
    p -= pos;
    return length(p)-s;
}

float map(vec3 p) {
    float d = sdSphere(mod(p, 1.), .15, vec3(0.5, 0.5+sin(floor(p.z/1.)+floor(p.x/1.)+iTime*10.)*.05, 0.5));
    return d;
}

vec3 calcNormal(vec3 p) {
	float eps = 0.01;
	return normalize(vec3(
		map(p + vec3(eps, 0, 0)) - map(p + vec3(-eps, 0, 0)),
		map(p + vec3(0, eps, 0)) - map(p + vec3(0, -eps, 0)),
		map(p + vec3(0, 0, eps)) - map(p + vec3(0, 0, -eps))
		));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec3 point_light_pos = vec3(1., 1., 1.);

    vec3 cam_pos = vec3(iTime*2., iTime, iTime*5.);
    vec3 ray_dir = normalize(vec3(fragCoord.x/iResolution.y-iResolution.x/iResolution.y/2., fragCoord.y/iResolution.y-1./2., 1.));
    ray_dir = rotateVector(ray_dir, sin(iTime)*.5, sin(iTime*.35)*.5);

    float closest_distance = 10000.;
    bool hit = false;
    vec3 hit_pos = cam_pos;
    float t = 0.;
    for (int i = 0; i < 60; i += 1) {
        t = map(hit_pos);
        hit_pos += ray_dir*t;
        if (t < .001) {
            hit = true;
            break;
        }
        closest_distance = min(t, closest_distance);
    }

    float light_power = (dot(calcNormal(hit_pos), normalize(point_light_pos-hit_pos))+1.)/2.;

    float depth = distance(hit_pos, cam_pos);

    float vignette = (1.-abs(fragCoord.x-iResolution.x/2.)/iResolution.x)*(1.-abs(fragCoord.y-iResolution.y/2.)/iResolution.y);

    if (hit) {
        fragColor = smoothstep(1., vignette/40., depth/10.)*vignette*vec4(random(floor(hit_pos/1.)), random(floor(hit_pos/1.)+vec3(1., 1., 1.)), random(floor(hit_pos/1.)+vec3(5., 5., 5.)), 1.);
    } else {
        fragColor = vignette/40.*vec4(1.);
    }

}

