vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float n = snoise(uv * 2.2 + iTime * 0.5);    
    vec3 img1 = texture(iChannel0, uv).rgb;
    vec3 img2 = texture(iChannel1, uv).rgb;
    vec3 mixed = uv.x > 0.5 ? vec3(n) : mix(img1, img2, n);
    fragColor = vec4(mixed,1.0);
}

/*
Oddly enough, I found out about the Jacobi theta functions
when I was trying to find the "exact" value of the infinite
decimal 0.1010010001000010000010000001...

By expanding the decimal into an infinite sum of powers of ten
(specifically, triangular number powers because of the constant
increase in the gaps between ones) and manipulating it a bit, it
can be expressed in terms of the second Jacobi theta function:
10^(1/8) * ϑ2(0, 1/10^(1/2)) / 2 - 1
*/

#define k 24.0

// Modified version of font loader from https://www.shadertoy.com/view/ltcXzs
void drawChar(inout vec3 color, in vec3 charColor, in vec2 uv, in vec2 pos, in vec2 size, in int char) {
    uv = (uv - pos) / size + 0.5;
    vec2 charUv = uv / 16.0;
    vec2 dfdx = dFdx(charUv);
    vec2 dfdy = dFdy(charUv);
    if (all(lessThan(abs(uv - 0.5), vec2(0.5)))) {
        float val = textureGrad(iChannel0, charUv + fract(vec2(char, 15 - char / 16) / 16.0), dfdx, dfdy).r;
        color = mix(color, charColor, val);
    }
}

// Jacobi theta function 1, 2, 3, and 4
vec2 theta1(in vec2 z, in vec2 q) {
    vec2 w = vec2(0.0);
    float s = 1.0;
    for (float n=0.0; n <= k; n++) {
        w += s * cmul(cpow(q, sqr(n + 0.5)), csin((2.0 * n + 1.0) * z));
        s = -s;
    }

    return 2.0 * w;
}

vec2 theta2(in vec2 z, in vec2 q) {
    vec2 w = vec2(0.0);
    for (float n=0.0; n <= k; n++) {
        w += cmul(cpow(q, sqr(n + 0.5)), ccos((2.0 * n + 1.0) * z));
    }

    return 2.0 * w;
}

vec2 theta3(in vec2 z, in vec2 q) {
    vec2 w = vec2(0.0);
    for (float n=1.0; n <= k; n++) {
        w += cmul(cpow(q, n * n), ccos(2.0 * n * z));
    }

    return 2.0 * w + 1.0;
}

vec2 theta4(in vec2 z, in vec2 q) {
    vec2 w = vec2(0.0);
    float s = 1.0;
    for (float n=1.0; n <= k; n++) {
        w += s * cmul(cpow(q, n * n), ccos(2.0 * n * z));
        s = -s;
    }

    return 2.0 * w + 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = 2.0 * fragCoord / iResolution.xy - 1.0;
    vec2 mouse = 2.0 * iMouse.xy / iResolution.xy - 1.0;
    float aspect = iResolution.x / iResolution.y;
    vec3 color = vec3(0.0);

    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(0.62, 0.74);

    vec2 q = mouse;
    vec2 w = vec2(0.0);

    // ϑ1(z, q) in lower left quadrant
    if (uv.x < 0.0 && uv.y < 0.0) {
        vec2 z = 2.0 * uv + 1.0;
        z.x *= aspect;
        w = theta1(z, q);
    }

    // ϑ2(z, q) in lower right quadrant
    if (uv.x > 0.0 && uv.y < 0.0) {
        vec2 z = 2.0 * uv + vec2(-1.0, 1.0);
        z.x *= aspect;
        w = theta2(z, q);
    }

    // ϑ3(z, q) in upper right quadrant
    if (uv.x > 0.0 && uv.y > 0.0) {
        vec2 z = 2.0 * uv - 1.0;
        z.x *= aspect;
        w = theta3(z, q);
    }

    // ϑ4(z, q) in upper left quadrant
    if (uv.x < 0.0 && uv.y > 0.0) {
        vec2 z = 2.0 * uv + vec2(1.0, -1.0);
        z.x *= aspect;
        w = theta4(z, q);
    }

    // Domain coloring
    color = hue2rgb(carg(w) / tau + 0.5);

    // Dividers
    if (abs(fragCoord.x - 0.5 * iResolution.x) < 2.0) color = vec3(0.0);
    if (abs(fragCoord.y - 0.5 * iResolution.y) < 2.0) color = vec3(0.0);

    // Label quadrants
    uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 corner = vec2(0.5 * iResolution.x / iResolution.y, 0.5);

    // θ1
    drawChar(color, vec3(0.0), uv, -corner + 0.1, vec2(0.1), 133);
    drawChar(color, vec3(0.0), uv, -corner + vec2(0.15, 0.1), vec2(0.1), 49);

    // θ2
    drawChar(color, vec3(0.0), uv, vec2(0.0, -corner.y) + 0.1, vec2(0.1), 133);
    drawChar(color, vec3(0.0), uv, vec2(0.0, -corner.y) + vec2(0.15, 0.1), vec2(0.1), 50);

    // θ3
    drawChar(color, vec3(0.0), uv, vec2(0.1), vec2(0.1), 133);
    drawChar(color, vec3(0.0), uv, vec2(0.15, 0.1), vec2(0.1), 51);

    // θ4
    drawChar(color, vec3(0.0), uv, vec2(-corner.x, 0.0) + 0.1, vec2(0.1), 133);
    drawChar(color, vec3(0.0), uv, vec2(-corner.x, 0.0) + vec2(0.15, 0.1), vec2(0.1), 52);

    fragColor = vec4(color, 1.0);
}

