void main()
#else
void mainImage( out vec4 fragColor, in vec2 fragCoord )
#endif
{
	fragColor.a = 1.0;

	vec2 uv = fragCoord.xy / iResolution.xy;
    float bandWidth = 1.0 / 8.0;

	if ( uv.y > 1.0 - bandWidth ) fragColor.xyz = Turbo( uv.x );
	else if ( uv.y > 1.0 - bandWidth * 2.0 ) fragColor.xyz = vec3(greyScale(Turbo( uv.x )));
	else if ( uv.y > 1.0 - bandWidth * 3.0 ) fragColor.xyz = TurboPlus( uv.x );
	else if ( uv.y > 1.0 - bandWidth * 4.0 ) fragColor.xyz = vec3(greyScale(TurboPlus( uv.x )));
	else if ( uv.y > 1.0 - bandWidth * 5.0 ) fragColor.xyz = Magma( uv.x );
	else if ( uv.y > 1.0 - bandWidth * 6.0 ) fragColor.xyz = vec3(greyScale(Magma( uv.x )));
	else if ( uv.y > 1.0 - bandWidth * 7.0 ) fragColor.xyz = Inferno( uv.x );
	else if ( uv.y > 1.0 - bandWidth * 8.0 ) fragColor.xyz = vec3(greyScale(Inferno( uv.x )));

	#ifdef BLACK_BANDS
	float r = 32.0 / iResolution.y;
	fragColor.xyz *= step( r, tri( uv.y / 0.125 ) );
	#endif

	#ifdef PLOT_CURVES
	float y = fract( uv.y / 0.25 );
	float r = 6.0 / iResolution.y;
	fragColor.xyz += smoothbump( vec3( 0.0 ), vec3( r ), vec3( y ) - fragColor.xyz );
	#endif
}

// Steel Spin by NuSan
// PC 4k intro made for Novoque 2020

// Unfortunately, the GPU Synthesizer I made cannot be ported on shadertoy, as it uses a second audio pass to compute reverbs
// So only soundcloud for now ...

// Original Tools: Leviathan, custom GPU synth, Shader Minifier, Crinkler
// https://www.pouet.net/prod.php?which=85684
// https://youtu.be/OjZVSqhReqA

// if sound doesn't start or seems desynchronised:
// try clicking pause/start button in the "soundcloud" square in the bottom right
// then press rewind just under the shader picture on the left

///////////////////////
// POST-PROCESS PASS //
///////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{		
	vec2 frag = fragCoord.xy;
	vec2 uv = frag/iResolution.xy;

	float time = iTime-.5;

    vec3 col = texture(iChannel0, uv).xyz;

    // Bloom computation
    vec3 cumul = vec3(0);
	for(float i=-2.; i<=2.5; ++i) {
		for(float j=-2.; j<=2.5; ++j) {
			vec4 cur = textureLod(iChannel1, uv + (vec2(i,j))*36./vec2(1920.,1080.), iResolution.y>720. ? 6. : 4.);
			cumul += cur.xyz;
		}
	}

    // use more bloom for brighter values
	col += cumul * clamp(dot(cumul.xyz,vec3(.01))-.3,0.,1.)*0.05;

    // 'tone mapping'
    col = smoothstep(0.,1.,col);
    col = pow(col,vec3(.6));

    // fade in / fade out
    col *= sat(time*2.) * sat(131.-time);

	fragColor = vec4(col, 1);
}

