#define SURFACE_DIST .01
#define MAX_DIST 50.0
#define MAX_STEPS 100

//most of this shader is derived from Inigo Quilez's SDFs

float displacement(vec3 p)
{
    float stAnim = step(.7, sin(iTime * .5));
     return sin(1.*p.x)*sin(1.*p.y)*sin(.3*p.z + iTime * 2.0 - iTime * 1.5 * stAnim);
}

float sdPyramid( vec3 p, float s)
{
  float stAnim = step(.7, sin(iTime * .5));
  float angle = (100. - 75. * stAnim)*(sin(iTime * .1 + 3.14) + iTime * .1) + 7.;
  float xx = p.x * cos(angle) - p.z * sin(angle);
  float zz = p.x*sin(angle) + p.z*cos(angle);
  p.xz = vec2(xx, zz);
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k));
}

float sdPlane( vec3 p, vec3 n, float h )
{
  // n must be normalized
  return dot(p,n) + h + displacement(p) * .2;
}
float sdBoxFrame( vec3 p, vec3 b, float e )
{
      float stAnim = step(.7, sin(iTime * .5));
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  float bf =  min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.5 * stAnim))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.5))+min(max(q.x,max(q.y,p.z)),0.0));
  return bf;
}

float opSmoothUnion( float d1, float d2, float k ) 
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}



float opRep( in vec3 p, in vec3 c)
{
    float stAnim = step(.7, sin(iTime * .5));
    vec3 q = mod(p+0.5*c,c)-0.5*c;
    return min(sdBoxFrame(q, vec3(1.5), .2), sdPyramid(q + vec3(0,.3,0), .4));
}




float GetDist( vec3 p)
{
  float stAnim = step(.7, sin(iTime * .5));
  float ground = sdPlane(p, vec3(0.0,1.0,0.0), -4.2+ stAnim * 4545.45);
  float bf = opRep(p, vec3(4.75 + stAnim * 5.));
  float gr = opSmoothUnion(bf, ground, .5 );
  float d = gr;
  return d;
}


float raymarch(vec3 ro, vec3 rd){
    float d0 = 0.0;
    for(int i = 0; i < MAX_STEPS; i++){
        vec3 p = ro+d0*rd;
        float dS = GetDist(p);
        d0 += dS;
        if(dS < SURFACE_DIST || d0 > MAX_DIST) break;
    }
    return d0;

}

vec3 GetNormal(vec3 p){
