void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float stAnim = step(.7, sin(iTime * .5));
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 bg = vec3(mix(.9, 0., uv.y), mix(0.2, 0.0, uv.y), 0.0);
    if(stAnim > .5) bg = vec3(1.,.99,.95);
    vec3 ro = vec3(0, 5., -5) - vec3(0, 0,iTime - iTime * .75 * stAnim) * ((-stAnim * 1.6) + 1.);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));
    float d = raymarch(ro, rd);    
    vec3 p = ro + rd * d;
    float anim = clamp(abs(sin(sin(iTime))) / (10. * sin(iTime * 3.) * sin(iTime* 3.)), .5, 4.);
    float dif = GetLight(p, vec3(10. * sin(iTime * .1) - 5., 5., 5.));
    float dif2 = GetLight(p, vec3(-15.,1. + stAnim * 10.,-5.));
    dif = mix(dif * anim, dif2, stAnim);
    float m = clamp(d / MAX_DIST ,0.,1.);
    vec3 mat = vec3(100., 0, 0.) / 255.;
    if(stAnim > .5) mat = vec3(1.5,1.5,1.5);
    vec3 s = vec3(dif) * mat;
    vec3 col = mix(s, bg,  m);

    fragColor = vec4(col,1.0);
}

const float PI = 2.0*acos(0.0);
const float SQRT3 = sqrt(3.0);
const mat2 ROT1 = mat2(cos(PI/1.5),sin(PI/1.5),-sin(PI/1.5),cos(PI/1.5));
const mat2 ROT2 = mat2(cos(PI/6.0),sin(PI/6.0),-sin(PI/6.0),cos(PI/6.0));

float fractal(vec2 x, float param);

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord-iResolution.xy/2.0)/length(iResolution);
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float a = mod((iTime/3.0),1.0);
    mat2 rot = pow(SQRT3,-a)*mat2(cos(a*PI/6.0),-sin(a*PI/6.0),sin(a*PI/6.0),cos(a*PI/6.0));

    float temp = fractal((0.7+0.2*sin(iTime/2.0))*rot*uv+vec2(0,0.5/SQRT3),(a-0.25*a*a)/0.75);
    temp = mod((iTime/3.0),2.0)<1.0?temp:1.0-temp;

    fragColor = vec4(temp<0.0?col:vec3(0.15+0.2*temp),1.0);
}

float fractal(vec2 x, float param){
    if (x.y<-0.0||x.y/(0.25*SQRT3)+abs(x.x)*4.0/3.0>2.0){
        return -1.0;
    }
    float t = 1.0;
    for (int i=0;i<6;i++){
        if (x.y/(0.25*SQRT3)+abs(x.x)*4.0<2.0){
            if (x.y>1.0/SQRT3){
                x = x*3.0-vec2(0.0,SQRT3);
            }else if(x.x+x.y/SQRT3<-1.0/6.0){
                x = x*3.0+vec2(1.0,0.0);
            }else if(-x.x+x.y/SQRT3<-1.0/6.0){
                x = ROT1*ROT1*(x*3.0-vec2(1.0,0.0))+vec2(-0.25,0.25*SQRT3);
                t = 1.0-t;
            }else if(x.x<1.0/6.0&&x.x+min(x.y,1.0/SQRT3-x.y)*SQRT3>1.0/6.0){
                x = mat2(1.5,0.5*SQRT3,-0.5*SQRT3,1.5)*(x-vec2(0.0,0.5/SQRT3))+vec2(0.0,0.5/SQRT3);
                t = 1.0-t;
            }else{
                if (x.x<1.0/6.0){
                    if (x.y>0.5/SQRT3){
                        x = ROT1*ROT1*(x-vec2(0.0,0.5/SQRT3))+vec2(0.0,0.5/SQRT3);
                    }else{
                        x = ROT1*(x-vec2(0.0,0.5/SQRT3))+vec2(0.0,0.5/SQRT3);
                        t = 1.0-t;
                    }
                }
                x = mat2(0.0,SQRT3,-SQRT3,0.0)*(x-vec2(0.0,0.5/SQRT3))+vec2(0.0,0.5/SQRT3);
                t = 1.0-t;
            }
        }else{
            if (x.x<0.0){
                x = ROT1*ROT2*(x+vec2(0.5,0.0))*SQRT3+vec2(0.0,0.5*SQRT3);
            }else{
                x = ROT2*ROT2*ROT1*ROT2*(x-vec2(0.5,0.0))*SQRT3+vec2(0.0,0.5*SQRT3);
            }
            if (x.y/(0.25*SQRT3)+abs(x.x)*4.0>2.0){
                if (x.x<0.0){
                    x = ROT1*ROT2*(x+vec2(0.5,0.0))*SQRT3+vec2(0.0,0.5*SQRT3);
                }else{
                    x = ROT2*ROT2*ROT1*ROT2*(x-vec2(0.5,0.0))*SQRT3+vec2(0.0,0.5*SQRT3);
                }
            }
        }
    }
    x = (param*mat2(1.0,0.0,0.0,1.0)+(1.0-param)*mat2(0.5,-0.5*SQRT3,0.5/SQRT3,1.5))*(x-vec2(0.5,0.0))+vec2(0.5,0.0);
    return ((x.y>1.0/SQRT3||x.x+x.y*SQRT3<1.0/6.0||x.y/(0.25*SQRT3)+abs(x.x)*4.0>2.0)?t:1.0-t);
}

// The displays expects colors in sRGB space, so we'll be converting colors
// from linear space to sRGB. But also, the display has a limted number of
// bits (usually 8, but we'll do 3 for today's test - change line 12 to emulate
// other bit counts). So we'll want to dither the results to prevent banding
// before sending the colors ot the display. So now the question arises - should
// we convert to sRGB before or after dithering. The answer is _before_, since
// dithering aids quantization, and that happens in sRGB, or in the display if
// you want. Comment the #define in line 13 to see the consequences of doing the
// sRGB conversion _after_ dithering - darkest colors are wrong, and the noise
// no longer feels uniform.

#define BITS 4
#define SHOW_CORRECT
//#define PERCEPTUAL_UNIFORM

//------------------------------------------------------------------
// rand()
//------------------------------------------------------------------
int   seed = 1;
int   rand(void) { seed = seed*0x343fd+0x269ec3; return (seed>>16)&32767; }
float frand(void) { return float(rand())/32767.0; }
void  srand( ivec2 p, int frame )
{
    int n = frame;
    n = (n<<13)^n; n=n*(n*n*15731+789221)+1376312589; // hash by Hugo Elias
    n += p.y;
    n = (n<<13)^n; n=n*(n*n*15731+789221)+1376312589;
    n += p.x;
    n = (n<<13)^n; n=n*(n*n*15731+789221)+1376312589;
    seed = n;
}

//------------------------------------------------------------------
// color display functions
//------------------------------------------------------------------

float lrgb2srgb( float color )
{
	return (color<0.0031308) ? color*12.92:1.055*pow(color,(1.0/2.4))-0.055;
}

float dither( float color )
{
    float n = frand()+frand()-1.0;  // triangular noise
    return color + n*exp2(-float(BITS));
}

float quantize( float color )
{
    const float levels = exp2(float(BITS));
    return floor(color*levels)/(levels-1.0);
}        

//------------------------------------------------------------------
// main
//------------------------------------------------------------------

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Init randoms
    srand( ivec2(fragCoord), iFrame );

    // Coordinates
    float x = 1.0*fragCoord.x/iResolution.x;
    float y = 3.0*fragCoord.y/iResolution.y;

    // Create an energy linear color ramp. Note that darks will feel
    // compressed to your eye. Enable #define PERCEPTUAL_UNIFORM in 
    // line 14 to make it more perceptually uniform, if you want.
    float col = x;
    #ifdef PERCEPTUAL_UNIFORM
    col = pow( col, 2.2 );
    #endif


    // CORRECT: convert to sRGB BEFORE dithering
    #ifdef SHOW_CORRECT
        col = lrgb2srgb( col ); 

        if( y>1.0 && y<2.0 )
        col = dither( col );

    // INCORRECT: converting to sRGB AFTER dithering
    #else
        if( y>1.0 && y<2.0 )
        col = dither(col);

        col = lrgb2srgb( col ); 
    #endif

    // simulate 2^BITS color level display screen
    if( y>1.0 )
    col = quantize(col); 

    // separation lines
    //col *= smoothstep( 0.0, 0.01, 1.0-2.0*abs(fract(y)-0.5) );

    fragColor = vec4( col, col, col, 1.0 );
}

#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float GetDist(vec3 p) {
    float r1 = 1.3;
    float r2 = 0.3;
    float d1 = length(p.xz) - r1;

    float a = atan(p.x, p.z);

    vec2 u = vec2(d1, p.y);    
    u.xy *= Rot(1.5 * a - 0.5 * atan(u.x, u.y) + iTime);
    u.x = sabs(u.x) - 0.5;
    u = sabs(u) - 0.2;

    float d2 = length(u) - r2;

    return 0.5 * d2;
}

float RayMarch(vec3 ro, vec3 rd, float z) {	
    float dO=0.; 

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 0, -5);
    //ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.5);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1. + exp(-0.6 * d); // very specific trick
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);

        vec3 pIn = p - 30. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.); //rdIn

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float fresnel = pow(1.+dot(rdIn, nExit), 2.);
        col = vec3(fresnel);
        fresnel = pow(1.+dot(rd, n), 5.);
        col += vec3(fresnel);

        float dif = dot(p, normalize(vec3(1,2,3)))*.5+.5;
        col *= mix(dif, 1., 0.8 + 0.22 * thc(40., d * 20.));

        float v = 1.-exp(-1. * pow(dIn, 4.));
        //col = vec3(v);
        col = smin(col, vec3(v));
        vec3 e = vec3(dIn);
        col *= pal(0.02 * dIn, e, e, e, vec3(0,1,2)/3.);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

