// ----------------------------------------
// Options
// ----------------------------------------

// How many bits per channel (BpC) should be emulated?
// Range: [1 .. Inf] Default: 4
#define BITS_PER_CHANNEL 4

// How much space should be reserved for fully dark and bright pixels?
// Range: [0 .. .5] Default: (1. / (exp2(float(BITS_PER_CHANNEL)) + 2.)) // About 1 step on each side.
#define RESERVED_AREA (1. / (exp2(float(BITS_PER_CHANNEL)) + 2.))

// ----------------------------------------
// References
// ----------------------------------------
// https://blog.demofox.org/2022/01/01/interleaved-gradient-noise-a-different-kind-of-low-discrepancy-sequence/
// https://loopit.dk/banding_in_games.pdf
// https://cdn.cloudflare.steamstatic.com/apps/valve/2008/GDC2008_PostProcessingInTheOrangeBox.pdf

// ----------------------------------------
// Code
// ----------------------------------------

float range = (exp2(float(BITS_PER_CHANNEL)) - 1.);
float4 generateBars(float2 uv) {
    float4 result;
    float gradient = lerp(0., 1., (uv.x - RESERVED_AREA) / (1. - (RESERVED_AREA * 2.)));
    // Incorrect: smoothstep(RESERVED_AREA, 1. - RESERVED_AREA, uv.x);

    float gy2 = uv.y * 1.;
    float gy = fract(gy2);

    result = float4(gradient, gradient, gradient, 1.);
    if (gy >= .75) {
        result.rg = float2(0., 0.);
    } else if (gy >= .50) {
        result.rb = float2(0., 0.);
    } else if (gy >= .25) {
        result.gb = float2(0., 0.);
    }

    return result;
}

#define DITHER_BANDS 7

float band0(uint2 fragCoord) {
    // No dithering.
    return 0.5;
}

float band1(uint2 fragCoord) {
    // No dithering, bias to zero.
    return 1.;
}

float band2(uint2 fragCoord) {
    // Bayer
    // Levels should be above 3u for ideal quality. 5u seems to be a good spot.
    return Bayer(fragCoord, 5u, true, uint(iFrame));
}

float band3(uint2 fragCoord) {
    // Interleaved Gradient Noise
    return InterleavedGradientNoise(fragCoord, true, uint(iFrame));
}

float band4(uint2 fragCoord) {
    // Shaped Thresholding (iChannel2)
    return textureLod(iChannel2, float2(fragCoord * 1u) / iChannelResolution[2].xy, 0.).r;
}

float band5(uint2 fragCoord) {
    // Shaped Thresholding (iChannel2)
    return textureLod(iChannel2, float2(fragCoord * 3u) / iChannelResolution[2].xy, 0.).r;
}

float band6(uint2 fragCoord) {
    // Shaped Thresholding (iChannel3)
    return textureLod(iChannel3, float2(fragCoord * 1u) / iChannelResolution[3].xy, 0.).r;
}

float band7(uint2 fragCoord) {
    // Shaped Thresholding (iChannel3)
    return textureLod(iChannel3, float2(fragCoord * 3u) / iChannelResolution[3].xy, 0.).r;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // As far as I can tell, ShaderToy writes directly to the canvas, which is sRGB.
    // Correct me if I'm wrong, but please provide sources/references.

    vec2 uv = fragCoord / iResolution.xy;
    uv.y = 1. - uv.y; // Flip V.

    // Generate a gradient from left to right.
    float2 guv = float2(uv.x, uv.y * (float(DITHER_BANDS) + 1.));
    fragColor = generateBars(float2(guv.x, fract(guv.y)));

    // Call the threshold function.
    float threshold = 0.;
    #if DITHER_BANDS >= 7
    if (guv.y >= 7.) {
        threshold = band7(uint2(fragCoord));
    } else
    #endif
    #if DITHER_BANDS >= 6
    if (guv.y >= 6.) {
        threshold = band6(uint2(fragCoord));
    } else
    #endif
    #if DITHER_BANDS >= 5
    if (guv.y >= 5.) {
        threshold = band5(uint2(fragCoord));
    } else
    #endif
    #if DITHER_BANDS >= 4
    if (guv.y >= 4.) {
        threshold = band4(uint2(fragCoord));
    } else
    #endif
    #if DITHER_BANDS >= 3
    if (guv.y >= 3.) {
        threshold = band3(uint2(fragCoord));
    } else
    #endif
    #if DITHER_BANDS >= 2
    if (guv.y >= 2.) {
        threshold = band2(uint2(fragCoord));
    } else
    #endif
    #if DITHER_BANDS >= 1
    if (guv.y >= 1.) {
        threshold = band1(uint2(fragCoord));
    } else
    #endif
    if (guv.y >= 0.) {
        threshold = band0(uint2(fragCoord));
    }

    // Perform Dithering (should happen in sRGB space).
    fragColor = DitherQuantize(fragColor, range, threshold);

    // Image writes to an lRGB buffer, which is presented as sRGB. Must fix this by inverting the generated color.
    fragColor = sRGBA_to_lRGBA(fragColor);

    // Debug threshold
    //if (fragCoord.y <= iMouse.y) {
    //    fragColor.rgb = float3(threshold, threshold, threshold);
    //}
}

