// ----------------------------------------
// HLSL to GLSL translations
// ----------------------------------------
#define float2 vec2
#define float3 vec3
#define float4 vec4
#define int2 ivec2
#define int3 ivec3
#define int4 ivec4
#define uint2 uvec2
#define uint3 uvec3
#define uint4 uvec4
#define bool2 bvec2
#define bool3 bvec3
#define bool4 bvec4
#define fmod mod
#define imod(a, b) (a % b)
#define lerp mix
#define frac fract

// ----------------------------------------
// Threshold Functions
// ----------------------------------------
float Bayer(uint2 xy, uint levels, bool temporal, uint iFrame) {
    if (temporal) {
        xy.x += iFrame % levels;
        xy.y += (iFrame / levels) % levels;
    }

    float val = 0.;
    float div = 0.;
    float mul = 1.;

    //[loop]
    for(uint level = levels; level >= 1u; level--)
    {
        mul *= 4.;

        float2 bayer = fmod(floor(float2(xy.xy) * exp2(1. - float(level))), 2.);
        float x2 = bayer.x * 2.;

        val += lerp(x2, 3. - x2, bayer.y) / 3. * mul;
        div += mul;
    }

    return val / div;
}

// https://blog.demofox.org/2022/01/01/interleaved-gradient-noise-a-different-kind-of-low-discrepancy-sequence/
float InterleavedGradientNoise(uint2 xy, bool temporal, uint iFrame) {
    if (temporal) {
        xy.x += uint(float(imod(iFrame, 64u)) * 5.588238f);
        xy.y += uint(float(imod(iFrame, 64u)) * 5.588238f);
    }

    return fmod(52.9829189f * fmod(0.06711056f * float(xy.x) + 0.00583715f * float(xy.y), 1.f), 1.f);
}

// ----------------------------------------
// Dither Functions
// ----------------------------------------
float4 Dither(float4 color, float threshold) {
    // color: [0..Infinity) Decimal value in the expected output range.
    // threshold: The output of a thresholding function, like Bayer or IGN.

    /* Naive variant.
    float4 fcolor = frac(color);
    color = floor(color);
    color.r += fcolor.r > threshold ? 1. : 0.;
    color.g += fcolor.g > threshold ? 1. : 0.;
    color.b += fcolor.b > threshold ? 1. : 0.;
    color.a += fcolor.a > threshold ? 1. : 0.;
    return color; //*/

    /* Incorrect Variant (>= instead of >)
    return floor(color) + step(threshold, frac(color)); //*/

    //* Faked Variant (> emulated with a tiny offset)
    return floor(color) + step(threshold + 0.0001, frac(color)); //*/
}
float3 Dither(float3 color, float threshold) {
    // color: [0..Infinity) Decimal value in the expected output range.
    // threshold: The output of a thresholding function, like Bayer or IGN.

    /* Naive variant.
    float3 fcolor = frac(color);
    color = floor(color);
    color.r += fcolor.r > threshold ? 1. : 0.;
    color.g += fcolor.g > threshold ? 1. : 0.;
    color.b += fcolor.b > threshold ? 1. : 0.;
    return color; //*/

    /* Incorrect Variant (>= instead of >)
    return floor(color) + step(threshold, frac(color)); //*/

    //* Faked Variant (> emulated with a tiny offset)
    return floor(color) + step(threshold + 0.0001, frac(color)); //*/
}
float2 Dither(float2 color, float threshold) {
    // color: [0..Infinity) Decimal value in the expected output range.
    // threshold: The output of a thresholding function, like Bayer or IGN.

    /* Naive variant.
    float2 fcolor = frac(color);
    color = floor(color);
    color.r += fcolor.r > threshold ? 1. : 0.;
    color.g += fcolor.g > threshold ? 1. : 0.;
    return color; //*/

    /* Incorrect Variant (>= instead of >)
    return floor(color) + step(threshold, frac(color)); //*/

    //* Faked Variant (> emulated with a tiny offset)
    return floor(color) + step(threshold + 0.0001, frac(color)); //*/
}
float Dither(float color, float threshold) {
    // color: [0..Infinity) Decimal value in the expected output range.
    // threshold: The output of a thresholding function, like Bayer or IGN.

    /* Naive variant.
    float4 fcolor = frac(color);
    color = floor(color);
    color += fcolor > threshold ? 1. : 0.;
    return color; //*/

    /* Incorrect Variant (>= instead of >)
    return floor(color) + step(threshold, frac(color)); //*/

    //* Faked Variant (> emulated with a tiny offset)
    return floor(color) + step(threshold + 0.0001, frac(color)); //*/
}
float4 DitherQuantize(float4 color, float range, float threshold) {
    return Dither(color * range, threshold) / range;
}
float3 DitherQuantize(float3 color, float range, float threshold) {
    return Dither(color * range, threshold) / range;
}
float2 DitherQuantize(float2 color, float range, float threshold) {
    return Dither(color * range, threshold) / range;
}
float DitherQuantize(float color, float range, float threshold) {
    return Dither(color * range, threshold) / range;
}

// ----------------------------------------
// Quantize Functions
// ----------------------------------------
float Quantize(float v, float range) {
    return round(v * range) / range;
}
vec2 Quantize(vec2 v, float range) {
    return round(v * range) / range;
}
vec3 Quantize(vec3 v, float range) {
    return round(v * range) / range;
}
vec4 Quantize(vec4 v, float range) {
