}

// ----------------------------------------
// Unaccelerated lRGB<->sRGB conversion
// ----------------------------------------
// May be potentially wrong as I relied on the StackOverflow source for this:
// - https://stackoverflow.com/questions/61430174/sRGB-linear-rgb-conversion-precision

float _sRGB_to_lRGB(float ch) {
	if (ch <= 0.04045) {
		return (ch / 12.92);
	}
	return pow((ch + 0.055) / 1.055, 2.4);
	//return (ch <= 0.04045) ? (ch / 12.92) : pow((ch + 0.055) / 1.055, 2.4);
}

float _lRGB_to_sRGB(float ch) {
	// Inverse of above.
    if (ch <= 0.0031308) {
        return ch * 12.92;
    }
    return (1.055 * pow(ch, (1.0 / 2.4)) - 0.055);
	//return (ch >= 0.0031308) ? (ch * 12.92) : (1.055 * pow(ch, 1.0 / 2.4) - 0.055);
}

float3 sRGB_to_lRGB(float3 sRGB) {
	return float3(
		_sRGB_to_lRGB(sRGB.r),
		_sRGB_to_lRGB(sRGB.g),
		_sRGB_to_lRGB(sRGB.b)
	);
}

float4 sRGBA_to_lRGBA(float4 sRGB) {
	return float4(
		_sRGB_to_lRGB(sRGB.r),
		_sRGB_to_lRGB(sRGB.g),
		_sRGB_to_lRGB(sRGB.b),
		_sRGB_to_lRGB(sRGB.a)
	);
}

float3 lRGB_to_sRGB(float3 lRGB) {
	return float3(
		_lRGB_to_sRGB(lRGB.r),
		_lRGB_to_sRGB(lRGB.g),
		_lRGB_to_sRGB(lRGB.b)
	);
}

float4 lRGBA_to_sRGBA(float4 lRGB) {
	return float4(
		_lRGB_to_sRGB(lRGB.r),
		_lRGB_to_sRGB(lRGB.g),
		_lRGB_to_sRGB(lRGB.b),
		_lRGB_to_sRGB(lRGB.a)
	);
}

