mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }
mat3 rotz(float a) { mat3 rot; rot[0] = vec3(cos(a), -sin(a), 0.0); rot[1] = vec3(sin(a), cos(a), 0.0); rot[2] = vec3(0.0, 0.0, 1.0); return rot; }


float smin( float a, float b, float k )
{
    float h = a-b;
    return 0.5*( (a+b) - sqrt(h*h+k) );
}

float sdSphere(vec3 p, float r){
    return length(p) - r;
}


float sdCutHollowSphere( vec3 p, float r, float h, float t )
{
  // sampling independent computations (only depend on shape)
  float w = sqrt(r*r-h*h);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  return ((h*q.x<w*q.y) ? length(q-vec2(w,h)) : 
                          abs(length(q)-r) ) - t;
}

float sdTorus( vec3 p, float t, float r)
{
  vec2 q = vec2(length(p.xz)- t,p.y);
  return length(q)- r;
}

float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}


vec3 bendPoint(vec3 p, float k)
{
    float c = cos(k*p.y);
    float s = sin(k*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xy,p.z);
    return q;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float displacement(vec3 p){
    return 
    texture(iChannel1,p*0.2).x*0.1+
    texture(iChannel1,p*0.4).x*0.05+
    texture(iChannel1,p*0.8).x*0.025+
    texture(iChannel1,p*1.6).x*0.0125;
}

float sdLand(vec3 p){
    p-=vec3(3,-0.1,1.5);
    return smin(
                smin(
                    sdBox((p-vec3(2,-1.8,7))*roty(-1.), vec3(2,1,2)),
                    sdBox((p-vec3(2.5,-3.8,4)), vec3(3,1.9,3)),
                    2.
                    ),
                smin(
                    sdBox((p-vec3(-2,-1.8,1))*roty(-1.), vec3(2,1,2)),
                    sdBox((p-vec3(4,-1.50,4)), vec3(1)),
                    2.
                    ),
                    2.
                )
            + displacement(p*vec3(.2, .1, .2))*6.;
}

float sdShroomLights(vec3 q){
   return min(
              min(
                  sdSphere(q-vec3(1.,1.5,5.4), 0.35), 
                  sdSphere(q-vec3(2.,1.5,5.6), 0.4)
                  ),
              min(
                  sdSphere(q-vec3(1.2,1.6,6.5), 0.45), 
                   sdSphere(q-vec3(2.7,1.4,6.6), 0.34)
                  )
              );
}


#define SHROOMBEND bendPoint(p, sin(iTime*0.6)*0.05+0.1)

float sdShroomHead(vec3 q){
    return sdCutHollowSphere((q-vec3(1.5,0.95,6.5))*rotz(3.3)*rotx(.25), 1.8, -0.24*smoothstep(-1.,01.,sin(10.*q.x-q.z))-0.1, 0.12 )+displacement(q);
}

float sdShroomStem(vec3 q){
    return sdVerticalCapsule(q-vec3(2.,-2.,6.5), 4., .32);
}

float sdShroom(vec3 p){
    vec3 q = SHROOMBEND;
        return smin(
                       min(
                            sdShroomHead(q),
                            sdShroomStem(q)
                           ),
                       sdShroomLights(q),
                       0.06
                   );
}

float sdScene(vec3 p){
    return smin(
                sdShroom(p),
                sdLand(p),
                .2
                );
}


vec3 calcShroomLightDir( in vec3 p) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    vec3 q = SHROOMBEND;
    return normalize( k.xyy*sdShroomLights(q + k.xyy*h) + 
                      k.yyx*sdShroomLights(q + k.yyx*h) + 
                      k.yxy*sdShroomLights(q + k.yxy*h) + 
                      k.xxx*sdShroomLights(q + k.xxx*h) );
}

vec3 calcNormal( in vec3 p) // for function f(p)
{
    const float h = 0.01; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdScene(p + k.xyy*h) + 
                      k.yyx*sdScene(p + k.yyx*h) + 
                      k.yxy*sdScene(p + k.yxy*h) + 
                      k.xxx*sdScene(p + k.xxx*h) );
}

float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = sdScene(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        t += h;
    }
    return 1.0;
}

