float blob(vec2 uv, float o) {
    float d = 10.;
    for (float i = 0.; i < 12.; i++) {
        float e = 0.01 * i;
        float hx = h21(vec2(e + o, 1.-e));
        float hy = h21(vec2(e + 1., e - o));
        vec2 p = 0.45 * (0.5 - vec2(hx, hy));
        d = smin(d, (1.5 - 0.5 * hx) * length(uv - p));
    }
    return d;
}

