#define TAU 6.2831853072

float amp(in float freq, in float time) {
    return (sin(time) * exp(-freq) + 0.01 * sin(5.0 * (freq + time)) + 0.1) * 14.3;
}

// Stop puzzling, the name is meaningless
float bar(in float time, in float foo) {
    float sound = 0.0;
    for (float freq=1.0; freq < 6.0; freq++) {
        sound += amp(freq, time * 10.0) * sin(TAU * fract(foo * freq * time));
    }

    return sound;
}

#define safepow(x, n) sign(x) * pow(abs(x), n)
float makeNoise(in float time) {
    float sound = 0.0;
    sound += bar(time, 50.0);
    sound += bar(time, 100.0);
    sound += bar(time, 200.0);
    sound += cos(0.5 * time) * bar(time, 300.0);
    sound += safepow(sin(0.25 * time), 3.0) * bar(time, 400.0 + 100.0 * step(6.0, mod(time, 10.0)));
    return sound / 200.0;
}

