// http://www.mimirgames.com/articles/programming/digits-of-pi-needed-for-floating-point-numbers/
const float pi     = 3.141592653589793; // Pi
const float two_pi = 6.283185307179586; // 2 * Pi
const float inv_pi = 0.318309886183790; // 1 / Pi

vec3 color_palette(float t)
{
    return 0.5 * (vec3(
    cos( two_pi * (t - 0.0000000000000000) ),
    cos( two_pi * (t - 0.3333333333333333) ),
    cos( two_pi * (t - 0.6666666666666666) )
    ) + 1.0);
}

vec3 visualizer(vec2 fragCoord)
{
    vec3 col = vec3(0.0);

    float wave_amplitude = 0.5 * (texture(iChannel0, vec2(fragCoord.x / iResolution.x, 1.)).x - 0.5);

    vec2 uv = 2.0 * (fragCoord - 0.5 * iResolution.xy) / max(iResolution.x, iResolution.y);

    float r = length(uv);
    float theta = atan(uv.y, uv.x) ;

    float freq_amplitude0 = texture( iChannel0, vec2(fract(2.0 * ( (2.0 * theta + 0.25 * two_pi * iTime) / two_pi ) ), 0.0) ).x;
    float freq_amplitude1 = texture( iChannel0, vec2(fract(2.0 * ( (2.0 * theta - 0.25 * two_pi * iTime) / two_pi ) ), 0.0) ).x;

    //freq_amplitude0 = pow(freq_amplitude0, 2.0);
    //freq_amplitude1 = pow(freq_amplitude1, 2.0);
    freq_amplitude0 *= freq_amplitude0;// * freq_amplitude0;
    freq_amplitude1 *= freq_amplitude1;// * freq_amplitude1;

    if(wave_amplitude + 0.4 < uv.y)
    {
        col = color_palette( fract( (fragCoord.x / iResolution.x) + iTime ) );
    }
    if(wave_amplitude - 0.4 > uv.y)
    {
        col = color_palette( fract( (fragCoord.x / iResolution.x) - iTime ) );
    }

    if(r > 0.25 && r < 0.3 * freq_amplitude0 + 0.3)
    {
        col = color_palette( fract( 2.0 * ( (theta - 0.25 * two_pi * iTime) / two_pi) ) );
    }
    if(r < 0.20 && r > 0.1 * freq_amplitude1 + 0.15)
    {
        col = color_palette( fract( 2.0 * ( (theta + 0.25 * two_pi * iTime) / two_pi) ) );
    }

    return col;
}

