float smin(float a, float b)
{
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

image_code:
const int FSAA = 4;
const float GRID_DIM = 1.0;
const float R = 0.8;

float jTime;


float sq(float x) {
    return x*x;
}

// From https://www.shadertoy.com/view/4sc3z2
// and https://www.shadertoy.com/view/XsX3zB
#define MOD3 vec3(.1031,.11369,.13787)
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}
float simplexNoise(vec3 p)
{
    const float K1 = 0.333333333;
    const float K2 = 0.166666667;

    vec3 i = floor(p + (p.x + p.y + p.z) * K1);
    vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);

    vec3 e = step(vec3(0.0), d0 - d0.yzx);
	vec3 i1 = e * (1.0 - e.zxy);
	vec3 i2 = 1.0 - e.zxy * (1.0 - e);

    vec3 d1 = d0 - (i1 - 1.0 * K2);
    vec3 d2 = d0 - (i2 - 2.0 * K2);
    vec3 d3 = d0 - (1.0 - 3.0 * K2);

    vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
    vec4 n = h * h * h * h * vec4(dot(d0, hash33(i)), dot(d1, hash33(i + i1)), dot(d2, hash33(i + i2)), dot(d3, hash33(i + 1.0)));

    return dot(vec4(31.316), n);
}

float length_sq(vec2 x) {
    return dot(x, x);
}

float segment_df(vec2 uv, vec2 p0, vec2 p1) {
  float l2 = length_sq(p1 - p0);
  float t = clamp(dot(uv - p0, p1 - p0) / l2, 0.0, 1.0);
  vec2 projection = p0 + t * (p1 - p0);
  return distance(uv, projection);
}

float semi_circle_sdf(vec2 uv, vec2 c, float r) {
    return max((length(uv - c) - r), -(uv.y-c.y));
}

float frame_sdf(vec2 uv, vec2 pf) {
    // Factor by which to multiply the distance at max depth for cheap 3d look.
    const float fm = 5.0;

    const vec2 A = vec2(-R, 0.0);
    const vec2 B = vec2(R, 0.0);

    // Two segments towards pf
    float sdf = min(segment_df(uv, A, pf), segment_df(uv, B, pf)) * mix(1., fm, smoothstep(0., pf.y, uv.y));

    // Semi-circles
    const int n_steps = 15;
    for(int i = 0; i < n_steps; i++) {
        float t = 1. - exp(-0.3*float(i));
        float fmi = mix(1., fm, t);
        // sdf = min(sdf, segment_df(uv, mix(A, pf, t), mix(B, pf, t)) * fmi);
        sdf = min(sdf, abs(semi_circle_sdf(uv, mix(vec2(0,0), pf, t), R*(1.-t))) * fmi);
    }

    return sdf;
}

vec4 sampleColor(in vec2 sampleCoord )
{
    float aspectRatio = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from -1 to 1)
    //vec2 uv = 2.0 * sampleCoord / iResolution.xy - 1.0;

    // Pixel coordinates fixed with correct aspect ratio
    vec2 uvf = 2.0* (sampleCoord - iResolution.xy / 2.0) / iResolution.y;

    vec2 pf = vec2(0.55 * cos(0.7*iTime), 0.4);
    vec2 pfl = pf + vec2(0, -0.1);

    vec3 col;

    float my_noise = simplexNoise(vec3(500.0*uvf, iTime));

    // Black noisy background
    vec3 bg_col = mix(vec3(0,0,0), vec3(0.9, 0.3, 1), smoothstep(0.2, 1.0, my_noise));
    col = bg_col;

    // Very visible noise in lower part of the circle
    if(uvf.y < 0.0) col = mix(vec3(0,0,0), vec3(0.9, 0.3, 1.), 0.1+0.9*smoothstep(0., 1., my_noise));

    // Bright lines with glow
    const float fm = 1.7;

    const vec2 po0 = vec2(-0.6*R, 0.);
    const vec2 po1 = vec2(-0.1*R, 0.);
    vec2 po0a = pfl + 0.5 * (po0-pf);
    vec2 po1a = pfl + 0.2 * (po1-pf);
    vec2 po0b = pfl + 3. * (po0-pf);
    vec2 po1b = pfl + 3. * (po1-pf);
    vec3 outer_col_orange = uvf.y > 0. ? vec3(1, 0.35, 0.) : vec3(1, .1, 0);
    vec3 inner_col_orange = vec3(.95, 1, .8);
    float df_orange = min(segment_df(uvf, po0b, po0a), segment_df(uvf, po1b, po1a)) * mix(1.0, fm, smoothstep(-R, pf.y, uvf.y));
    col = mix(outer_col_orange, col, smoothstep(0.1, 0.2, df_orange+0.1*my_noise));
    col = mix(col, inner_col_orange, step(smoothstep(0., 0.35, df_orange), my_noise));

    const vec2 pp0 = vec2(0.4*R, 0.);
    const vec2 pp1 = vec2(0.75*R, 0.);
    vec2 pp0b = pfl + 3. * (pp0-pf);
    vec2 pp1b = pfl + 3. * (pp1-pf);
    vec3 outer_col_blue = uvf.y > 0. ? vec3(0.5, .8, .9) : vec3(0.3, 0.3, 1);
    vec3 inner_col_blue = vec3(0.95, 1, 1);
    float df_purple = min(segment_df(uvf, pp0b, pfl), segment_df(uvf, pp1b, pfl)) * mix(1.0, fm, smoothstep(-R, pf.y, uvf.y));
    col = mix(outer_col_blue, col, smoothstep(0.1, 0.2, df_purple+0.1*my_noise));
    col = mix(col, inner_col_blue, step(smoothstep(0., 0.35, df_purple), my_noise));

    // Mask
    if(length(uvf) > R) col = bg_col;

    // Frame
    col = mix(vec3(1,1,1), col, smoothstep(0., 0.007, frame_sdf(uvf, pf)));


    // Output to screen
    return vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec4 colSum = vec4(0);
    for(int i = 0; i < FSAA; i++) {
        for(int j = 0; j < FSAA; j++) {
            colSum += sampleColor(fragCoord + vec2(float(i) / float(FSAA), float(j) / float(FSAA)));
        }
    }
    fragColor = colSum / colSum.w;
}

#define PI                 3.14159

#define BREATHE_SPEED      1.0
#define SACCADE_FREQUENCY  1.2
#define SACCADE_SPEED      7.0
#define BLINK_PERIOD       5.0
#define BLINK_SPEED        3.0

#define SCALE_FACTOR       1.0
#define STROKE_WEIGHT      0.01
#define EYE_SPACE          0.5


float distancetocoverage( float d ) {
    return clamp(d/SCALE_FACTOR*iResolution.y+0.5, 0.0, 1.0);
}


float circle( vec2 uv, vec2 p, float r ) {
    float d = r - distance(uv,p);
    return distancetocoverage(d);
}


float circlestroke( vec2 uv, vec2 p, float r, float t ) {
    float d = r - distance(uv,p);
    d = t - abs(d);
    return distancetocoverage(d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy/iResolution.xy*2.0 - 1.0;	// camera-space position (-1 => 1)
    uv.y *= iResolution.y/iResolution.x;

    //breathing
    float breathe = sin(iTime*BREATHE_SPEED);

    //saccades
    float sn = floor(iTime*SACCADE_FREQUENCY);
    vec2 saccade = (1.0-abs(cos(sn*67.0))) * vec2(cos(sn*PI*1.618),sin(sn*PI*1.618));
    sn++;
    vec2 nextsaccade = (1.0-abs(cos(sn*67.0))) * vec2(cos(sn*PI*1.618),sin(sn*PI*1.618));
    float st = max( (fract(iTime*SACCADE_FREQUENCY)-1.0)*SACCADE_SPEED+1.0, 0.0);
    saccade = mix( saccade, nextsaccade, st*st*(3.0-(2.0*st)) );
    saccade.x *= sign(uv.x);   //flip one eye so that it moves in the same direction after mirroring

    //blinking
    float bt = max( mod(iTime,BLINK_PERIOD)-BLINK_PERIOD+1.0, 0.0 );
    float b = max( (fract(bt)-1.0)*BLINK_SPEED+1.0, 0.0 ) ;
    float blink = 9.5212*b*(b-0.8)*(b-1.0); // animating with a cubic function for a nice little 'bounce' at the end

    uv.y += 0.05-0.005*breathe;
    uv *= SCALE_FACTOR;
    uv.x = -abs(uv.x) + EYE_SPACE;                                       // mirror
    float r = (0.15+blink*0.02)*PI;
    vec2 ruv = vec2( uv.x*cos(r)-uv.y*sin(r), uv.x*sin(r)+uv.y*cos(r) ); // rotated space for outer eye
    vec2 buv = vec2( ruv.x, ruv.y/(1.0-blink) );                         // squashed space for blinking eyes

    float aperture = circle( buv, vec2(0.0), 0.25-STROKE_WEIGHT );       // eyelid coverage

    float eyelids = 0.0;
    eyelids += circle( buv,vec2(-0.04,0.0),0.3 ) * distancetocoverage(ruv.y);                       // upper lashes
    eyelids += circle( buv,vec2(-0.04,0.0),0.25 ) * distancetocoverage(-max(ruv.x,ruv.y));          // lower lashes
    eyelids += circle( buv, vec2(0.25,0.0), 0.04 );                                                 // lacrimal caruncle
    eyelids += circlestroke( ruv,vec2(0.0),0.325,STROKE_WEIGHT) * circle(ruv,vec2(0.0,0.4),0.375 ); // eye socket upper arch
    eyelids *= 1.0-aperture;

    float eyeball = 0.0;
    vec2 ip = vec2(0.03-blink*0.01, -blink*0.05) + saccade*0.01;
    eyeball += circlestroke( uv, ip, 0.18, STROKE_WEIGHT );                         // limbal ring
    eyeball += circle( uv, ip, 0.18 ) * (1.0-circle( uv, ip-vec2(0.0,0.5), 0.5 ));  // shadow on upper iris
    eyeball += circle( uv, ip, 0.13+blink*0.02 );                                   // pupil
    float glint = circle( uv, ip, 0.025+blink*0.005 );                              // highlight
    eyeball *= 1.0-glint;
    float iriscoverage = circle( uv, ip, 0.18+STROKE_WEIGHT ) * (1.0-glint);
    eyeball *= aperture;

    float eyebrows = circle(uv,vec2(0.125,-0.05-blink*0.005),0.6) * (1.0-circle(uv,vec2(0.0),0.4)) * circle(uv,vec2(-0.3+blink*0.01,1.2),1.0);

    float nose = 0.0;
    float nh = -0.2+blink*0.002;
    nose += circlestroke( uv, vec2(EYE_SPACE,nh), 0.125, STROKE_WEIGHT ) * circle(uv,vec2(EYE_SPACE,nh+0.4),0.4); //nose
    nose += circle( uv, vec2(EYE_SPACE-0.05,nh+0.02), STROKE_WEIGHT*2.0+0.001*breathe );                          //nostril

    float mouth = 0.0;
    float mh = -0.2;
    mouth += circlestroke( uv, vec2(EYE_SPACE,mh+0.05), 0.25, STROKE_WEIGHT ) * distancetocoverage(mh-uv.y);                             //smile curve
    mouth += circle( uv, vec2(EYE_SPACE-0.245,mh), STROKE_WEIGHT*(2.0+blink*0.1) );                                                      //mouth corners
    //mouth += circlestroke( uv, vec2(EYE_SPACE,-0.9+blink*0.002), 0.625, STROKE_WEIGHT*1.0 ) * circle( uv, vec2(EYE_SPACE,-0.15), 0.25 ); //upper lip
    //aperture += circle( uv, vec2(EYE_SPACE,-0.9+blink*0.002), 0.625 ) * circle( uv, vec2(EYE_SPACE,-0.15), 0.25 );

    float face = 0.0;
    face += eyelids;
    face += eyeball;
    face += eyebrows;
    face += nose;
    face += mouth;
    face = clamp( face, 0.0, 1.0 );

    vec3 col = vec3(1.0,0.6,0.4);
    col -= vec3(0.0,0.35,0.3) * clamp(1.0-distance(uv*vec2(1.0,2.5),vec2(EYE_SPACE,-0.3)),0.0,1.0);  //blush
    col = mix(col, mix(vec3(1.0), vec3(0.1,0.7,1.0), iriscoverage), aperture);  //eye colours
    //col *= circle( uv*vec2(1.0,1.5+blink*0.02), vec2(EYE_SPACE,0.15), 1.0 );
    col *= 1.0-face;
    col = pow( col, vec3(0.45) );				//gamma adjustment
    fragColor = vec4(col,1.0);
}

#define NOISE fbm

#define OBJ_DIFF vec3(1.0, 0.0, 0.0)

#define LIGHT_DIFF vec3(1.0)
#define LIGHT_AMB  vec3(0.1)
#define LIGHT_DIR  normalize(vec3(-1.0, -1.0, -1.0))

#define EPSILON 0.001
#define PI 3.1415926538
#define NUM_NOISE_OCTAVES 8

#define REFRACTIVE_INDEX_OUTSIDE 1.0
#define REFRACTIVE_INDEX_INSIDE 1.1
#define OBJECT_REFLECTIVITY 0.1

float hash(float n) { return fract(sin(n) * 1e4); }
float hash(vec2 p) { return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); }

float noise(float x) {
	float i = floor(x);
	float f = fract(x);
	float u = f * f * (3.0 - 2.0 * f);
	return mix(hash(i), hash(i + 1.0), u);
}


float noise(vec2 x)
{
	vec2 i = floor(x);
	vec2 f = fract(x);

	// Four corners in 2D of a tile
	float a = hash(i);
	float b = hash(i + vec2(1.0, 0.0));
	float c = hash(i + vec2(0.0, 1.0));
	float d = hash(i + vec2(1.0, 1.0));

	// Simple 2D lerp using smoothstep envelope between the values.
	// return vec3(mix(mix(a, b, smoothstep(0.0, 1.0, f.x)),
	//			mix(c, d, smoothstep(0.0, 1.0, f.x)),
	//			smoothstep(0.0, 1.0, f.y)));

	// Same code, with the clamps in smoothstep and common subexpressions
	// optimized away.
	vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

float fbm(vec2 x)
{
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
	// Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < NUM_NOISE_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

vec3 surf_norm(vec2 pos)
{
    float h = NOISE(pos);

    vec3 d1 = normalize(vec3(EPSILON, 0, NOISE(pos + vec2(EPSILON, 0)) - h));
    vec3 d2 = normalize(vec3(0, EPSILON, NOISE(pos + vec2(0, EPSILON)) - h));

    return normalize(cross(d1, d2));
}

vec3 get_scene_color(vec3 ray_dir)
{
    return vec3(1.0);
}

float FresnelReflectAmount(float n1, float n2, vec3 normal, vec3 incident)
{
    // Schlick aproximation
    float r0 = (n1-n2) / (n1+n2);
    r0 *= r0;
    float cosX = -dot(normal, incident);
    if (n1 > n2)
    {
        float n = n1/n2;
        float sinT2 = n*n*(1.0-cosX*cosX);
        // Total internal reflection
        if (sinT2 > 1.0)
            return 1.0;
        cosX = sqrt(1.0-sinT2);
    }
    float x = 1.0 - cosX;
    float ret = r0 + (1.0-r0)*x*x*x*x*x;

    // adjust reflect multiplier for object reflectivity
    ret = (OBJECT_REFLECTIVITY + (1.0 - OBJECT_REFLECTIVITY) * ret);
    return ret;
}

vec3 apply_light(vec3 surf_norm, vec3 view_dir)
{
    // ambient
    vec3 diff = OBJ_DIFF * LIGHT_AMB; 

    // diffuse
    float diff_w = clamp(dot(surf_norm, -LIGHT_DIR), 0.0, 1.0);
    diff += OBJ_DIFF * diff_w * LIGHT_DIFF;

    // specular
    vec3 refl_light_dir = reflect(LIGHT_DIR, surf_norm);
    float spec_w = max(dot(-view_dir, refl_light_dir), 0.0);
    diff += pow(spec_w, 2.0) * LIGHT_DIFF;  

    // reflect
    float reflect_mult = FresnelReflectAmount(
        REFRACTIVE_INDEX_OUTSIDE,
        REFRACTIVE_INDEX_INSIDE,
        surf_norm,
        view_dir
    );

    vec3 view_refl_dir = reflect(view_dir, surf_norm);

    vec3 ret = vec3(0);
    ret += diff * (1.0 - reflect_mult);
    ret += get_scene_color(view_refl_dir) * reflect_mult;
    return ret;
}

float gamma = 2.2;
vec3 lumaBasedReinhardToneMapping(vec3 color)
{
	float luma = dot(color, vec3(0.2126, 0.7152, 0.0722));
	float toneMappedLuma = luma / (1. + luma);
	color *= toneMappedLuma / luma;
	color = pow(color, vec3(1. / gamma));
	return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0;

    vec3 norm = surf_norm(uv);
    vec3 view_dir = normalize(vec3(uv, -1.0));

    vec3 col = apply_light(norm, view_dir);

    // reinhard tone mapping
    //col = col / (col + vec3(1.0));

    // tone mapping
    //col = lumaBasedReinhardToneMapping(col);

    fragColor = vec4(col, 1.0);
}

float circ_fn(float k) { return 2.0*sqrt(0.25 - pow(k - 0.5, 2.0)); }

float circ_pattern(vec2 v)
{
    float sgn = sign( (mod(v.x+1.0, 2.0)-1.0) * (mod(v.y+1.0, 2.0)-1.0) );
    return sgn * circ_fn(mod(v.x,1.0)) * circ_fn(mod(v.y,1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tide = 0.4*sin(0.003*fragCoord.x + 0.7*iTime)
               - 0.45*cos(0.01*fragCoord.y + 0.005*fragCoord.x - 0.3*iTime)
               + 0.3*cos(0.005*fragCoord.y + 0.4*iTime + 0.002*fragCoord.x)
               - 0.25*sin(0.02*fragCoord.y-0.25*iTime);
    float baseline = circ_pattern(fragCoord/30.0);
    float v = tide - baseline;
    float brightness = smoothstep(-1.0, 1.0, v/fwidth(v));
    fragColor = vec4(vec3(brightness), 1.0);
}

#define MAX_STEPS 400
#define MAX_DIST 50.
#define SURF_DIST .001

//nabbed from blacklemori
vec3 erot(vec3 p, vec3 ax, float rot) {
  return mix(dot(ax, p)*ax, p, cos(rot)) + cross(ax,p)*sin(rot);
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 distort(vec3 p) {
    float time = 0.5 * iTime;

    vec3 q = p;
    float m = 3.5 + 3. * cos( 0.4 * length(p) -  0.5 * iTime);
    float th = 0.2 * iTime;
    for (float i = 0.; i < 9.; i++) {
        th += -0.1 * iTime;
        q.xy *= Rot(th);
        q.zy *= Rot(th);
        q = abs(q) - m; //sabs cool too
        m *= 0.28 + 0.1 * i;
    }

    float spd = 0.01;
    //float time = iTime;
    float cx = cos(time);
    float cy = cos(time + 2. * pi / 3.);
    float cz = cos(time - 2. * pi / 3.);
    q = erot(q, normalize(vec3(cx,cy,cz)), iTime);
    return cross(p, q);
}

float GetDist(vec3 p) {


    float sd = length(p - vec3(0, 3., -3.5)) - 2.2;

    //p = mix(sabs(p) - 0., sabs(p) - 1., 0.5 + 0.5 * thc(4., iTime));

    p = distort(p);
    //p.xz *= Rot(4. * p.y + iTime);
   // p = sabs(p) - 0.25;
    float d = length(p) - 1.; // was 0.8
    d *= 0.05; //smaller than I'd ike it to be
    d = -smin(-d, sd); 

    return d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = z * GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

