    float c3 = cos(time + sc * smin(p.x, p.y) + pi * cos(0.1 * iTime - tp));

    vec3 q = erot(normalize(p), normalize(vec3(c,c2,c3)), pi + 0.3 * cos(2. * length(p) - iTime));
