#define SAMPLES 200u
#define MODE 1
// mode 0: display points as cartesian
// mode 1: display points as polar
// mode 2: display points as uniform polar
// mode 3: secret :)

#define PI (3.141592653)
#define TWO_PI (2.0 * PI)

// reverses the bits of the input
uint MyBitfieldReverse(uint i)
{
	uint b =  ( uint(i) << 16u) | (uint(i) >> 16u );
	b = (b & 0x55555555u) << 1u | (b & 0xAAAAAAAAu) >> 1u;
	b = (b & 0x33333333u) << 2u | (b & 0xCCCCCCCCu) >> 2u;
	b = (b & 0x0F0F0F0Fu) << 4u | (b & 0xF0F0F0F0u) >> 4u;
	b = (b & 0x00FF00FFu) << 8u | (b & 0xFF00FF00u) >> 8u;
    return b;
}

vec2 Hammersley(uint i, uint N)
{
  return vec2(
    float(i) / float(N),
    float(MyBitfieldReverse(i)) * 2.3283064365386963e-10
  );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // rotate the UV
    float t = iTime * .25;
    mat2 rot = mat2(
        cos(t), sin(t),
        -sin(t), cos(t)
    );
    uv = (rot * (uv - 0.5)) + 0.5;

    // accumulate color based on proximity to samples
    vec3 color = vec3(0, 0, 0);
    uint s = uint(ceil(float(SAMPLES) * (.5 + .5 * sin(iTime))));
    for (uint i = 0u; i < s; i++)
    {
        float weight = 1.0;
        vec2 point = vec2(0.0);
#if MODE == 0
        point = Hammersley(i, SAMPLES);
#elif MODE == 1 || MODE == 2 || MODE == 3
        vec2 xi = Hammersley(i, SAMPLES);
    #if MODE == 1
        weight = max(.10, 3.0 * xi.x);
    #elif MODE == 2
        xi.x = sqrt(xi.x);
    #elif MODE == 3
        xi.x = float(i) / float(SAMPLES);
        xi.y = float(i) / float(SAMPLES) * PI;
        xi.y = log(xi.y);
        weight = .25;
    #endif
        float r = xi.x * 0.5;
        float theta = xi.y * TWO_PI;
        point = vec2(0.5) + vec2(r * cos(theta), r * sin(theta));
#endif
        float str = 1.0 - smoothstep(0.0, .02 * weight, distance(uv, point));
        color.rb += str;
        color.g += str * float(i) / float(SAMPLES);
    }


    fragColor = vec4(pow(color, vec3(1.0/2.2)),1.0);
}

