const float PI = 3.14159265;

vec4 flip(vec2 p) {
    vec3 a = vec3(0.0998); int ni = 58;  // parameters
    vec3 q= vec3(p, .12);  // scale
    vec3 t;
    for(int i=0; i<ni; i++) {
        a = sin(a);
        a = vec3(1.-a.y-a.x,a.y,a.x)*PI;

        vec3 c = cos(a), s = sin(a);
        mat3 N= mat3(1, 0, 0,
                 -c.x, s.x, 0,
                 -c.z, -(c.y+c.x*c.z)/s.x, 1);

        for(int j=0; j<3; j++) {
            t[j] = dot(q, N[j]);
            if(t[j] < 0.) {q -= 2.*t[j]*N[j]*vec3(1,1,0);}
        }
    }
    return vec4(q.xy, t.yz);
}



