#define bps 4.0 
float rnd(float t){return fract(sin(mod(t,32.123)*32.123)*41.123);} 
vec2 nofs(float n){//the song's "random" ring 
  float r=0.5+0.5*rnd(floor(n));//random volume as well 
  n=mod(n,8.0); 
  if(n<1.0)n= 0.0; 
  else if(n<2.0)n= 5.0; 
  else if(n<3.0)n= -2.0; 
  else if(n<4.0)n= 4.0; 
  else if(n<5.0)n= 7.0; 
  else if(n<6.0)n= 4.0; 
  else if(n<7.0)n= 2.0; 
  else n=0.0;
  return vec2(n,r); 
}
