float segment(vec2 p, vec2 a, vec2 b, bool doclamp) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  if (doclamp) h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

float segment(vec2 p, vec2 a, vec2 b) {
