            p = 50.0*MOV(9.54,1.7,1.37,1.8,sin(0.5*iTime+17.0)+1.2*iTime);
        finalColor += alien1(uv,c+p) * green;

    }

    fragColor = vec4( finalColor, sin(iTime) );
}

