// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/smin
float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pabs(float a, float k) {
  return -pmin(a, -a, k);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/spherefunctions
float sphered(vec3 ro, vec3 rd, vec4 sph, float dbuffer) {
    float ndbuffer = dbuffer/sph.w;
    vec3  rc = (ro - sph.xyz)/sph.w;

    float b = dot(rd,rc);
    float c = dot(rc,rc) - 1.0;
    float h = b*b - c;
    if( h<0.0 ) return 0.0;
    h = sqrt( h );
    float t1 = -b - h;
    float t2 = -b + h;

    if( t2<0.0 || t1>ndbuffer ) return 0.0;
    t1 = max( t1, 0.0 );
    t2 = min( t2, ndbuffer );

    float i1 = -(c*t1 + b*t1*t1 + t1*t1*t1/3.0);
    float i2 = -(c*t2 + b*t2*t2 + t2*t2*t2/3.0);
    return (i2-i1)*(3.0/4.0);
}

// "Amazing Surface" fractal
// https://www.shadertoy.com/view/XsBXWt
vec4 formula(vec4 p) {
  p.xz = abs(p.xz+1.)-abs(p.xz-1.)-p.xz;
  p.y-=.25;
  p.xy*=ROT(radians(30.0));
  p=p*2.0/clamp(dot(p.xyz,p.xyz),0.24,1.0);
  return p;
}

vec3  g_trap0 = vec3(0.0);

float rail(vec3 pos) {
  vec3 tpos =pos;
  tpos.z    = abs(3.-mod(tpos.z, 6.));
  vec4 p    = vec4(tpos,1.);

  vec3 trap0pos = vec3(-2., 0.2, -3.0);
  vec3 trap0 = vec3(1E6);

  for (int i=0; i < 4; ++i) {
    p = formula(p);
    trap0 = min(trap0, abs(p.xyz-trap0pos));
  }
  g_trap0 = trap0;

  float fr=(length(max(vec3(0.),p.xyz-1.5))-1.0)/p.w;

  return fr;
}

float df(vec3 p) {
  // Space distortion found somewhere on shadertoy, don't remember where
  vec3 wrap = cam(p.z);
  vec3 wrapDeriv = normalize(dcam(p.z));
  p.xy -= wrap.xy;
  p -= wrapDeriv*dot(vec3(p.xy, 0), wrapDeriv)*0.5*vec3(1,1,-1);

#if defined(TWISTS)
  vec3 ddcam = ddcam(p.z);
  p.xy *= ROT(-16.0*ddcam.x);
#endif  

  p.x -= 1.0;
  p.y = -pabs(p.y, 1.5);
  p.y -= -1.5;

  float dr = rail(p); 
  return dr;  
}

float rayMarch(vec3 ro, vec3 rd, out int iter) {
  float t = 0.0;
  int i = 0;
  for (i = 0; i < MAX_RAY_MARCHES; i++) {
    float d = df(ro + rd*t);
    if (d < TOLERANCE || t > MAX_RAY_LENGTH) break;
    t += d;
  }
  iter = i;
  return t;
}

vec3 normal(vec3 pos) {
  vec2  eps = vec2(NORM_OFF,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

vec3 render(vec3 ro, vec3 rd) {
  const vec3 bgcol  = vec3(2.0, 1.0, 0.75).zyx;
  vec3 lightPos     = cam(ro.z+10.0);

  int iter    = 0;
  float t     = rayMarch(ro, rd, iter);
  vec3  trap0 = g_trap0;

  float pulse = smoothstep(0.0, 1.0, sin(TAU*TIME*0.25));
  float sr    = mix(2.0, 3.0, pulse);
  float sd    = sphered(ro, rd, vec4(lightPos, sr), t);

  vec3 gcol   = mix(1.0, 1.75, pulse)*sd*sd*bgcol;

  if (t >= MAX_RAY_LENGTH) {
    return gcol;
  }

  vec3 pos  = ro + t*rd;
  vec3 nor  = normal(pos);
  vec3 refl = reflect(rd, nor);
  float ii  = float(iter)/float(MAX_RAY_MARCHES);
  vec3 ld   = normalize(lightPos - pos);
  float fre = abs(dot(rd, nor));
  fre *= fre;
  fre *= fre;
  float spe = fre*pow(max(dot(refl, ld), 0.), 10.);
  float fo  = smoothstep(0.9, 0.4, t/MAX_RAY_LENGTH);
  float ao  = 1.0-ii;

  vec3 col = vec3(0.0);
  col += pow(smoothstep(0.5, 1.0, trap0.x*0.25)*1.3, mix(6.0, 2.0, pulse))*0.5*bgcol*mix(0.5, 1.6, pulse);
  col += smoothstep(0.7, 0.6, trap0.z)*smoothstep(0.4, 0.5, trap0.z)*ao*bgcol*mix(0.05, 0.4, pulse);
  col += spe*bgcol*mix(0.66, 1.5, pulse);
  col *= 1.0-sd*sd;
  col *= fo;
  col += gcol;
  return col;
}

vec3 effect3d(vec2 p, vec2 q) {
  float z   = TIME*2.5;

  vec3 cam  = cam(z);
  vec3 dcam = dcam(z);
  vec3 ddcam= ddcam(z);

  vec3 ro = cam;
  vec3 ww = normalize(dcam);
  vec3 uu = normalize(cross(vec3(0.0,1.0,0.0)+ddcam*4.0, ww ));
  vec3 vv = normalize(cross(ww,uu));
  const float fov = 2.0/tanh(TAU/6.0);
  vec3 rd = normalize(-p.x*uu + p.y*vv + fov*ww );

  return render(ro, rd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect3d(p, q);
  col = sRGB(col);
  fragColor = vec4(col, 1.0);
}

const mat2 m = mat2(0.8, 0.6, -0.6, 0.8);

float noise(vec2 p) {
	return texture(iChannel2, p / 256.).r;
}

float fbm4(vec2 p) {
    float f = 0.0;
    f += 0.5000*noise(p); p = m*p*2.02;
    f += 0.2500*noise(p); p = m*p*2.03;
    f += 0.1250*noise(p); p = m*p*2.01;
    f += 0.0625*noise(p);
    return f/0.9375 * 2. - 1.;
}

float rect(vec2 p, vec2 hs) {
    vec2 a = abs(p) - hs;
    return step(max(a.x, a.y), 0.);
}

float TextSDF(vec2 p, float glyph){ 
	float v = texture(iChannel0, p / 16. + fract(vec2(glyph, 15. - floor(glyph / 16.)) / 16.)).w;
    float start = 0.5;
    float border = 0.003;
    return smoothstep(start + border, start, v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime;
    vec2 p = (fragCoord - iResolution.xy * 0.5) / min(iResolution.y, iResolution.x);

    vec3 background = texture(iChannel3, p * 0.5 + vec2(0.12, -0.05)).rgb;

    p *= 1.4;
    float depth = background.r * 0.299 + background.g * 0.587 + background.b * 0.114;
    vec3 normal = vec3(dFdx(depth), dFdy(depth), 0.333) * 3.;

    float number = floor(mod(time, 256.) * 2.);

    vec2 textureP = (p + vec2(0.5, +0.5));
    textureP += texture(iChannel1, p).xy * 0.01;

    vec2 fbm = vec2(fbm4(textureP * 10.), fbm4(textureP * 10. + vec2(13., 7.)));

    float textAlpha = TextSDF(textureP + fbm * 0.06, number) * rect(p, vec2(0.5));

    float fbm2 = fbm4(textureP * 10. + vec2(53., 17.)) * 0.2 + 0.8;
    float depthToBrightness = 0.3 + 1.7 * depth;
    float lightForNormal = dot(normal, normalize(vec3(0.3, 0.3, 0.9)));
    vec3 color = vec3(textAlpha * fbm2 * vec3(0.4, 0.85, 0.95) * depthToBrightness * lightForNormal) + background * (1. - textAlpha);

    fragColor = vec4(color, 1.0);
}

#define MAX_STEPS 400
#define MAX_DIST 35.
#define SURF_DIST .001

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

#define pi 3.14159

float h21 (float a, float b, float zoom) {
    a = mod(a, zoom); b = mod(b, zoom);
    return fract(sin(dot(vec2(a, b), vec2(12.9898, 78.233)))*43758.5453123);
}

// rand value that changes with val (time usually)
float rand(vec2 ipos, float val) {
    float a = 2. * pi * h21(ipos);
    float c = cos(a), s = sin(a);

    float f = floor(val);

    // current value (using 0.01 so it looks "random" for longer)
    float v = h21(vec2(c * f, s * f) + 0.01 * ipos);
    // next value
    float v2 = h21(vec2(c * (f + 1.), s * (f + 1.)) + 0.01 * ipos);

    // smooth lerp between values
    return mix(v, v2, smoothstep(0., 1., fract(val)));
}

float GetDist(vec3 p) {
    // Rotate xz plane with height + time
    vec2 uv = p.xz;
    uv *= Rot(0.28 * p.y - 0.1 * iTime);
    uv *= 3.;

    // Cut into grid
    vec2 ipos = floor(uv) + 0.5;
    vec2 fpos = uv - ipos;

    // Rand values
    float h = h21(ipos);
    float h2 = rand(ipos, h + 0.2 * iTime);

    // Each cell rotates with height+time, randomly
    float time = h * 4. * iTime + p.y * h2 * 9.; // + 2. * pi * h; // dont need offset
    vec2 q = h2 * 0.1 * vec2(cos(time), sin(time));

    // Radius of each squiggle (height+time), offset randomly
    float r = 0.1 * (1. + 0.5 * cos(2. * pi * h + 4. * p.y + iTime));
    float d = length(fpos - q) - r; 

    // Cut sphere out of squiggles so we can see - smin looks nice
    float sd = length(p - vec3(0, -3.5, -1.3)) - 0.7;

    // (artifacts appear in furthest tiles if d is larger)
    d = -smin(-0.35 * d, sd); 
    return d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {
    float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}


vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    // (sphere cut-out doesnt follow mouse, but you can still look)
    vec3 ro = vec3(0, -3.5, -1.3);
    if (iMouse.z > 0.) {
        ro.yz *= Rot(-m.y*3.14+1.);
        ro.xz *= Rot(-m.x*6.2831);
    }
    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.8);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);

        //float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        //col = vec3(dif);

        vec3 rdIn = refract(rd, n, 1./IOR);

        vec3 pEnter = p - n*SURF_DIST*30.;
        float dIn = RayMarch(pEnter, rdIn, -1.); // inside the object

        vec3 pExit = pEnter + rdIn * dIn; // 3d position of exit
        vec3 nExit = -GetNormal(pExit);

        float fresnel = pow(1.+dot(rd, n), 3.);
        col = 2.5 * vec3(fresnel);

        // No idea what this does, but I need it
        col *= 0.55 + 0.45 * cross(nExit, n); 

        //col = clamp(col, 0., 1.); // clamp removes highlighting that I want       

        // Cylinder length
        float ln = length(p.xz);

        // Stripey patterns etc
        vec3 e = vec3(1.);
        col *= pal(4.*ln + .05*iTime, e, e, e, 
                   0.5 + 0.5 * thc(3., 20.*p.y + 10.*ln - 8.*iTime) * vec3(0,1,2)/3.);

        // Darken with height
        col *= (0.32 * p.y + .95);

        // Fake shadows (I think)
        col *= 0.8 + 1. * n.y; 

        // Darken in a cylinder (less aliasy thingies from far away / fake vignette)
        col *= clamp(1. - 0.3 * ln, 0., 1.);
        //float val = 0.5 + 0.5 *  thc(4., -0.2 * p.y + 1. * iTime);//clamp(5. + 5. * cos(p.y + iTime), 0., 1.);

        // Mix col with fres vertically, fake fog
        fresnel = pow(1.+dot(rd, n), 1.);
        col = mix(col, vec3(fresnel), clamp(-0.3 + 0.15 * p.y, 0., 1.));
    }

    col = pow(col, vec3(.4545));	// gamma correction
    col += 0.04;
    fragColor = vec4(col,1.0);
}

common_code:
#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

// (SdSmoothMin) stolen from here: https://www.shadertoy.com/view/MsfBzB
float smin(float a, float b)
{
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

