float sdSpeaker(vec3 p, float amplitude){
    p -= vec3(0,-0.3,0);

    return
                smin(
                    min(
                        sdCutHollowSphere(p - vec3(0, -1. + amplitude*2., 0.), .4,-.15, 0.01),
                        sdSphere(p - vec3(0, -1.4 + amplitude*2., 0.), 0.1)
                        ),
                    sdTorus(p - vec3(0,-0.30,0.), 0.4, 0.03),
                    0.01
                    );                 
}
