float sdScene(vec3 p, float amplitude) {
        vec3 q = p;
        p = repeat(p);

        vec2 id = p.xz-q.xz;

        float off = hash12(round(id)*10.);
        amplitude = smoothstep(0.,1.,texture(iChannel0, vec2(0.02+off*0.5, 0)).x)*0.15+0.35;

        return smin(
                    sdSpeaker(p, amplitude),
                    opSubtraction(
