                                   sdPlane(p, -.6)
                                  ),
                    0.001
                    );
}

//iq
vec3 softshadow( in vec3 ro, in vec3 rd, float amplitude, float mint, float maxt, float k )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = sdScene(ro + rd*t, amplitude);
        if( h<0.001 )
            return vec3(0.0);
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return mix(vec3(res),vec3(1.000,0.702,0.602)*res,smoothstep(1.,-.5,abs(0.5-res)));
}

