vec3 calcNormal( in vec3 p, float amplitude ) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdScene(p + k.xyy*h, amplitude ) + 
                      k.yyx*sdScene(p + k.yyx*h ,amplitude ) + 
                      k.yxy*sdScene(p + k.yxy*h, amplitude ) + 
                      k.xxx*sdScene(p + k.xxx*h, amplitude ) );
}

float calcAO(vec3 pos, vec3 nor,float a){
	float occ = 0.0;
    float sca = .4;
    for( int i=0; i<5; i++ )
    {
            float h = 0.01 + 0.25*float(i)/4.0;
        float d = sdScene(pos+h*nor, a);
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

vec3 intersection(vec3 ro, vec3 rd){

