    vec2 mouseUV = (iMouse.xy-iResolution.xy*.5f)/iResolution.x * 5.;
    float T = 0.;

    //Colour 
    float h = sdScene(pos,0.);


    //The interesting part
    float omega = 1.;
    float pom = 1.;
    float ph = 1e5;
    vec2 gap = vec2(0.,0.);
    for (int i = 0; i<40; ++i) {

        //Position and distance estimation
        vec3 p = ro+T*rd;
        h = sdScene(p,0.);


        //Overstep recovery
        float om = (ph+h)/ph;
        if (om<pom && pom > 1.) { //ph+h<ph*pom

            gap = vec2(h,T);
            T+=(1.-pom)*ph;
            pom = 1.;

        } else {

            //Variable updates
        	T += h * omega;
            ph = h;
            pom = omega;

            //Back to the place where the gap opened (foward)
        	if (T>=gap.y-gap.x && gap.y+gap.x > T) {T = gap.y+gap.x*omega; ph = gap.x;};
        }


        //Dynamic Omega 
        //omega = clamp((omega+om)*.5,1.,3.);
        omega = clamp(omega+(om-omega)*.6,1.,3.);
    }
    return ro+T*rd;;
}
