const float MAXD = 100.;
const float MAXSHD = 4.5;
const float MAXRD = 15.;
float t;
// 2d rotation
vec2 rot(vec2 p, float r) {
	float c = cos(r), s = sin(r);
	return vec2(p.x * c - p.y * s, p.x * s + p.y * c);
}
float dcube( vec3 p, float b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
//dh hight
//a phase 0 to 1
float bounce(float dh,float a)
{
    if (dh==0.) return 0.;
    float g=10.;
    float v0 = sqrt(dh*4.*g);
    float t = mod(iTime + a*v0/g+ 0.*v0/g,v0/g); 
    return -g*t*t+v0*t;
}
vec4 opu(vec4 a,vec4 b){ return (a.x<b.x) ? a : b;}
vec4 map(vec3 p)
{
    vec3 blue = vec3(.7,.7,.9); //normalize(vec3(.3,.4,.8));
    vec3 pink = vec3(1.,.8,.9);
    vec3 q=p;
    p.x=mod(p.x,4.)-2.;
    float s = (q.x-p.x)/4.-.5;
	vec3 u = p;   
    float x = mod(iTime*4. ,4.);
    float r = 1.;


    vec4 plan = vec4(p.y,blue-.1);
    vec4 cube = vec4(10., blue*.3-.1 +.5*vec3(1.,sin(s*77.+49.)*.5+.5,sin(s*33.)*.5+.5)*.3+.7 ) ;
    cube.x = dcube(p - vec3(0., .65, 0.),.65);


    float h = bounce(10./4.,.5); //period =1.


    vec4 ball = vec4(0. ,pink);
    ball.x = length(p - vec3(x,r+h,0.))-r;

    vec4 ball2 = vec4(0. ,pink);
    ball2.x = length(p - vec3(x-4.,r+h,0.))-r;



    vec4 o = opu(ball, ball2);
    o = opu(o, plan);
    o = opu(o, cube);


    //o.yzw = vec3(1.,.8,.9); //color 

    return o;//vec4(d,1.,0.,0.);
}
//simple raymarch
vec4 rayMarch(vec3 rO, vec3 rD)
{   
    float d = 0.;
    vec4 D;
    for (int i = 0; i<130 &&  d < MAXD; i++)
    {
        D = map(rO + rD * d);
        //d += D.x;
        if (D.x > .5)  d += D.x*.5;   else d += D.x;
        if (D.x < 3e-4)  break;
    }
    return vec4(d, D.yzw);
}

//to learn about normal using sdf
//https://iquilezles.org/articles/normalsSDF
vec3 normal(vec3 p)
{
    vec2 N = vec2(0., 1e-2);
    return normalize(vec3(map(p-N.yxx).x, map(p-N.xyx).x, map(p-N.xxy).x));
}
vec3 simpleSky(vec3 p){   
    return vec3(.5,.6,1.-p.y);
}
// you can learn more about soft shadows in this article https://iquilezles.org/articles/rmshadows
float shadows(vec3 or, vec3 lD)
{
    const float k = 1.5;
    float res = 1.;
    float t = 0.;
    for (int i = 0; i < 40; i++)
    {
        float d = map(or + lD*t).x;
        res = min(res, .5 + d * k / t);
        if (res < .01|| t > MAXSHD) break;
        t += d;
    }
    res = max(res, 0.);
    return smoothstep(.3, 1., res);
}
vec4 render(in vec2 fragCoord )
{
    float fov = iResolution.x;
    vec2 uv = fragCoord - iResolution.xy / 2.;
    uv = uv - .5;
    uv /= fov;
    float ww=1.+ t*.05;
    vec3 dis = vec3(iTime,2.,0.);
    vec3 or = vec3(-1.,.5, 1.)*16.+dis;
    //or.y = 20.;
    //or.y-=17.5;
    vec3 w = normalize(vec3(0.,0.,0.)-or+dis);
    vec3 u = normalize(cross(w, vec3(0., 1., 0.)));
    vec3 v = cross(u, w);
    vec3 rD = normalize(w*1.5+uv.x*u+uv.y*v);

    vec4 d = rayMarch(or, rD);
    vec3 col = vec3(0,0,0);

    if (d.x < MAXD)
    {
        vec3 p = or + d.x * rD;
    	vec3 nrm = normal(p);
        vec3 sun = -normalize(vec3(.4, 2., .8));

        col = vec3(dot(sun,nrm)) *d.yzw;
        //col *= d.yzw; //colloring
        col *= vec3(shadows(p - sun * 0.01, -sun));

    }
    else 
    {
        col = simpleSky(rD);
    }

    return vec4(col,1.0);
}
