vec3 colorize(float its) {
    float x = mod(its*6.0, 6.0);
    return vec3((x<4.50)?smoothstep(4.0, 0.4, x):smoothstep(0.0, 1.4, x-4.6),
                (x<4.25)?smoothstep(2.2, 0.1, x):smoothstep(0.0, 2.1, x-3.9),
                (x<2.00)?smoothstep(1.1, 0.0, x):smoothstep(0.0, 4.0, x-2.0));
}

float mandelbrot(vec2 c) {
    vec2 z = c;
    float i = 0.;
    for (i; i<512.; i++) {
        z = vec2(z.x*z.x-z.y*z.y, 2.*z.x*z.y)+c;
        if (dot(z,z) > 16.0) break;
    }
    if (i == 512.) {
        return -1.;
    }

    //iq wrote continuous iteration count
    return i - log2(log2(dot(z,z))) + 4.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // Output to screen
    vec3 col;
    float di = distance(fragCoord/iResolution.y, vec2(0.5*(iResolution.x/iResolution.y), 0.5));

    vec3 mcol;
    vec2 tuv = (fragCoord-iResolution.xy/2.)/iResolution.y;
    float d = iTime/5.;
    vec2 n = vec2(tuv.x*cos(d)-tuv.y*sin(d), tuv.y*cos(d)+tuv.x*sin(d));
    float revt = (sin(d)+1.)*7.+8.;
    float z = (revt*revt/pow(2., revt));
    float r = mandelbrot(n*z+vec2(-0.167426747312500013323, 1.04127240284375));
    if (r<0.) {
        mcol = vec3(0, 0, 0);
    } else {
        mcol = vec3(colorize(r/40.0+iTime/24.0));
    }

    vec3 tcol = texture(iChannel0, uv).xyz;

    float s = smoothstep(0.0, 1.0, di);

    fragColor = vec4((1.0-s)*mcol + s*tcol, 1.0);
}

