void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float scale = 5.+3.*iMouse.x/iResolution.y;
    vec2 uv1 = uv*scale+vec2(iTime,0);//scale and tile
    vec2 gv = fract(uv1)-.5;//grid uv

    vec3 col;
    //if(gv.x>.48 ||gv.y>.48) col = vec3(1,0,.6);//grid line

    vec2 n = hash(floor(uv1));
    gv = rotate(gv,floor(n.x*4.)*1.5708);//n.x - random dirction
    int ty = int(floor(n.y*6.));//n.y - random tile

    float w = 0.16666667;

    float m;
    switch(ty){
    case 0://2 arc
    m = s(abs(length(gv-0.5)-0.5),w,scale)+s(abs(length(gv+0.5)-0.5),w,scale);break;
    case 1://1 arc
    m = s(abs(length(gv-0.5)-0.5),w,scale)+s(length(gv+vec2(0,.5)),w,scale)+s(length(gv+vec2(.5,0)),w,scale);break;
    case 2://1 line
    m = s(abs(gv.x),w,scale)+s(length(abs(gv)-vec2(.5,0)),w,scale);break;
    case 3://t line
    m = s(gv.x,w,scale)-s(length(abs(gv)-vec2(.5)),2.*w,scale)+s(length(gv+vec2(-.5,0)),w,scale);break;
    case 4://+ line
    m = 1.-s(length(abs(gv)-vec2(.5)),2.*w,scale);break;
    case 5:
    default:
    //4pt
    m = s(length(abs(gv)-vec2(0,.5)),w,scale)+s(length(abs(gv)-vec2(.5,0)),w,scale);break;
    }
    m = clamp(m,0.,1.);

    col += m*.6;
    col =mix(col,vec3(1),logo(uv));
    fragColor = vec4(col,1.0);
}


float logo(vec2 uv){
    float n = S(.05,abs(length(uv-vec2(-1.23,0))-.12));
    n *= 1.-S(-1.25,uv.x);
    n =max(n, S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29)));
    n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
    n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
    n += clamp((1.+S(-.6,uv.x)-S(.05,uv.y)),0.,1.)*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
    n += clamp((1.-S(-.6,uv.x)+S(-.05,uv.y)),0.,1.)*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
    return n;
}

