    float bl0 = sdQuads(vec2(0.1,0.1),vec2(-0.1,0.15),vec2(-0.1,-1.5),vec2(0.1,-1.5),uvRef);
    float bl1 = sdQuads(vec2(0.5,-0.05),vec2(0.1,0.05),vec2(0.1,-1.5),vec2(0.5,-1.5),uvRef);
    float bl2 = sdQuads(vec2(-0.1,0.05),vec2(-0.3,-0.1),vec2(-0.3,-1.5),vec2(-0.1,-1.5),uvRef);
    float bl3 = sdQuads(vec2(-0.3,-0.3),vec2(-0.6,-0.3),vec2(-0.6,-1.5),vec2(-0.3,-1.5),uvRef);
    float blD = unionSDF(bl0,bl1);
    float blD1 = unionSDF(bl2,bl3);
    float blD2 = unionSDF(blD,blD1);

    uv=p2.xy;
    uvRef=uv-vec2(1.25,0.2);
    float ml0 = sdQuads(vec2(0.1,-0.2),vec2(-0.1,-0.25),vec2(-0.1,-1.5),vec2(0.1,-1.5),uvRef);
    float ml1 = sdQuads(vec2(0.5,0.1),vec2(0.1,-0.05),vec2(0.1,-1.5),vec2(0.5,-1.5),uvRef);
    float ml2 = sdQuads(vec2(-0.1,-0.2),vec2(-0.3,-0.15),vec2(-0.3,-1.5),vec2(-0.1,-1.5),uvRef);
    float ml3 = sdQuads(vec2(-0.3,-0.3),vec2(-0.65,-0.3),vec2(-0.65,-1.5),vec2(-0.3,-1.5),uvRef);
    float mlD = unionSDF(ml0,ml1);
    float mlD1 = unionSDF(ml2,ml3);
    float mlD2 = unionSDF(mlD,mlD1);

    float dfin = unionSDF(blD2,mlD2);
    float  d1=sdBox( uv-vec2(0.25,-0.45), vec2(0.25,0.25) );
    float  d2=sdBox( uv-vec2(1.1,-0.45), vec2(0.25,0.25) );
    dfin= differenceSDF(dfin, d1);
    dfin= differenceSDF(dfin, d2);

    p.z=abs(p.z)-1.0;
    float d3=sdBox(p, vec3(1.5,1.5,0.15) );
    dfin= intersectSDF(dfin, d3);
    float d4=sdBox(p-vec3(0.0,-0.2,0.0), vec3(2.5,0.05,0.15) );
    float d5=sdBox(p-vec3(0.0,-1.0,0.0), vec3(2.5,0.25,0.15) );
    dfin = unionSDF(dfin,d4);
    dfin = unionSDF(dfin,d5);    
      res =opU2(res, vec2(dfin,100.0));
      return res;
}


vec2 map(vec3 p)
{
    vec3 tmp, op = p;
    vec2 res;
    // plane
    float planeDist=p.y+1.0;
    //float planeID = 2.0;
    float planeID = 101.0;
    vec2 plane = vec2(planeDist, planeID);
    res=plane;
    vec2 re1= pared01(p);
    res =opU2(res, re1);
    return res;
}

vec3 getMaterial(vec3 p, float id, vec3 normal) {
    vec3 m;
    switch (int(id)) 
    {
        case 1:
        m = vec3(0.9, 0.0, 0.0); break;

        case 2:
        m = vec3(0.2 + 0.4 * mod(floor(p.x*0.5) + floor(p.z*0.5), 2.0)); break;

        case 3:
            m = vec3(0.7, 0.8, 0.9); break;

        case 4:
            vec2 i = step(fract(0.5 * p.xz), vec2(1.0 / 10.0));
            m = ((1.0 - i.x) * (1.0 - i.y)) * vec3(0.37, 0.12, 0.0); break;
        case 100:
            m = triPlanar(iChannel0, p , normal); m=m*m; break;
        case 101:
            m = triPlanar(iChannel1, p , normal);  break;

      }  
    return m;
}

vec2 rayMarch(vec3 ro, vec3 rd)
{
    vec2 hit, object;
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + object.x * rd;
        hit = map(p);
        object.x += hit.x;
        object.y = hit.y;
        if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }
    return object;
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(EPSILON, 0.0);
    vec3 n = vec3(map(p).x) - vec3(map(p - e.xyy).x, map(p - e.yxy).x, map(p - e.yyx).x);
    return normalize(n);
}

float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 1.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
        float hit = map(p + lightPos * dist).x;
        res = min(res, hit / (dist * lightSize));
        dist += hit;
        if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float getAmbientOcclusion(vec3 p, vec3 normal) {
    float occ = 0.0;
    float weight = 1.0;
    for (int i = 0; i < 8; i++) {
        float len = 0.01 + 0.02 * float(i * i);
        float dist = map(p + normal * len).x;
        occ += (len - dist) * weight;
        weight *= 0.85;
    }
    return 1.0 - clamp(0.6 * occ, 0.0, 1.0);
}

vec3 getLight(vec3 p, vec3 rd, float id, vec3 plightPos ) 
{   vec3 lightPos = plightPos;
    vec3 L = normalize(lightPos - p);
    vec3 N = getNormal(p);
    vec3 V = -rd;
    vec3 R = reflect(-L, N);
    vec3 color = getMaterial(p, id, N);
    vec3 specColor = vec3(0.6, 0.5, 0.4);
    vec3 specular = 1.3 * specColor * pow(clamp(dot(R, V), 0.0, 1.0), 10.0);
    vec3 diffuse = 0.9 * color * clamp(dot(L, N), 0.0, 1.0);
    vec3 ambient = 0.05 * color;
    vec3 fresnel = 0.15 * color * pow(1.0 + dot(rd, N), 3.0);

    // shadows
    float shadow = getSoftShadow(p + N * 0.02, normalize(lightPos));
    // occ
    float occ = getAmbientOcclusion(p, N);
    // back
    vec3 back = 0.5 * color * clamp(dot(N, -L), 0.0, 1.0);
    return  (back+ambient + fresnel) * occ + (specular * occ + diffuse) * shadow;    
}

mat3 getCam(vec3 ro, vec3 lookAt) {
    vec3 camF = normalize(vec3(lookAt - ro));
    vec3 camR = normalize(cross(vec3(0, 1, 0), camF));
    vec3 camU = cross(camF, camR);
    return mat3(camR, camU, camF);
}

void mouseControl(inout vec3 ro) {
    vec2 m = iMouse.xy / iResolution.xy;
    pR(ro.yz, m.y * PI * 0.39 - 0.39);
    pR(ro.xz, m.x * TAU);
}

vec3 render(vec2 uv) 
{
    vec3 col = vec3(0);
    vec3 background = vec3(0.5, 0.8, 0.9);

     float t;
    t=mod(iTime*0.5,500.0);

    vec3 ro=vec3(0.0,-0.5,0.15);
   ro.x=t;
    //mouseControl(ro);
    vec3 rd=normalize( vec3(uv.x,uv.y,1.0));

   rd= rotate_y(rd, radians(90.0));
    vec3 light_pos1= vec3(-10.0, 120.0, 10.0 );
 	vec3 light_pos2= vec3(10.0, 30.0, -25.0 );

    light_pos1+=ro;
    light_pos2+=ro;
    vec2 object = rayMarch(ro, rd);

    if (object.x < MAX_DIST) {
        vec3 p = ro + object.x * rd;
        col += getLight(p, rd, object.y,light_pos2);
        //col += getLight(p, rd, object.y,light_pos1);
        //col/=2.0;
        // fog
        col = mix(col, background, 1.0 - exp(-1e-7 * object.x * object.x * object.x));
    } else 
    {
        //col += background - max(0.7 * rd.y, 0.0);
        ///by ocb --11-feb-2022
          col += smoothstep(.45,.9,texture(iChannel1,vec2(.1/rd.y-.02*iTime,.3*rd.z)).z) + background - max(0.7 * rd.y, 0.0);
    }
    return col;
}

vec2 getUV(vec2 offset, vec2 frag) 
{   return (2.0 * (frag.xy + offset) - iResolution.xy) / iResolution.y; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   vec3 color= render( getUV(vec2(0.0), fragCoord)  );  
    // gamma correction
    color = pow(color, vec3(0.4545));
    fragColor = vec4(color, 1.0);
}

