vec3 o502144_input_hdri(vec2 uv, float _seed_variation_) {

return Simple360HDR_make360hdri(vec2((uv).x,-(uv).y+1.0),normalize(vec3(-p_o502144_SunX,p_o502144_SunY,-p_o502144_SunZ)));
}

//tetrahedron normal by PauloFalcao
//https://www.shadertoy.com/view/XstGDS
vec3 normal_o502144(vec3 p, float _seed_variation_) {  
  const vec3 e=vec3(0.001,-0.001,0.0);
  float v1=o502144_input_mfsdf(vec4(p+e.xyy,0.0), _seed_variation_).w;
  float v2=o502144_input_mfsdf(vec4(p+e.yyx,0.0), _seed_variation_).w;
  float v3=o502144_input_mfsdf(vec4(p+e.yxy,0.0), _seed_variation_).w;
  float v4=o502144_input_mfsdf(vec4(p+e.xxx,0.0), _seed_variation_).w;
  return normalize(vec3(v4+v1-v3-v2,v3+v4-v1-v2,v2+v4-v3-v1));
}

void march_o502144(inout float d,inout vec3 p,float dS, vec3 ro, vec3 rd, float _seed_variation_) {
    for (int i=0; i < 500; i++) {
    	p = ro + rd*d;
        dS = o502144_input_mfsdf(vec4(p,0.0), _seed_variation_).w;
        d += dS;
        if (d > 50.0 || abs(dS) < 0.0001) break;
    }
}

//from https://www.shadertoy.com/view/lsKcDD
float calcAO_o502144( in vec3 pos, in vec3 nor , float _seed_variation_) {
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ ){
        float h = 0.001 + 0.25*float(i)/4.0;
        float d = o502144_input_mfsdf(vec4( pos + h*nor ,0.0), _seed_variation_).w;
        occ += (h-d)*sca;
        sca *= 0.98;
    }
    return clamp( 1.0 - 1.6*occ, 0.0, 1.0 );    
}

//from https://www.shadertoy.com/view/lsKcDD
float calcSoftshadow_o502144( in vec3 ro, in vec3 rd, in float mint, in float tmax, float _seed_variation_) {
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration
    for( int i=0; i<32; i++ ){
		float h = o502144_input_mfsdf(vec4( ro + rd*t ,0.0), _seed_variation_).w;
        res = min( res, 10.0*h/t );
        t += h;
        if( res<0.0001 || t>tmax ) break;  
    }
    return clamp( res, 0.0, 1.0 );
}

vec3 raymarch_o502144(vec2 uv, float _seed_variation_) {
    uv-=0.5;
	vec3 cam=vec3((sin(sin(iTime*0.1))*6.0),(sin(iTime*0.03)+2.0),(cos(sin(iTime*0.1))*4.0))*p_o502144_CamZoom;
	vec3 lookat=vec3(p_o502144_LookAtX,p_o502144_LookAtY,p_o502144_LookAtZ);
	vec3 ray=normalize(lookat-cam);
	vec3 cX=normalize(cross(vec3(0.0,1.0,0.0),ray));
	vec3 cY=normalize(cross(cX,ray));
	vec3 rd = normalize(ray*p_o502144_CamD+cX*uv.x+cY*uv.y);
	vec3 ro = cam;

	float d=0.;
	vec3 p=vec3(0);
	float dS=0.0;
	march_o502144(d,p,dS,ro,rd, _seed_variation_);

    vec3 color=vec3(0.0);
	vec3 objColor=o502144_input_mfsdf(vec4(p,1.0), _seed_variation_).xyz;   // 1 - BaseColor (r,g,b,sdf) linear (0-1) 
	float objSpecular=o502144_input_mfsdf(vec4(p,4.0), _seed_variation_).x*0.2;  // 4 - Specular  (v,0,0,sdf)
	float objRoughness=o502144_input_mfsdf(vec4(p,5.0), _seed_variation_).x; // 5 - Roughness (v,0,0,sdf)
	vec3 light=normalize(vec3(p_o502144_SunX,p_o502144_SunY,p_o502144_SunZ));
	if (d<50.0) {
	    vec3 n=normal_o502144(p, _seed_variation_);
		float l=clamp(dot(-light,-n),0.0,1.0);
		vec3 ref=normalize(reflect(rd,-n));
		float r=clamp(dot(ref,light),0.0,1.0);
		float cAO=mix(1.0,calcAO_o502144(p,n, _seed_variation_),p_o502144_AmbOcclusion);
		float shadow=mix(1.0,calcSoftshadow_o502144(p,light,0.05,5.0, _seed_variation_),p_o502144_Shadow);
		color=min(vec3(max(shadow,p_o502144_AmbLight)),max(l,p_o502144_AmbLight))*max(cAO,p_o502144_AmbLight)*objColor+4.0*pow(r,pow(256.0,(1.0-objRoughness)))*objSpecular;
		//reflection
		d=0.01;
		march_o502144(d,p,dS,p,ref, _seed_variation_);
		vec3 objColorRef=vec3(0);
		if (d<50.0) {
			objColorRef=o502144_input_mfsdf(vec4(p,1.0), _seed_variation_).xyz;
			n=normal_o502144(p, _seed_variation_);
			l=clamp(dot(-light,-n),0.0,1.0);
			objColorRef=max(l,p_o502144_AmbLight)*objColorRef;
		} else {
			objColorRef=o502144_input_hdri(equirectangularMap(ref.xzy), _seed_variation_).xyz;
		}
		color=mix(color,objColorRef,objSpecular);
	} else {
		color=o502144_input_hdri(equirectangularMap(rd.xzy), _seed_variation_).xyz;
	}
	return pow(color,vec3(1.0/p_o502144_Gamma));
}




vec3 shader(vec2 fragCoord){

    float minSize = min(iResolution.x, iResolution.y);
    float _seed_variation_ = SEED_VARIATION;
    vec2 UV = vec2(0.0, 1.0) + vec2(1.0, -1.0) * (fragCoord-0.5*(iResolution.xy-vec2(minSize)))/minSize;
    vec3 o502144_0_1_rgb = raymarch_o502144((UV), _seed_variation_);

    return vec3(o502144_0_1_rgb);
}




