#define Aperture .05
#define DoFClamping .35
#define DoFSamples 32

#define dir normalize(vec2(1.0, 1.0))


void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    //autofocus
    float focalDistance = texture(iChannel0, vec2(.5), 5.0).a;

    vec4 col = vec4(0);
    float samples = 0.0, dr, influence, depth;
    vec2 d;
    vec4 p;
    for(int i = 0; i < DoFSamples; i++){
        d = dir * float(2*i-DoFSamples)/float(DoFSamples) * DoFClamping;
        p = texture(iChannel0, (fragCoord + d*iResolution.y*Aperture)/iResolution.xy);
        dr = min(abs(p.a-focalDistance)/p.a, DoFClamping);
        influence = clamp((dr - length(d))*iResolution.y*Aperture + .5, 0.0, 1.0) / (dr*dr+.001);
        col += influence * p;
        samples += influence;
    }

    col /= samples;

    fragColor = col;
}

