void mainImage(out vec4 col, in vec2 coord) {
    vec4 buf = texture(iChannel0, coord/iResolution.xy);
    col = buf / buf.w;

    // red bar is used as a rough sample counter
    // when it spans across the screen there's at least 100,000
    // samples done
    if(coord.y < 2.) {
        float x = iResolution.x * buf.w / 100000.;
        if(coord.x < x) {
            col = vec4(1.,0.,0.,1.);
        }
    }
}

