#define MAX_ITER 80
#define MAX_DIST 15.
#define MIN_DIST .001

#define BOUNCES 6

const vec3 SUN_DIR = normalize(vec3(45.,30.,-45.));
const vec3 SUN_COL = vec3(1.);

vec2 dstMin(vec2 a, float bt, float bm) {
    return a.x < bt ? a : vec2(bt,bm);
}

vec2 dstScene(vec3 p, bool noGlass) {
    vec2 d = vec2(p.y,0.);

    d = dstMin(d, sdBoxFrame(p-vec3(0.,.4,0.), vec3(1.,.4,.8), .05), 1.);
    d = dstMin(d, length(p-vec3(.4,1.06,0.))-.2, 3.);


    d = dstMin(d, length(p-vec3(-.8,1.06,.5))-.2, 3.);

    if(!noGlass) {
        d = dstMin(d, sdCappedCylinder(p-vec3(0.,.85,0.), 1.3, .02), 2.);
    }
    return d;
}

vec2 raymarch(vec3 ro, vec3 rd, float mint, bool noGlass) {
    float t = mint;
    float m = -1.;
    for(int i = 0; i < MAX_ITER; i++) {
        vec2 d = dstScene(ro+rd*t, noGlass);
        if(abs(d.x) < MIN_DIST || t > MAX_DIST) break;
        t += d.x;
        m  = d.y;
    }
    return vec2(t,m);
}

vec3 normal(vec3 p, float t) {
    vec2 e = vec2(.001*t,0.);
    vec3 n = vec3(
        dstScene(p+e.xyy,false).x-dstScene(p-e.xyy,false).x,
        dstScene(p+e.yxy,false).x-dstScene(p-e.yxy,false).x,
        dstScene(p+e.yyx,false).x-dstScene(p-e.yyx,false).x
    );
    return normalize(n);
}

void material(inout Material mat, in float id, in vec3 p) {
    if(id == 0.) { // ground
        mat.diffuse    = texture(iChannel0, p.xz / 2.).rgb;
        mat.smoothness = 0.;
    } else if(id == 1.) { // metal frame
        mat.diffuse    = vec3(.5);
        mat.smoothness = .9;
    } else if(id == 2.) { // glass top
        mat.diffuse    = vec3(.4,.4,.6); 
        mat.smoothness = 1.;
    } else if(id == 3.) { // ball
        mat.diffuse    = vec3(1., .2, .2);
        mat.smoothness = .1;
    }
}

vec3 lightRay(in vec3 n, in vec3 r, in Material mat) {
    if(frand() < .05) return mix(SUN_DIR,cosineDirection(n),.01);
    else if(frand() < mat.smoothness) return r;
    else return cosineDirection(n);
}

//void refractionRayThroughSurface(inout vec3 ro, inout vec3 rd, in vec3 n) {
//    rd = normalize(refract(rd, n, 1./1.52));
//    ro = ro + rd * .001;
//    // if the point is still inside the glass surface, march until
//    // it's not anymore
//    float d = dstScene(ro, false).x;
//    if(d < 0.) {
//        ro += rd * -d;
//        d = dstScene(ro, false).x;
//    }
//}

vec3 glassLightRay(in vec3 n, in vec3 r, in vec3 rd, in Material mat, in int s, inout bool refr) {
    float fres = pow(max(-dot(rd,n),0.),.3);
    if(frand() < fres) {
        refr = true;
        return normalize(refract(rd,n,1./1.52));
    } else {
        return lightRay(n, r, mat);
    }
}

vec3 sky(in vec3 rd) {
    float sunDot = dot(SUN_DIR,rd);
    float sunFac = smoothstep(sunDot,.98,1.);
    return mix(vec3(.8),vec3(20.),1.-pow(sunFac,5.));
}

vec3 shade(vec3 ro, vec3 rd) {
    vec3 col   = vec3(0.);
    vec3 mask  = vec3(1.);
    float dist = MAX_DIST;

    for(int i = 0; i < BOUNCES; i++) {
        vec2 hit = raymarch(ro, rd, 0., false);

        if(hit.x < MAX_DIST) {
            vec3 p = ro+rd*hit.x;
            vec3 n = normal(p, hit.x);
            vec3 r = normalize(reflect(rd, n));

            // get material data and direct lighting
            Material mat;
            material(mat, hit.y, p);

            // get next ray based on material
            bool refr = false;
            if(hit.y == 2.) {
                rd = glassLightRay(n, r, rd, mat, i, refr);
            }
            else rd = lightRay(n, r, mat);

            // calculate new mask
            mask *= mat.diffuse;
            if(hit.y != 2.) {
                mask *= max(dot(rd,n),0.);
            }

            // calculate new ray origin depending on if
            // the next ray is reflection or refraction
            if(!refr) {
                ro = p+n*.02;
            } else {
                ro = p+rd*.07;
            }

            if(i == 0) dist = hit.x;
        } else {
            col += mask * sky(rd);
            break;
        }
    }

    float fog = min(exp(dist-MAX_DIST),1.);
    col = mix(col, vec3(.8), fog);
    return col;
}

vec3 cameraPath() {
    if(iMouse.xy == vec2(0.)) {
        float a = 14.36 * .5;
        float d = 3.;
        return vec3(cos(a),1.5/d,sin(a))*d+vec3(0.,.4,0.);
    } else {
        vec2 m = iMouse.xy/iResolution.xy;
        float rx = 1.27079632679 * m.y;
        float ry = 6.28318530718 * m.x;
        float cx = cos(rx), sx = sin(rx);
        float cy = cos(ry), sy = sin(ry);
        return vec3(0.,.4,0.)+vec3(cy*cx,sx,sy*cx)*3.;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // init randoms
    ivec2 q = ivec2(fragCoord);
    srand( hash(q.x+hash(q.y+hash(iFrame))));

    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    vec3 ro = cameraPath();
    vec3 rd = vec3(uv, 1.);

    vec3  f = normalize(-ro);
    vec3  u = normalize(cross(f, vec3(0.,1.,0.)));
    vec3  v = normalize(cross(u,f));

    rd = mat3(u,v,f) * rd;

    vec4 accum = texture(iChannel1, fragCoord/iResolution.xy);
    if(iFrame == 0 || iMouse.z > 0.) accum = vec4(0.);

    fragColor.rgb = accum.rgb + shade(ro, normalize(rd));
    fragColor.a   = accum.a + 1.;
}

