// Drawing utilities
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

// Align SDF with an axis
vec2 axisAlign(in vec2 p, in vec2 up) {
    up = normalize(up);
    return p * mat2(up.y, -up.x, up);
}

// SDFs
float sdDisk(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

float sdBox(in vec2 p, in vec2 b) {
    p = abs(p) - b;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y));
}

float sdSegment(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * dot(pa, ba) / dot(ba, ba));
}

// vec3(x, y, radius)
vec2 perp(in vec2 v) { return vec2(-v.y, v.x); }
vec3 circumcircle(in vec2 a, in vec2 b, in vec2 c) {
    vec2 baT = perp(b - a), cb = c - b;
    vec2 pos = (a + b + baT * dot(c - a, cb) / dot(baT, cb)) / 2.0;
    return vec3(pos, length(a - pos));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Animated points
    vec2 a = vec2(sin(iTime * 0.75), cos(iTime));
    vec2 b = vec2(cos(iTime), sin(iTime * 1.25));
    vec2 c = vec2(cos(iTime * 1.25), cos(iTime * 0.5));

    // Midpoints
    vec2 abMid = (a + b) / 2.0;
    vec2 bcMid = (b + c) / 2.0;
    vec2 caMid = (c + a) / 2.0;

    // Target points for perpendicular bisectors
    vec2 abPerp = abMid + perp(b - abMid);
    vec2 bcPerp = bcMid + perp(c - bcMid);
    vec2 caPerp = caMid + perp(a - caMid);

    drawSDF(abs(sdBox(axisAlign(uv - abMid, abPerp - abMid) + 0.08, vec2(0.08))), vec3(1.0));
    drawSDF(abs(sdBox(axisAlign(uv - bcMid, bcPerp - bcMid) + 0.08, vec2(0.08))), vec3(1.0));
    drawSDF(abs(sdBox(axisAlign(uv - caMid, caPerp - caMid) + 0.08, vec2(0.08))), vec3(1.0));

    // Triangle edges
    drawSDF(sdSegment(uv, a, b), vec3(0.5, 0.4, 0.0));
    drawSDF(sdSegment(uv, b, c), vec3(0.5, 0.4, 0.0));
    drawSDF(sdSegment(uv, c, a), vec3(0.5, 0.4, 0.0));

    // Perpendicular bisectors
    drawSDF(sdLine(uv, abMid, abPerp), vec3(1.0, 0.8, 0.0));
    drawSDF(sdLine(uv, bcMid, bcPerp), vec3(1.0, 0.8, 0.0));
    drawSDF(sdLine(uv, caMid, caPerp), vec3(1.0, 0.8, 0.0));

    // Circumcircle center
    vec3 circ = circumcircle(a, b, c);
    drawSDF(sdDisk(uv, circ.xy, 0.05), vec3(1.0, 0.0, 0.0)); // Center
    drawSDF(abs(sdDisk(uv, circ.xy, circ.z)), vec3(0.0, 1.0, 0.0)); // Circle

    // Midpoints
    drawSDF(sdDisk(uv, abMid, 0.03), vec3(0.0, 0.0, 1.0));
    drawSDF(sdDisk(uv, bcMid, 0.03), vec3(0.0, 0.0, 1.0));
    drawSDF(sdDisk(uv, caMid, 0.03), vec3(0.0, 0.0, 1.0));

    // Points
    drawSDF(sdDisk(uv, a, 0.05), vec3(1.0));
    drawSDF(sdDisk(uv, b, 0.05), vec3(1.0));
    drawSDF(sdDisk(uv, c, 0.05), vec3(1.0));

    fragColor = vec4(color, 1.0);
}

// SerenityOS Ladyball, fragment shader by movAX13h, Feb. 2022
// thanks to iq for sdCappedTorus and polynomial smooth min
// thanks to IWBTShyGuy and Shane for support

#define REFLECTION
#define ANTIALIAS

// --
#define resolution iResolution
#define mouse iMouse

#define pih 1.5707963267949

float sdSphere(vec3 p, float r)
{
	return length(p)-r;
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
    p.x = abs(p.x);
    float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

vec2 rotate(vec2 p, float a)
{
	vec2 r;
	r.x = p.x*cos(a) - p.y*sin(a);
	r.y = p.x*sin(a) + p.y*cos(a);
	return r;
}

float smin(float a, float b, float k)
{
    float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
    return mix(b, a, h) - k*h*(1.0-h);
}

float time;
vec3 sunPos;
vec3 sun;
float focus;
float far;

struct Hit
{
	float d;
	float ref;
	float spec;
};


Hit scene(vec3 p)
{
	vec3 q = p;

	Hit h = Hit(1.0e6, 0.1, 0.2);
    p.zy = rotate(p.zy, 0.7); // tilt
    p.xy = rotate(p.xy, -3.0*iTime); // spin

	// yin-yang
	float a = max(sdCappedTorus(p, vec2(1.0, 0.0), 0.5, 0.4), sdSphere(p, 0.42));    
    float b = sdSphere(p, 0.4);
	h.d = smin(a, b, 0.01);

    // dots
    float c = sdSphere(p-vec3(0.4, 0.0, 0.0), 0.14);
    c = min(c, sdSphere(p-vec3(0.21, 0.32, 0.0), 0.1));
    c = min(c, sdSphere(p-vec3(-0.1, 0.4, 0.0), 0.06));
    c = max(c, sdSphere(p, 0.43));
    h.d = smin(h.d, c, 0.01);

	return h;
}

vec3 normal(vec3 p)
{
	float c = scene(p).d;
	vec2 h = vec2(0.01, 0.0);
	return normalize(vec3(scene(p + h.xyy).d - c, 
						  scene(p + h.yxy).d - c, 
		                  scene(p + h.yyx).d - c));
}


vec3 colorize(const vec3 ray, const float spec, const vec3 n, const vec3 dir, const in vec3 lightPos)
{
    // determine color based on radius
    float r = length(ray); // because we know object is at origin
    vec3 col = vec3(0.0);
    float red = smoothstep(0.4, 0.41, r)*smoothstep(0.43, 0.416, r);
    col = mix(col, vec3(0.871,0.047,0.008), red);

	float diffuse = 0.2*max(0.0, dot(n, lightPos));
	vec3 ref = normalize(reflect(dir, n));
	float specular = spec*pow(max(0.0, dot(ref, lightPos)), 3.5);
	return (col + diffuse * vec3(0.9) +	specular * vec3(1.0));
}

vec3 tex3D(vec3 dir)
{
    return texture(iChannel0, dir).gbr;
}

vec3 march(in vec2 fragCoord) 
{   
    vec2 pos = (fragCoord.xy*2.0 - resolution.xy) / resolution.y;

	float d = 6.0;
	float t = 1.2*sin(time*0.1+35.0) ;
	vec3 cp = vec3(d*cos(t), 0.8+sin(0.2*time), d*sin(t)); // circle center

	if (mouse.x > 10.0)
	{
		vec2 mrel = 3.0*(mouse.xy/resolution.xy-0.5);
		d = 8.0;
        focus = 9.0 + 4.0*mrel.y;
		cp = vec3(d*cos(-mrel.x*pih), 6.0, d*sin(-mrel.x*pih));
	}

    vec3 ct = vec3(0.0, 0.0, 0.0);
   	vec3 cd = normalize(ct-cp);
    vec3 cu  = vec3(0.0, 1.0, 0.0);
    vec3 cs = cross(cd, cu);
    vec3 dir = normalize(cs*pos.x + cu*pos.y + cd*focus);	

    Hit h;
	vec3 col = vec3(0.16);
	vec3 ray = cp;
	float dist = 0.0;

	// raymarch scene    
    for(int i=0; i < 120; i++) 
	{
        h = scene(ray);

		if(h.d < 0.0001) break;

		dist += h.d*0.8;
		ray += dir * h.d*0.8;

        if(dist > far) 
		{ 
			dist = far; 
			break; 
		}
    }

	float m = (1.0 - dist/far);
	vec3 n = normal(ray);

	col = colorize(ray, h.spec, n, dir, sun)*m;

	if (dist < far && h.d < 0.0001)
	{	
        #ifdef REFLECTION
		// MIRROR (from obj to reflected normal direction)		
		Hit h2;
		dir = reflect(dir, n);
		vec3 neb = tex3D(dir);

		vec3 ray2 = ray + dir*0.008;

		dist = 0.0;

		for(int i=0; i < 35; i++) 
		{
			h2 = scene(ray2 + dir*dist);
			dist += h2.d;
			if (h2.d < 0.001) break;
		}

		if (dist > far) col += h.ref*neb;
		else col += h.ref*colorize(ray2, h2.spec, normal(ray2+dir*dist), dir, sun)*(1.0 - dist/far);
		#endif
	}
	else col = tex3D(dir);

    return col;
}   

void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{    
    time = iTime + 7.0;
	sunPos = vec3(10.0, 15.0, -10.0);
	sun = normalize(sunPos);
	focus = 9.0+2.0*cos(0.35*time);
	far = 30.0;    

    // simple anti-aliasing
    #ifdef ANTIALIAS
    vec3 col = vec3(0.0);
    for(int i = 0; i < 2; ++i)
    {
        for(int j = 0; j < 2; ++j)
        {
            col += march(fragCoord+vec2(i,j)/2.0);
        }
    }
    col /= 4.0;
    #else
    vec3 col = march(fragCoord);
    #endif    

	col = clamp(col, vec3(0.0), vec3(1.0));
	col = pow(col, vec3(2.2, 2.4, 2.5)) * 3.7; 
	col = pow(col, vec3(1.0 / 2.2)); // gamma	

	fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Blank background
    vec3 col = vec3(0.0f);

    int box_count = 8;

    float sz = 5.0f * iResolution.x / 400.0f;
    float speed = 3.0f;

    for(int i = 0; i < box_count; i++)
    {
        float lum = mix(0.9, 0.1, float(i)/float(box_count-1));

        float y = (iResolution.y * 0.5) + (iResolution.y * 0.35) * sin(iTime * speed);
        float x = mix(iResolution.x * 0.25, iResolution.x * 0.75,  float(i)/float(box_count-1));

        if(abs(fragCoord.x - x) < sz && abs(fragCoord.y - y) < sz)
        {
            col = vec3(lum);
        }
    }    

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define pi 3.14

#ifdef HW_PERFORMANCE
    #if HW_PERFORMANCE==0
    #define AA 1.
    #else
    #define AA 2.   // make this 2 or 3.. for better antialiasing
    #endif
#else
#define AA 1.
#endif

const float MAXD = 100.;
const float MAXSHD = 3.;
const float MAXRD = 15.;
float t;
// 2d rotation
vec2 rot(vec2 p, float r) {
	float c = cos(r), s = sin(r);
	return vec2(p.x * c - p.y * s, p.x * s + p.y * c);
}
float H = .4; //size of the cross sphere raduis is cqlculated based on this
// rolling a vector u in a closed path (square of length h, in plan y=0)
// f is the animation parametre (iTime)
vec3 roll4(vec3 u, float h, float f)
{
    float s=.5;
    float c = .03; //this add a clamping effect i think it look cooler
    f = floor(f)+smoothstep(s,1.+c,fract(f));
    f*=pi;
    float b = f/pi/2.;
    float a = b + .5;
    a =(max(0., 2.*fract(a) - 1. ) + floor(a));  
    b = abs(  max(0., 2.*fract(b)-1.) + floor(b)- floor(b/2.+.25)*2.);
    u.x += (2.-h)*  (4.*abs(fract(a/2.)-.5)-1.);
    u.z += (2.-h)*  (4.*abs(fract(b/2.)-.5)-1.);
    u.zy = rot(u.zy,b*pi);
    u.xy = rot(u.xy,a*pi);
    return u;   
}
// rolling wheel (1/4 of a sphere)
float wheel(vec3 p,float r)
{
    float d = length(p) - r;
    return max(-p.z,max(-p.x,d));
}
// 1d-elongated cylindre
float cyl(in vec3 p, in float r,in float h)
{
    p.x -=clamp(p.x,-h,h);    
    vec2 d = abs(vec2(length(p.yx),p.z)) - vec2(r,h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
// 2d-elongated sphere 
float sph(in vec3 p, in float r,in float h)
{
    p.xz -=clamp(p.xz,-h,h);    
    return length(p)-r;
    vec2 d = abs(vec2(length(p.yx),p.z)) - vec2(r,h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float cros(in vec3 p, in float r,in float h)
{
    float d = min(cyl(p,r,h),cyl(p.zyx,r,h));
    /*
    for(int i=0;i<6;i++)
    {
        p.y -= r/2. + r/2.;// r*2.-.5+r/4.;
        r/=1.4;
        h-=r/sqrt(2.);
        d = min(d, sph(p,r,h));
    }
    */
    return d;
}
// surface texture
float text(vec3 u)
{
    return  0.; //remove this to apply surface textures
    u *= 6.28*10.;
    //u = abs(fract(u/10.)-.5)*4.-1.;
    u = sin(u/2.);
    return 0.015*( u.x*u.y*u.z )  ;
}
vec4 opu(vec4 a,vec4 b){ return (a.x<b.x) ? a : b;}
vec4 map(vec3 p)
{
    vec3 blue = normalize(vec3(.3,.4,.8));
    vec3 q=p;
    p.xz=mod(p.xz,4.)-2.;
    vec2 s = (q.xz-p.xz)/4.-.5;
	vec3 u = p;        

    float h = H;
    float r = (4.-h*2.)/3.14;
    u.y-=r;

    q = u;
    q.xz = abs(u.xz) - vec2(2.);
    float frame = cros(q,r,h);
    if(fract(s.x/2.+s.y/2.)>.25) frame += text(u);
    vec4 plan = vec4(p.y,blue-.1);
    vec4 wall = vec4(frame,blue);

    float c = t*.2 +s.y+2.*s.x; //; -  0.*s.y/2.;
    float c2 = c +1.+mod(s.x*s.y,2.); //; -  0.*s.y/2.;
    p = roll4(u,h,c);
    float w1 = wheel(p , r);
    w1 += text(p);
    p = roll4(u,h,c2);
    float w2 = wheel(p , r);
    //w2 += text(p);
    vec4 wheel1 = vec4(w1, blue); 
    vec4 wheel2 = vec4(w2, blue*0.65);
    wheel1 = opu(wheel1,wheel2);

    vec4 o = opu(plan,opu(wheel1,wall));
    o.x -= .04;//+0.01*(sin(p.x*40.)*sin(p.y*40.));
    o.yzw = vec3(1.,.8,.9); //color 

    return o;//vec4(d,1.,0.,0.);
}
//simple raymarch
vec4 rayMarch(vec3 rO, vec3 rD)
{   
    float d = 0.;
    vec4 D;
    for (int i = 0; i<130 &&  d < MAXD; i++)
    {
        D = map(rO + rD * d);
