// you can learn more about soft shadows in this article https://iquilezles.org/articles/rmshadows
float shadows(vec3 or, vec3 lD)
{
    const float k = 1.5;
    float res = 1.;
    float t = 0.;
    for (int i = 0; i < 40; i++)
    {
        float d = map(or + lD*t).x;
        res = min(res, .5 + d * k / t);
        if (res < .01|| t > MAXSHD) break;
        t += d;
    }
    res = max(res, 0.);
    return smoothstep(.3, 1., res);
}
vec4 render(in vec2 fragCoord )
{
    float fov = iResolution.x;
    vec2 uv = fragCoord - iResolution.xy / 2.;
    uv = uv - .5;
    uv /= fov;
    float ww=t*.025;
    vec3 or = vec3(cos(ww),.6+sin(ww*1.5345)*.2,sin(ww))*10.*(sin(ww*1.37)+3.);
    //or.y = 20.;
    //or.y-=17.5;
    vec3 w = normalize(vec3(0.,0.,0.)-or);
    vec3 u = normalize(cross(w, vec3(0., 1., 0.)));
    vec3 v = cross(u, w);
    vec3 rD = normalize(w*1.5+uv.x*u+uv.y*v);

    vec4 d = rayMarch(or, rD);
    vec3 col = vec3(0,0,0);

    if (d.x < MAXD)
    {
        vec3 p = or + d.x * rD;
    	vec3 nrm = normal(p);
        vec3 sun = -normalize(vec3(.4, 2., .8));

        col = vec3(dot(sun,nrm)) *d.yzw;
        //col *= d.yzw; //colloring
        col *= vec3(shadows(p - sun * 0.01, -sun));

    }
    else 
    {
        col = simpleSky(rD);
    }

    return vec4(col,1.0);
}
