vec2 turbulence (in vec2 _st) {

    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    float m1 = mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;

    float a1 = random(i+1.0);
    float b1 = random(i + vec2(1.0, 0.0));
    float c1 = random(i + vec2(0.0, 1.0));
    float d1 = random(i + vec2(1.0, 1.0));

    vec2 u1 = f * f * (3.0 - 2.0 * f);

    float m2 = mix(a1, b1, u1.x) +
            (c1 - a1)* u1.y * (1.0 - u1.x) +
            (d1 - b1) * u1.x * u1.y;
    return vec2(m1,m2);
}

vec2 fbmV ( in vec2 _st) {

    vec2 v = vec2(0.0);
    vec2 a = vec2(0.5);
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5),
                    -sin(0.5), cos(0.50));
    for (int i = 0; i < NUM_OCTAVES; ++i) {
        v += a * turbulence(_st);
        _st = rot * _st * 2.0 + shift;
        a *= vec2(0.5);
    }
    return v;
}

