precision highp float;

#define MAX_ITER 80
#define MAX_DIST 15.
#define MIN_DIST .001

#define SAMPLES 2
#define BOUNCES 3

const vec3 SUN_DIR = normalize(vec3(45.,30.,-45.));
const vec3 SUN_AMB = vec3(.15);

vec2 dstMin(vec2 a, float bt, float bm) {
    return a.x < bt ? a : vec2(bt,bm);
}

vec2 dstScene(vec3 p, bool noGlass) {
    vec2 d = vec2(p.y,0.);

    d = dstMin(d, sdBoxFrame(p-vec3(0.,.4,0.), vec3(1.,.4,.8), .05), 1.);
    d = dstMin(d, length(p-vec3(.4,1.06,0.))-.2, 3.);

    if(!noGlass) {
        d = dstMin(d, sdCappedCylinder(p-vec3(0.,.85,0.), 1.3, .02), 2.);
    }
    return d;
}

vec2 raymarch(vec3 ro, vec3 rd, float mint, bool noGlass) {
    float t = mint;
    float m = -1.;
    for(int i = 0; i < MAX_ITER; i++) {
        vec2 d = dstScene(ro+rd*t, noGlass);
        if(abs(d.x) < MIN_DIST || t > MAX_DIST) break;
        t += d.x;
        m  = d.y;
    }
    return vec2(t,m);
}

vec3 normal(vec3 p, float t) {
    vec2 e = vec2(.001*t,0.);
    vec3 n = vec3(
        dstScene(p+e.xyy,false).x-dstScene(p-e.xyy,false).x,
        dstScene(p+e.yxy,false).x-dstScene(p-e.yxy,false).x,
        dstScene(p+e.yyx,false).x-dstScene(p-e.yyx,false).x
    );
    return normalize(n);
}

void material(inout Material mat, in float id, in vec3 p) {
    if(id == 0.) { // ground
        mat.diffuse  = texture(iChannel0, p.xz / 2.).rgb;
        mat.specular = vec3(.1);
        mat.shine    = 4.;
        mat.reflective = 0.;
    } else if(id == 1.) { // metal frame
        mat.diffuse  = vec3(.5);
        mat.specular = vec3(1.);
        mat.shine    = 50.;
        mat.reflective = .9;
    } else if(id == 2.) { // glass top
        mat.diffuse  = vec3(.4,.4,.6);
        mat.specular = vec3(1.);
        mat.shine    = 80.;
        mat.reflective = 1.;
    } else if(id == 3.) { // ball
        mat.diffuse  = vec3(1., .2, .2);
        mat.specular = vec3(.4);
        mat.shine    = 30.;
        mat.reflective = .1;
    }
}

// source: https://iquilezles.org/articles/rmshadows
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k, bool noGlass )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = dstScene(ro + rd*t, noGlass).x;
        if( h<0.001 )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

vec3 lighting(vec3 p, vec3 n, vec3 r, Material mat, bool noGlass) {
    float ndotl = max(dot(n,SUN_DIR),0.);
    float rdotl = max(dot(r,SUN_DIR),0.);
    float shd   = softshadow(p, SUN_DIR, .01, MAX_DIST, 40., noGlass);
    return mat.diffuse * (ndotl * shd + SUN_AMB) + mat.specular * pow(rdotl,mat.shine) * shd;
}

vec3 shade(vec3 oro, vec3 ord) {
    vec3 fin = vec3(0.);

    for(int s = 0; s < SAMPLES; s++) {
        vec3 col   = vec3(0.);
        vec3 mask  = vec3(1.);
        float mint = 0.;
        float last = -1.;
        float dist = 0.;

        vec3 ro = oro;
        vec3 rd = ord;

        for(int b = 0; b < BOUNCES; b++) {
            vec2 hit = raymarch(ro, rd, mint, last == 2.);
            if(hit.x > 0. && hit.x < MAX_DIST) {
                vec3 p = ro + rd * hit.x;
                vec3 n = normal(p, hit.x);
                vec3 r = normalize(reflect(rd, n));

                Material mat;
                material(mat, hit.y, p);

                col += mask * lighting(p, n, r, mat, s > 0);

                float fres = pow(max(-dot(rd,n),0.),.4);

                // on first pass, glass uses refraction instead
                if(hit.y == 2. && s == 0) {
                    r = normalize(refract(rd, n, 1./1.52));
                    mask *= fres;
                } else {
                    mask *= mat.diffuse * mat.reflective * (1.-fres);
                }

                ro = p;
                rd = r;
                mint = .1;
                last = hit.y;                   // set last material id

                if(b == 0) dist = hit.x;        // set distance for fog
                if(mat.reflective < .01) break; // if material isn't reflective, dont bounce
            } else {
                col += mask * vec3(.8);
                break;
            }
        }

        float fog = min(exp(dist-MAX_DIST),1.);
        col = mix(col, vec3(.8), fog);

        fin += col / float(SAMPLES);
    }

    return fin;
}

vec3 cameraPath() {
    float a = iTime * .5;
    float d = 3.;
    return vec3(cos(a),1.5/d,sin(a))*d+vec3(0.,.4,0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    vec3 ro = cameraPath();
    vec3 rd = vec3(uv, 1.);

    vec3  f = normalize(-ro);
    vec3  u = normalize(cross(f, vec3(0.,1.,0.)));
    vec3  v = normalize(cross(u,f));

    rd = mat3(u,v,f) * rd;
    fragColor.rgb = shade(ro, normalize(rd));
    fragColor.a = 1.;
}

