//
// Memory Buffer
//
// The MIT License
// Copyright © 2022 IWBTShyGuy
//

/*
memory layout

row 0: heart status
Each pixel represents one heart status.
    vec2(original_position), clicked_time, move_parameter

- original_position: pixel coordinate, y-height == 256, x-center == 0
- clicked_time: if heart has not been clicked, this parameter is 0.0.
- move_parameter: the parameter of heart move in [0, 1]. Default is 0.0.

pixel (0, 1):
- channel 0: the number of hearts that shyguy gets
- channel 1: previous time that shyguy gets heart
- channel 2: time if heart velocity is took into account
- channel 3: previous frame iTime

pixel (1, 1):
- channel 0: if thumnail, this value is 1.0, else 0.0.
*/

ivec2 heartOriginalPosition(in int i) {
    vec2 r = iResolution.xy;
    float t = texelFetch(iChannel0, ivec2(0, 1), 0).z,
        screenWidth = r.x / r.y * 256.0,
        yFirst = float(i) * 16.0 + 8.0,
        id = floor((yFirst + t / 32.0) / 16.0);
    return ivec2(
        screenWidth * (noise11(id) - 0.5),
        mod(yFirst + t * 10.0, 320.0) - 32.0
    );
}

vec4 heartPositionBuffer(in int i) {
    ivec2 p = heartOriginalPosition(i),
    previousPosition = ivec2(texelFetch(iChannel0, ivec2(i, 0), 0).xy);
    int currentHeart = int(texelFetch(iChannel0, ivec2(0, 1), 0).x);
    float clickedTime = iFrame == 0 || p.y < previousPosition.y ?
        0.0 : texelFetch(iChannel0, ivec2(i, 0), 0).z;
    if ((iMouse.z > 0.0 || iMouse.w > 0.0) && clickedTime == 0.0 && currentHeart < 56) {
        vec2 r = iResolution.xy;
        int screenWidth = int(r.x / r.y * 256.0);
        ivec2 v = ivec2(iMouse.xy / r.y * 256.0) - ivec2(screenWidth / 2, 0) - p + 8;
        clickedTime = abs(v) == v && abs(16 - v) == 16 - v ? iTime : 0.0;
    }
    float t = clickedTime > 0.0 ? min((iTime - clickedTime) * 2.0, 1.0) : 0.0;
    return vec4(p, clickedTime, t);
}

int gotHeart() {
    int res = 0;
    for (int i = 0; i < 20; i++) {
        float previousParameter = texelFetch(iChannel0, ivec2(i, 0), 0).w;
        float currentParameter = heartPositionBuffer(i).w;
        res += previousParameter < 1.0 && currentParameter == 1.0 ? 1 : 0;
    }
    return res;
}

float heartVelocity(int currentHeart) {
    if (currentHeart < 8) return 1.0;
    else if (currentHeart < 24) return 2.0;
    else if (currentHeart < 56) return 4.0;
    else return 8.0;
}

vec4 currentStatusBuffer() {
    if (iFrame == 0) return vec4(0, -100, iTime, iTime);
    vec4 res = texelFetch(iChannel0, ivec2(0, 1), 0);
    int got = gotHeart();
    if (got != 0) {
        res.x += float(got);
        res.y = iTime;
    }
    res.z += (iTime - res.w) * heartVelocity(int(res.x));
    res.w = iTime;
    return res;
}

float isThumbnail() {
    if (iFrame == 0) return iTime < 9.0 ? 0.0 : 1.0;
    else return texelFetch(iChannel0, ivec2(1, 1), 0).x;
}

void mainImage(out vec4 O, in vec2 FC) {
    ivec2 U = ivec2(FC);
    if (U.y == 0) O = heartPositionBuffer(U.x);
    else if (U == ivec2(0, 1)) O = currentStatusBuffer();
    else if (U == ivec2(1, 1)) O.x = isThumbnail();
}

