// https://www.shadertoy.com/view/XlXcW4
vec3 hash(uvec3 x) {
    const uint k = 1103515245U;
    x.y = ((x.y>>8U)^x.y)*k;
    x.z = ((x.z>>8U)^x.z)*k;
    x.z = ((x.z>>8U)^x.z)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    return vec3(x)/float(0xffffffffU);
}

vec3 noise31(float x) {
    uint s = uint(int(floor(x)) + 2147483647);
    float t = fract(x);
    return mix(hash(uvec3(s)), hash(uvec3(s + 1u)), t);
}

float noise11(float x) {
    return noise31(x).x;
}

