const float bpm = 70.;
float T;
float seed;

// https://www.shadertoy.com/view/4djSRW
float hash11(float t) {
    float p = ((seed+=1.) + mod(t, 21.435214) * 1500. + 50.0);
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
float mixc(float a, float b, float r) { return mix(a, b, clamp(0., 1., r)); }

//float note2freq(float note) { return 440. * exp2(-10./12.) * exp2(note/12.); }
float note2freq(float note) { return 440. * exp2(-2./12.) * exp2(note/12.); }    // transposed to the current key

float sinhz(float x) { return sin(6.2831*x); }
float saw(float x) { return abs((fract(x-0.25)-0.5)*2.)*2.-1.; }
float sawt(float x) { return fract(x-0.5)*2.-1.; }

float distort(float x) { return sin(3.1415926/2.*x*2.); }

float kick(float t) { return distort(t*40.*sin(0.3/(t+0.0001))*exp(-t*30.)*0.35); }
float hh(float t, float decay) { return hash11(mod(t,1./4500.))*(exp(-t*decay) + t*200.*clamp(1.-t*100.,0.,1.)); }
float snare2(float t) { return t*3. * hash11(floor(t*8000.)/8000.)*exp(-t*10.); }
float snare(float t) { return snare2(t) + hash11(t)*sawt(t*40.)*smoothstep(0.08,0.,t); }

float padsynth(float t, float note) {
    float freq = note2freq(note);
    float h1 = saw(freq*t) + saw(freq*t*2.)/2. + saw(freq*t*3.)/6. + saw(freq*t*4.)/5. + sawt(freq*t*8.)/10.;
    float h2 = sinhz(freq*t) + sinhz(freq*t*2.)/2.;
    return mixc(h1, h2, 1.-exp(-t*2.));
}

float basssynth(float t, float note, float soft) {
    float freq = note2freq(note-24.);
    float p = freq*t;   //    p = p/smoothstep(0.,0.1,p);
    float h1 = sawt(p) * (1. + sin(0.1/(t+0.00001))*0.3), h2 = sinhz(p), h3 = sinhz(p/2.);
    return (mixc(h1, h2, 1.-exp(-t*1.2) + soft) + h2/3. + h3)*exp(-t*0.2);
}
float basssynth(float t, float note) { return basssynth(t, note, 0.); }

float t(float offset) { return T - 60./bpm*4. * offset; }
float m(float repeat, float offset) { return mod(T - 60./bpm*4. * offset, 60./bpm*4. * repeat); }
float m(float repeat) { return m(repeat, 0.); }
#define T(a,b)   (float(a)/float(b))
#define T1(a)    (1./float(a))
#define M(a,b)   m(float(a)/float(b))
#define M1(a)    m(1./float(a))
#define MM(a,b,c,d)  m(float(a)/float(b), float(c)/float(d))

float s(float offset, float len) { return step(60./bpm*4. * offset, T) * (1. - step(60./bpm*4. * (offset+len), T)); }
float s(float offset) { return step(60./bpm*4. * offset, T); }
float ss(float offset, float len, float decay) {
    return step(60./bpm*4. * offset, T)
        * (1. - smoothstep(60./bpm*4. * (offset+len), 60./bpm*4. * (offset+len+decay), T))
    ;
}
float ss(float offset, float len) { return ss(offset, len, 0.5); }
float st(float offset1, float offset2) { return step(60./bpm*4. * offset1, T) * (1. - step(60./bpm*4. * offset2, T)); }

vec2 pan(float smpl, float a) { return vec2(smpl * cos(a), smpl * (1.-cos(a)))*1.5; }

#define DETUNE(FUNC, TIME, NOTE)  (FUNC(TIME, NOTE) + FUNC(TIME, NOTE+0.2) + FUNC(TIME, NOTE-0.1))/6.
#define DETUNE2(FUNC, TIME, NOTE)  (FUNC(TIME, NOTE) + FUNC(TIME, NOTE-0.2) + FUNC(TIME, NOTE+0.1))/6.
#define SYNTH(TIME, NOTE)               vec2(DETUNE(padsynth, TIME, NOTE)*0.7, DETUNE2(padsynth, TIME, NOTE)*0.7)
#define VIBRATO(FREQ, DEPTH)           (1. + sinhz(T*FREQ)*DEPTH)
#define TREMOLO(FREQ, DEPTH)           sinhz(T*FREQ)*DEPTH
#define V                               VIBRATO(5.5, 0.3)
#define ORGAN(TIME, NOTE)               distort(DETUNE(padsynth, TIME, NOTE)+DETUNE(padsynth, TIME, NOTE+12.)*0.4)*0.2

#define BASSSYNTH(TIME, NOTE)       vec2(DETUNE(basssynth, TIME, NOTE), DETUNE2(basssynth, TIME, NOTE))

vec2 drumpat() {
    return vec2(0.
        + kick(m(1.))
        + kick(m(1., 1./2.+1./16.))*0.3
        + kick(m(1., 1./2.+1./8.))
        + hh(M(1,8), 50.)*0.2
        + hh(MM(1,8,1,16), 10.)*0.08
        + snare(m(T1(2), T1(4)))
    );
}

vec2 pat1(float time) {
    T = time;
    T = m(4.);
    return vec2(0)
        + drumpat()

        + BASSSYNTH(m(4.), 0.) * s(0., 1.)
        + BASSSYNTH(m(4., 1.), -4.) * s(1., 1.)
        + BASSSYNTH(m(4., 2.), -2.) * s(2., 1.)
        + BASSSYNTH(m(4., 3.), -7.) * s(3., 1./2.)
        + BASSSYNTH(m(4., 3.+2./4.), -5.) * s(3.+2./4., 1./4.)
        + BASSSYNTH(m(4., 3.+3./4.), -2.) * s(3.+3./4., 1./4.)

        + SYNTH(t(0.)+2., -12. + 7.) * ss(0., 1./8.)*0.3
        + SYNTH(t(1./8.), 3.) * ss(3./8., 1./8.)
        + SYNTH(t(3./8.), 2.) * ss(4./8., 1./8.)
        + SYNTH(t(4./8.), 3.) * ss(5./8. + 1./16., 1./8., 1.)
        + SYNTH(t(7./8.), 0.) * ss(7./8., 1./8., 1.5)

        + SYNTH(t(2.+1./8.), 3.) * ss(2.+3./8., 1./8.)
        + SYNTH(t(2.+3./8.), 2.) * ss(2.+4./8., 1./8.)
        + SYNTH(t(2.+5./8.), 3.) * ss(2.+5./8. + 1./16., 1./8.)
        + SYNTH(t(2.+7./8.), 0.) * ss(2.+7./8., 1./8.)

        + SYNTH(t(3.), 3.) * ss(3.+1./8.+1./16., 1./8.)
        + SYNTH(t(3.+3./8.), 2.) * ss(3.+3./8., 1./8., 0.75)
        ;
}

vec2 pat2(float time) {
    T = time;
    T = m(4.);
    float[] ts1 = float[] (
        0.,
        1. + 1./4. + 1./8.,
        1. + 1./4. + 1./8. + 1./16.,
        1. + 1./2.,

        2.,
        3. - 1./8.,
        3.,
        3. + 3./16.,
        3. + 6./16.,
        3. + 10./16.,
        3. + 13./16.
    );
    return vec2(0)
        + pat1(time)

        + pan(
              (ORGAN(time+0.3, 12. + 7.)
            + ORGAN(time+0.3, 12. + 3.))/2. * V * st(ts1[0], ts1[1])
            + (ORGAN(time, 12. + 5.)
            + ORGAN(time, 12. + 2.))/2. * st(ts1[1], ts1[2])
            + (ORGAN(time, 12. + 7.)
            + ORGAN(time, 12. + 3.))/2. * st(ts1[2], ts1[3])
            + (ORGAN(time, 12. + 3.)
            + ORGAN(time, 12. + 0.))/2. * V * ss(ts1[3], ts1[4]-ts1[3]-1./8., 1./6.)

            + (ORGAN(t(2.)+0.3, 12. + 5.)
            + ORGAN(t(2.)+0.3, 12. + 2.))/2. * V * st(ts1[4], ts1[5])
            + ORGAN(t(2.)+2., 12. + 3.)    * V * st(ts1[5], ts1[6])
            + ORGAN(t(2.)+2., 12. + 2.)    * V * st(ts1[6], ts1[7])
            + ORGAN(t(2.)+2., 12. + 0.)    * V * st(ts1[7], ts1[8])
            + ORGAN(t(2.)+2.,       7.)    * V * st(ts1[8], ts1[9])
            + ORGAN(t(2.)+2., 12. + 2.)    * V * ss(ts1[9], 1./4., 1./8.)
            , m(2.)*0.4)
        ;
}

vec2 mainSound( int samp, float time )
{
    T = time;
    seed = 10.;

    float p1 = m(16.);
    float p2 = m(16., 8.);
    return p1 < p2 ? pat1(p1) : pat2(p2);
}

