const float bpm = 126.;
float T;
float seed;

// https://www.shadertoy.com/view/4djSRW
float hash11(float t) {
    float p = ((seed+=1.) + mod(t, 21.44352345) * 1500. + 50.0);
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
float nf(float t, float freq) { return hash11(floor(t*freq)/freq); }
float mixc(float a, float b, float r) { return mix(a, b, clamp(0., 1., r)); }

//float note2freq(float note) { return 440. * exp2(-10./12.) * exp2(note/12.); }
float note2freq(float note) { return 440. * exp2(-2./12.) * exp2(note/12.); }    // transposed to the current key

float sinhz(float x) { return sin(6.2831*x); }
float saw(float x) { return abs((fract(x-0.25)-0.5)*2.)*2.-1.; }
float sawt(float x) { return fract(x-0.5)*2.-1.; }

float kick(float t) { return t*40.*sin(0.8/(t+0.0001))*exp(-t*30.)*0.9; }
float hh(float t) {
    return hash11(mod(t,1./4500.))*t*200.*clamp(1.-t*100.,0.,1.);
}
float snare(float t) { return hash11(t)*sawt(t*40.)*smoothstep(0.08,0.,t); }
float snare2(float t) { return hash11(t)*smoothstep(0.18,0.,t)*0.4; }

float padsynth(float t, float note) {
    float freq = note2freq(note);
    float h1 = saw(freq*t) + saw(freq*t*2.)/2. + saw(freq*t*3.)/6. + saw(freq*t*4.)/5. + sawt(freq*t*8.)/10.;
    float h2 = sinhz(freq*t) + sinhz(freq*t*2.)/2.;
    return mixc(h1, h2, 1.-exp(-t*2.));
}

float basssynth(float t, float note, float soft) {
    float freq = note2freq(note-24.);
    float p = freq*t;   //    p = p/smoothstep(0.,0.1,p);
    float h1 = sawt(p), h2 = sinhz(p);
    return (mixc(h1, h2, t*15. + soft)+h2/3.)*exp(-t*12.);
}
float basssynth(float t, float note) { return basssynth(t, note, 0.); }

float m(float repeat, float offset) { return mod(T - 60./bpm*4. * offset, 60./bpm*4. * repeat); }
float m(float repeat) { return m(repeat, 0.); }
#define T(a,b)   (float(a)/float(b))
#define T1(a)    (1./float(a))
#define M(a,b)   m(float(a)/float(b))
#define M1(a)    m(1./float(a))
#define MM(a,b,c,d)  m(float(a)/float(b), float(c)/float(d))

float s(float offset, float len) { return step(60./bpm*4. * offset, T) * (1. - step(60./bpm*4. * (offset+len), T)); }
float s(float offset) { return step(60./bpm*4. * offset, T); }

#define DETUNE(FUNC, TIME, NOTE)  (FUNC(TIME, NOTE) + FUNC(TIME, NOTE+0.2) + FUNC(TIME, NOTE-0.1))/3.
#define SYNTH(TIME, NOTE)               DETUNE(padsynth, TIME, NOTE)
#define CHORD(TIME, N, N2, N3)         (SYNTH(TIME, float(N)) + SYNTH(TIME, float(N+N2)) + SYNTH(TIME, float(N+N3)))*0.6

float drumpat() {
    return 0.
        + kick(M1(4))
        + hh(M1(16))*0.2
        + snare(m(T1(2), T1(4)))
    ;
}

float pat1(float time) {
    T = time;
    T = m(8.);
    return 0.
        + drumpat()
        + CHORD(m(1.),         0, 4, 7)*exp(-2.0*m(1.))
        + CHORD(m(2., T(3,4)), 0, 4, 9)*exp(-3.5*m(2., T(3,4)))
        + basssynth(M1(4), -12.)
        + basssynth(m(T1(4), T1(8)), 0.)
        + basssynth(m(1., -T1(16)), 0., 0.6)
        + snare(m(4., -T(4,8)))
        + snare(m(4., -T(3,8)))
        + snare(m(4., -T(2,8))) + snare2(m(4., -T(2,8)))
        + snare(m(4., -T(1,8)))
        + snare(m(4., -T(1,16)))
        ;
}

float pat2(float time) {
    T = time;
    T = m(8.);
    return 0.
        + drumpat()
        + snare2(m(T1(2), T(1,4)))
        + SYNTH(m(4.), 4.-12.) * exp(-0.5*m(1.))/2.
        + SYNTH(m(4., 1.), -1.)* exp(-0.3*m(4., 1.))/2.  * s(1.)
        + SYNTH(m(4., 2.), 2.) * exp(-0.2*m(4., 2.))/2. * s(2.)
        + SYNTH(m(4., 3.), 4.) * exp(-0.1*m(4., 3.))/2.  * s(3.)
        + basssynth(M1(4), -12. + 4.)
        + basssynth(m(T1(1), T(3,16)), 0. + 4.)
        + basssynth(m(T1(1), T(6,16)), 0. + 4.)
        + basssynth(m(T1(1), T(8,16)), 0. + 4., 0.6)/2.
        + basssynth(m(2., -T1(8)), 12. + 2., 0.9)
        + basssynth(m(2., -T1(16)), 0. + 2., 0.6)
        + snare(m(4., -T(4,8)))
        + snare(m(4., -T(3,8)))
        + snare(m(4., -T(7,16)))
        + snare(m(4., -T(2,8)))
        + snare(m(4., -T(1,8)))
        + snare(m(4., -T(1,16)))
        ;
}

vec2 mainSound( int samp, float time )
{
    T = time;
    seed = 10.;

    float p1 = m(12.);
    float p2 = m(12., 8.);
    return vec2(
        p1 < p2 ? pat1(p1) : pat2(p2)
    );
}

