//=================================================
// Hash without sine, from Dave Hoskins
// https://www.shadertoy.com/view/4djSRW
//=================================================
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

///  2 out, 2 in...
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

//=================================================
// Rand / RandColor
// From https://www.shadertoy.com/view/Mdy3DK
//=================================================
float Rand(vec2 co)
{
    co.x += fract(iDate.w * 5.342);
    co.y += fract(iDate.w * 11.214);
    return hash12(co);
}

// Modified to return vec4
vec4 RandColor (vec2 co)
{
    return vec4
    (
        Rand(co),
        Rand(co*2.143),
        Rand(co*3.163),
        0.
	);
}

