#define NOISE 0.999

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / R;

    vec4 me = texelFetch(iChannel0, ivec2(fragCoord), 0);
    float angle = me.z;


    // Neighbours
    vec4 n = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0., -1.), 0);
    vec4 e = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(-1., 0.), 0);
    vec4 s = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(0., 1.), 0);
    vec4 w = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(1., 0.), 0);

    vec4 ne = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(-1., -1.), 0);
    vec4 nw = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(1., -1.), 0);
    vec4 se = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(-1., 1.), 0);
    vec4 sw = texelFetch(iChannel0, ivec2(fragCoord) + ivec2(1., 1.), 0);


    vec4 m = (n + e + s + w) / 4. + ((ne + nw + se + sw) / 4. * 0.3);

    fragColor = me;

    vec2  p_loc = (uv - 0.5);
    float p = smoothstep(0.05, 0., length(p_loc) - 0.2);
    fragColor += vec4(p, 0. , 0., 0.);

    // Figure out if we have a moving pixel as neighbour
    vec4 next = vec4(0.);
    if (ne.w > 0.) next = ne;
    if (nw.w > 0.) next = nw;
    if (se.w > 0.) next = se;
    if (sw.w > 0.) next = sw;
    if (n.w > 0.) next = n;
    if (e.w > 0.) next = e;
    if (s.w > 0.) next = s;
    if (w.w > 0.) next = w;    

    if (me.w == 1.0){
        // We are a seed
        float angle = hash12(uv * 100.);
        vec4 col = vec4(fragCoord.x + 1. , fragCoord.y,angle, 1.0);
        fragColor = col;

    } else if (next.xy == fragCoord.xy){
        // We are the neighbour of a moving pixel. Compute next and store here.
        float seed = -hash12(fragCoord * iTime);

        next.x += round(cos(seed));
        next.y += round(sin(seed));
        next.w += 0.1; 

        fragColor = next;


    } else {
        if (fragCoord.x > iMouse.x) {

            fragColor = vec4(1.5 - uv.y + sin(iTime/3.14), 
                             -uv.y * 0.2 + sin(iTime/3.14) * 0.3, 
                             uv.y * 0.2 + cos(iTime/3.14) * 0.2, 0.0);
        }

    }

//=================================================
// Init. Create randomly placed dots from noise
//=================================================
    if (iFrame <= 1){
        float t = texture(iChannel1, uv).x;
        float v = Rand(uv * t);
        if (v > NOISE){
            float angle = hash12(uv);
            vec4 col = vec4(fragCoord.x + 0. , fragCoord.y,angle, 1.0);
            fragColor = col;
        } else {
            fragColor = COL(BLACK);
        }
    }
}

