// Examine the gear mechanism

#define AA  0   // (= 0/1) optional antialiasing

#if 0
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrBoxDf (vec3 p, vec3 b);
float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrCaps2Df (vec2 p, float r, float h);
float Minv2 (vec2 p);
float Maxv2 (vec2 p);
float SmoothMax (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir, qHit;
float tCur, dstFar, bLen, hGap, cRad, zMid, zLen, armAng, gState, bPos, bDir, bId;
int idObj;
const int idBase = 1, idSup = 2, idArm = 3, idRing = 4, idAx = 5, idGate = 6, idGr1 = 7, idGr2 = 8,
   idChan = 9, idRail = 10, idBoat = 11, idBoatH = 12, idTun = 13, idGrnd = 14, idWat = 15;
const float pi = 3.1415927;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec4 pArm, pGate;
  vec3 q, qq, sp;
  vec2 db;
  float dMin, d, dd, r, h, nt1, nt2, bGap, cCut;
  dMin = dstFar;
  pArm = vec4 (2.7, 1.2, 4.7, 5.19);
  pGate = vec4 (0.85, 0.2, 0.05, 0.01);
  bGap = bLen + 0.45;
  cCut = bGap - 0.2;
  nt1 = 60.;
  nt2 = 20.;
  p.y -= hGap + 0.15;
  qq = p;
  qq.xy = Rot2D (qq.xy, armAng);
  sp = sign (vec3 (qq.x, p.yz));
  q = p;
  q.y -= - hGap - 1.05;
  db = vec2 (hGap + 0.7, bLen + 0.6);
  d = max (PrBoxDf (q, vec3 (db, 0.9).xzy), - PrCylDf (q - vec3 (0., hGap + 1., 0.),
     db.x + 0.9, db.y - 0.3));
  DMINQ (idBase);
  q = qq;
  d = max (PrBox2Df (q.xy, pArm.xy), pArm.z - length (vec2 (q.x, abs (q.y) - pArm.w)));
  dd = cRad - length (vec2 (abs (q.x) - hGap, q.y));
  d = SmoothMax (max (min (min (max (d, dd), abs (dd + 0.15) - 0.15),
     max (max (max (length (vec2 (abs (q.x), sp.x * q.y)) - hGap - 1.4, sp.x * q.y - 3.),
     hGap + 0.7 - abs (q.x)), sp.x * q.y)), dd), abs (abs (q.z) - bLen) - 0.1, 0.02);
  DMINQ (idArm);
  q = p;
  q.xy = q.yx;
  d = max (PrBox2Df (q.xy, pArm.xy), pArm.z - length (vec2 (q.x, abs (q.y) - pArm.w)));
  dd = cRad - length (vec2 (abs (q.x) - hGap, q.y));
  d = SmoothMax (max (min (max (d, dd), abs (dd + 0.15) - 0.15), - hGap - 0.25 - p.y),
     max (- bGap - 0.2 - q.z, abs (mod (q.z, 2. * bGap) - bGap) - 0.15), 0.02);
  d = max (d, q.z - zMid - zLen);
  DMINQ (idSup);
  q = qq;
  d = PrCylDf (q, 0.3, bLen + 0.7);
  DMINQ (idAx);
  q.x = abs (q.x) - hGap;
  q.xy = Rot2D (q.xy, - armAng * sp.x);
  d = min (max (abs (length (q.xy) - cRad + 0.16) - 0.1, abs (q.y + 0.3) - 0.35),
     max (abs (q.y + 0.5) - 0.15, abs (q.x) - 0.85));
  d = max (d, abs (q.z) - cCut);
  DMINQ (idChan);
  d = max (max (abs (q.y + 0.18) - 0.1, abs (q.x) - cRad + 0.2), abs (q.z) - cCut);
  DMINQ (idWat);
  if (gState > 0. || gState == 0. && sp.y * sp.z < 0.) {
    q.z = abs (q.z);
    q.y += 0.2;
    d = PrRoundBoxDf (q - vec3 (0., 0., cCut - 0.05), pGate.xyz - pGate.w, pGate.w);
    DMINQ (idGate);
    q.y -= 0.2;
  }
  q.x = abs (q.x) - 0.93;
  d = min (PrCylDf (vec3 (q.xz, abs (q.y - 0.22) - 0.06).xzy, 0.02, bLen - 0.07),
     max (abs (q.z) - bLen + 0.07, PrCylDf (vec3 (q.x, q.y - 0.14, mod (q.z + 0.2, 0.4) -
     0.2).xzy, 0.02, 0.14)));
  DMINQ (idRail);
  q = p;
  q.y -= hGap * sp.z;
  dd = max (cCut - abs (q.z), abs (q.z - zMid) - zLen);
  d = max (min (max (abs (length (q.xy) - cRad + 0.16) - 0.1, abs (q.y + 0.3) - 0.35),
     max (abs (q.y + 0.5) - 0.15, abs (q.x) - 0.85)), dd);
  DMINQ (idChan);
  d = max (max (abs (q.y + 0.18) - 0.1, abs (q.x) - cRad + 0.2), dd);
  DMINQ (idWat);
  if (gState > 0.) {
    q = p - vec3 (0., hGap * sp.z - 0.2, (cCut + 0.05) * sp.z);
    d = PrRoundBoxDf (q, pGate.xyz - pGate.w, pGate.w);
    DMINQ (idGate);
  }
  q = qq;
  q.x = abs (q.x) - hGap;
  q.xy = Rot2D (q.xy, - armAng * sp.x);
  d = max (abs (length (q.xy) - cRad + 0.05) - 0.05, abs (abs (q.z) - bLen - 0.03) - 0.12);
  DMINQ (idRing);
  q.z -= bLen + 0.18;
  d = max (abs (length (q.xy) - cRad - 0.02) - 0.09, abs (q.z) - 0.02);
  q.xy = Rot2D (q.xy, 2. * pi * (floor (nt1 * atan (q.y, - q.x) / (2. * pi) + 0.5) / nt1));
  d = max (d, - PrCaps2Df (vec2 (q.y, q.x + 1.2), 0.035, 0.01));
  DMINQ (idGr1);
  q = p;
  q.z -= bLen + 0.18;
  d = max (abs (length (q.xy) - cRad - 0.02) - 0.09, abs (q.z) - 0.02);
  d = min (d, PrCylDf (vec3 (q.x, abs (q.y) - 1.05, q.z - 0.05), 0.07, 0.05));
  q.xy = Rot2D (q.xy, 2. * pi * (floor (nt1 * atan (q.y, - q.x) / (2. * pi) + 0.5) / nt1));
  d = max (d, - PrCaps2Df (vec2 (q.y, q.x + 1.2), 0.035, 0.01));
  DMINQ (idGr1);
  q = qq;
  q.z -= bLen + 0.1;
  q.x = abs (q.x) - 0.5 * hGap;
  d = PrCylDf (q, 0.2, 0.06);
  DMINQ (idArm);
  d = PrCylDf (q, 0.45, 0.03);
  q.xy = Rot2D (q.xy, (nt1 / nt2) * armAng * sp.x);
  q.xy = Rot2D (q.xy, 2. * pi * (floor (nt2 * atan (q.y, - q.x) / (2. * pi) + 0.5) / nt2));
  d = min (d, PrCylDf (q - vec3 (-0.4, 0., 0.05), 0.03, 0.06));
  DMINQ (idGr2);
  if (gState > 0.) {
    q = qq;
    bId = - sp.x;
    q.x -= hGap * sp.x;
    q.xy = Rot2D (q.xy, - armAng);
  } else {
    q = p;
    bId = bDir * sp.y;
    q.yz -= vec2 (hGap, bPos) * sp.y;
  }
  q.z *= bId;
  d = max (PrCapsDf (q, 0.4, 1.), q.y);
  DMINQ (idBoatH);
  d = min (PrCapsDf (q - vec3 (0., 0., -0.1), 0.3, 0.75),
     PrCylDf ((q - vec3 (0., 0.3, 0.6)).xzy, 0.1, 0.2));
  DMINQ (idBoat);
  q = p;
  q.y += hGap + 0.15;
  h = 0.2 * (Fbm2 (0.5 * q.xz) - 0.5) * smoothstep (0.9, 2., abs (q.x));
  d = max (abs (PrRoundBoxDf (q - vec3 (0., 29.7 - h, zMid), vec3 (15., 10. + h, zLen - 15.),
     20.)) - 0.2, dot (q.yz, sin (0.02 * pi + pi * vec2 (0.5, 1.))) - 7. + 2. * h);
  d = max (d, - PrBox2Df (q.xz, db));
  q.y -= hGap * (1. + sp.z) + 0.15;
  q.z = abs (q.z - zMid) - zLen - 5.;
  dd = max (q.z, zMid - ((sp.z > 0.) ? -3. : 12.) - q.z);
  r = length (q.xy) - cRad;
  if (dd < 0.) d = max (d, 0.2 - r);
  DMINQ (idGrnd);
  d = min (max (abs (r - 0.1) - 0.1, dd), max (r, abs (q.z) - 0.1));
  DMINQ (idTun);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.02 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 rd)
{
  vec3 col;
  vec2 u;
  float el, f;
  rd.xz = Rot2D (rd.xz, 0.002 * tCur);
  el = asin (rd.y);
  u = vec2 (atan (rd.z, - rd.x) + pi, tan (2. * atan (0.5 * el))) / (2. * pi);
  f = 32.;
  col = mix (vec3 (0.1, 0.2, 0.4), vec3 (0.8), mix (Fbm2 (f * u),
     Fbm2 (f * (u - vec2 (1., 0.))), u.x));
  col = mix (col, vec3 (0.3, 0.4, 0.5), smoothstep (0.95, 0.98, abs (el) / (0.5 * pi)));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, rdo, vn, vnw;
  vec2 vf;
  float dstObj, tc, tg, spd, a, sh, nDotL;
  bool isWat;
  bLen = 2.5;
  hGap = 3.2;
  cRad = 1.1;
  zMid = -10.;
  zLen = 35.;
  spd = 0.03;
  tc = mod (spd * tCur, 1.);
  bDir = - sign (tc - 0.5);
  tg = 0.3;
  gState = (0.5 - abs (tc - 0.5) > tg) ? 1. : 0.;
  if (gState == 0.) {
    bPos = 35. * (1. - smoothstep (0.1, 0.9, (0.5 - abs (tc - 0.5)) / tg));
    armAng = 0.5 * pi;
  } else {
    bPos = 0.;
    armAng = pi * (0.5 + smoothstep (0.2, 0.8, (tc - tg) / (1. - 2. * tg)) *
       sign (2. * floor (mod (spd * tCur, 2.)) - 1.));
  }
  isWat = false;
  rdo = rd;
  for (int k = VAR_ZERO; k < 2; k ++) {
    dstObj = ObjRay (ro, rd);
    if (k == 0 && dstObj < dstFar && idObj == idWat) {
      ro += dstObj * rd;
      vnw = VaryNf (vec3 (4., 4., 2.) * qHit + 0.5 * vec3 (0., 0., tCur), vec3 (0., 1., 0.), 0.2);
      rd = reflect (rd, vnw);
      ro += 0.01 * rd;
      isWat = true;
    } else break;
  }
  if (dstObj < dstFar) {
    vf = vec2 (0.);
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == idRing || idObj == idAx) a = atan (qHit.y, - qHit.x);
    if (idObj <= idGr2) {
      if (idObj == idBase) {
        col4 = vec4 (0.7, 0.6, 0.5, 0.) * (0.8 + 0.2 * Fbm2 (32. *
           (((abs (vn.z) < 0.99) ? qHit.xz : qHit.xy)))) * (0.9 + 0.1 *
           smoothstep (0., 0.01, abs (fract (qHit.z) - 0.5) - 0.02));
        vf = vec2 (64., 1.);
      } else if (idObj == idSup) {
        col4 = vec4 (0.8, 0.8, 0.8, 0.2) * (0.8 + 0.2 * smoothstep (0., 0.01,
           abs (abs (abs (qHit.x) - 1.6) - 0.1) - 0.01));
        vf = vec2 (64., 0.3);
      } else if (idObj == idArm) {
        col4 = vec4 (0.95, 0.95, 0.6, 0.2);
        if (abs (qHit.x) < hGap) col4 *= (0.8 + 0.2 * smoothstep (0., 0.01,
           abs (abs (qHit.y) - 0.3) - 0.01));
        vf = vec2 (64., 0.2);
      } else if (idObj == idRing) {
        col4 = vec4 (0.9, 0.9, 1., 0.2) * (0.8 + 0.2 * smoothstep (0., 0.01,
           abs (fract (60. * a / (2. * pi)) - 0.5) - 0.05));
      } else if (idObj == idAx) {
        col4 = vec4 (0.8, 0.8, 0.8, 0.2) * (0.9 + 0.1 * step (0., cos (8. * a)));
      } else if (idObj == idGate) {
        col4 = vec4 (0.4, 0.5, 0.2, 0.1) * (0.8 + 0.2 * step (0., PrBox2Df (qHit.xy,
           vec2 (0.7, 0.1))));
      } else if (idObj == idGr1) {
        col4 = vec4 (0.8, 0.6, 0.2, 0.2);
      } else if (idObj == idGr2) {
        col4 = vec4 (0.9, 0.7, 0.2, 0.2);
      }
    } else {
      if (idObj == idChan) {
        col4 = vec4 (0.8, 0.9, 1., 0.1) * (0.8 + 0.2 * smoothstep (0., 0.01,
           abs (abs (qHit.x) - 0.8) - 0.01));
        vf = vec2 (64., 0.2);
      } else if (idObj == idRail) {
        col4 = vec4 (0.9, 0.9, 1., 0.2);
      } else if (idObj == idBoatH) {
        col4 = vec4 (0.5, 0.3, 0.1, 0.);
      } else if (idObj == idBoat) {
        if (abs (qHit.y - 0.15) < 0.03 || qHit.y > 0.4) col4 = vec4 (1., 0., 0., 0.2);
        else col4 = (bId > 0.) ? vec4 (0.3, 0.8, 0.8, 0.2) : vec4 (0.8, 0.8, 0.3, 0.2);
      } else if (idObj == idTun) {
        col4 = vec4 (0.7, 0.6, 0.5, 0.) * (0.8 + 0.2 * smoothstep (0.,0.01,
           abs (fract (qHit.z) - 0.5) - 0.01));
        vf = vec2 (64., 1.);
      } else if (idObj == idGrnd) {
        col4 = mix (vec4 (0., 0.5, 0., 0.), vec4 (0.1, 0.45, 0.1, 0.), smoothstep (0.4, 0.6,
           Fbm2 (0.5 * ro.xz)));
        vf = vec2 (4., 1.);
      }
    }
    if (vf.y > 0.) vn = VaryNf (vf.x * qHit, vn, vf.y);
    nDotL = max (dot (vn, sunDir), 0.);
    if (idObj == idArm || idObj == idGr1 || idObj == idGr2) nDotL *= nDotL;
    sh = ObjSShadow (ro + 0.01 * vn, sunDir);
    col = col4.rgb * (0.2 + 0.1 * max (- dot (vn, normalize (vec3 (sunDir.xz, 0.)).xzy), 0.) +
       0.8 * sh * nDotL) + col4.a * step (0.95, sh) * pow (max (dot (sunDir,
       reflect (rd, vn)), 0.), 32.);
  } else {
    col = SkyCol (rd);
  }
  if (length (vec2 (ro.x, ro.y - 0.05 - hGap * (1. + sign (ro.z)))) < 1.1 * cRad)
     col *= 0.2 + 0.8 * smoothstep (0., 1., zLen - ((sign (ro.z) > 0.) ? 10. : 5.) - abs (ro.z));
  if (isWat) col = mix (vec3 (0., 0.1, 0.), 0.9 * col, 1. - 0.8 * pow (dot (- rdo, vnw), 2.));
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv, mMid, ut, mSize, msw;
  float el, az, zmFac, asp, sr;
  int vuId, regId;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mSize = (1./5.) * vec2 (asp, 1.);
  mMid = (1. - mSize.y) * vec2 (asp, -1.);
  ut = abs (uv - mMid) - mSize;
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    msw = 2. * mPtr.xy - mMid / vec2 (asp, 1.);
    if (Maxv2 (abs (msw)) < mSize.y) {
      regId = 1;
      msw /= 2. * mSize.y;
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  if (Maxv2 (ut) < 0.) {
    uv = (uv - mMid) / mSize.y;
    vuId = 1;
  }
  if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  az = 0.;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * msw.x;
    el += 0.7 * pi * msw.y;
  } else {
    az += 0.9 * pi * (2. * SmoothBump (0.25, 0.75, 0.24, mod (0.01 * tCur, 1.)) - 1.);
    el -= 0.07 * pi * sin (0.02 * pi * tCur);
  }
  el = clamp (el, -0.35 * pi, -0.08 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 3., -30.);
  zmFac = (vuId == 0) ? 3.5 : 7.;
  dstFar = 150.;
  sunDir = vuMat * normalize (vec3 (-1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi)) / zmFac;
    rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (Maxv2 (ut) < 0. && Minv2 (abs (ut)) * canvas.y < 3.) col = vec3 (0.8, 0.5, 0.2);
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrCaps2Df (vec2 p, float r, float h)
{
  return length (p - vec2 (0., clamp (p.y, - h, h))) - r;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

float scale = 16.;

void pixel2cubial(vec2 uv, out vec3 cellCoord, out vec3 cellUv) {
	#define HX vec2(1.0, 0.0)
	#define HY vec2(0.5, 0.866)
	#define HMAT mat2(HX, HY)

	vec2 axial = inverse(HMAT) * uv;
	vec3 cubial = vec3(axial, -axial.x - axial.y);
	cellCoord = round(cubial);
	vec3 dist = abs(cubial - cellCoord);
	if (dist.x > dist.y && dist.x > dist.z) {
		cellCoord.x = -cellCoord.y - cellCoord.z;
	}
	else if (dist.y > dist.z) {
		cellCoord.y = -cellCoord.x - cellCoord.z;
	}
	else {
		cellCoord.z = -cellCoord.x - cellCoord.y;
	}

	cellUv = cubial - cellCoord;
}

// cubialDistance
// finds the distance between 2 hex cell positions
float cubialDistance(vec3 a, vec3 b) {
	vec3 c = abs(a - b);
	return max(c.x, max(c.y, c.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    uv *= scale;

    // find cubial coordinate
	vec3 cellCoord, cellUv;
	pixel2cubial(uv, cellCoord, cellUv);

	vec3 col = cos(cellCoord + iTime) * 0.5 + 0.5;

    vec3 dist = abs(cellUv.xyz - cellUv.zxy);

    // outline
	float s = smoothstep(0.0, 0.05, 1.0 - max(dist.x, max(dist.y, dist.z)));
	col *= s;

    fragColor = vec4(col,1.0);
}

float scale = 16.0;

bool flatTop = true;

vec4 rect2hex(vec2 p){
	vec2 var = flatTop ? vec2(1., .5) : vec2(.5, 1.);

    vec2 s = flatTop ? vec2(1.7320508, 1.) : vec2(1., 1.7320508);

	vec4 hC = floor(vec4(p, p - var)/s.xyxy) + .5;
	vec4 h = vec4(p - hC.xy*s, p - (hC.zw + .5)*s);

    //vec4 hC = floor(vec4(p/s, p/s + .5));
	//vec4 h = p.xyxy - vec4(hC.xy + .5, hC.zw)*s.xyxy;

	return dot(h.xy, h.xy) < dot(h.zw, h.zw) ? vec4(h.xy, hC.xy) : vec4(h.zw, hC.zw + .5);
}

float edgeDistance(vec2 p) {
	p = abs(p);

	float var = flatTop ? p.y : p.x;

    vec2 s = flatTop ? vec2(1.7320508, 1.) : vec2(1., 1.7320508);

	return max(dot(p, s*.5), var);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    uv *= scale;

    vec4 uvHex = rect2hex(uv);

    float ed = edgeDistance(uvHex.xy); // Edge distance.
	float cDist = dot(uvHex.xy, uvHex.xy); // Relative squared distance from the center.

    vec3 col = vec3(0.);
	col.rg = cos(uvHex.zw + iTime) * 0.5 + 0.5;

    // outline
	col *= 1.0 - smoothstep(0.48, 0.5, ed);

    fragColor = vec4(col,1.0);
}

#define PI 3.1415926535

vec2 rotate(vec2 original, float angle, vec2 pivot){
	mat2 rotation = mat2(cos(angle), sin(angle), -sin(angle), cos(angle));
	vec2 final = original;
	final -= pivot;
	final *= rotation;
	final += pivot;
	return final;
}

vec3 simplexGrid (vec2 uv) {
	vec3 xyz = vec3(0.0);

	float f2d = 0.3660254038; //(sqrt(3.) - 1.) / 2.;
	f2d *= (uv.x + uv.y);

    vec2 skew = vec2(0.0);

	skew.x = uv.x + f2d;
	skew.y = uv.y + f2d;

    //skew.x = 1.1547 * uv.x;
	//skew.y = uv.y + 0.5 * skew.x;


	if (skew.x > skew.y) {
		xyz.xy = 1.0 - vec2(skew.x, skew.y - skew.x);
		xyz.z = skew.y;
	}
	else {
		xyz.yz = 1.0 - vec2(skew.x - skew.y, skew.y);
		xyz.x = skew.x;
	}

	return xyz;
}

float scale = 10.;
float lineSize = 0.05;
float lineBlur = 0.01;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

	uv = rotate(uv, -PI/4., vec2(0.));

	uv *= scale;

    vec3 uvT = simplexGrid(uv);
	vec3 cellCoord = floor(uvT);
	vec3 cellUv = fract(uvT);

    vec3 col = vec3(0.0);
	col = cellUv;
	col = cos(cellCoord+iTime) * 0.5 + 0.5;

	// outline
    for (int i = 0; i < 3; i++) {
        col *= smoothstep(lineSize / 2. - lineBlur, lineSize / 2. + lineBlur, cellUv[i]);
        col *= smoothstep(lineSize / 2. - lineBlur, lineSize / 2. + lineBlur, 1. - cellUv[i]);
    }

    fragColor = vec4(col,1.0);
}

#define LINESIZE 0.1

vec2 scale = vec2(20., 20.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    //uv *= SCALE;
    uv *= scale;

	vec2 cellCoord = floor(uv);
	vec2 cellUv = fract(uv);

	vec3 col = vec3(0.0);
	col.rg = cos(cellCoord + iTime) * 0.5 + 0.5;

    // outline
    float lineSizeY = LINESIZE * scale.y / scale.x;
	col *= step(LINESIZE/2., cellUv.x);
    col *= 1. - step(1. - LINESIZE/2., cellUv.x);
	col *= step(lineSizeY/2., cellUv.y);
    col *= 1. - step(1. - lineSizeY/2., cellUv.y);

    fragColor = vec4(col,1.0);
}

vec3 sinCol(int i, vec2 uv)
{
    return 0.5 + 0.5 * cos(float(i) + iTime + uv.xyx + vec3(0,2,4));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float stepMax = bpm2ms(BPM);
    float stepTime = mod(iTime, stepMax);

    stepCounter = int(mod(iTime / stepMax, float(SEQ_LENGTH)));

    vec2 uv = fragCoord/iResolution.xy;

    float xP = stepTime / (stepMax * float(SEQ_LENGTH));
    float x = float(stepCounter) / float(SEQ_LENGTH);

    if (uv.x <= x + xP)
        fragColor = vec4(sinCol(stepCounter, uv + vec2(stepTime)), 1.0);
    else
        fragColor = vec4(sinCol(stepCounter, -uv * vec2(stepTime)) * vec3(0.1),1.0);

    if (uv.x <= x)
        fragColor *= vec4(vec3(0.5), 1.0);
}

