// https://en.wikipedia.org/wiki/Piano_key_frequencies
// f(n) = 2^((n - 49) / 12) * 440.
float KeyToHz(float key)
{
    float k = (key - 49.) / 12.;
    return pow(2., k) * 440.;
}

#define KEY_0_A KeyToHz(49.)
#define KEY_0_C KeyToHz(52.)
#define KEY_0_D KeyToHz(54.)
#define KEY_0_E KeyToHz(56.)

// Toggle me between [0, 1]
#define USE_FM 0
#define USE_LFO 0

float Amp(float a)
{
    return a * 0.1;
}

float SquareWave(float a, float f, float p, float t)
{
    return Amp(a) * sign(sin(T_PI * f * t + p));
}

float SinWave(float f, float a, float t)
{
    return Amp(a) * sin(T_PI * f * t);
}

float SawWave(float p, float a, float t)
{
    float period_t = t/p;
    return Amp(a) * (2.0 * (period_t - floor(0.5 + period_t)));
}

float FMSin(float time)
{
    // modulating frequency
    float mf = 1. / 40.;
    return SinMod(time, mf, 0., 1.);
}

float LFO(float time)
{
    // modulating frequency
    float mf = 1. / 8.2;
    return SinMod(time, mf, 0., 1.);
}

float AmpEnvelope(float s, float curve_amount, float t)
{
    return s * exp(curve_amount * t);
}

//
// 1x8 Sequencer
//
// The key of each tone is placed in 'seq'
//
// Each step lasts 1/4 bar for a given BPM before moving to the next step
//
// The tone is generated from 'SinWave' function. You can also generate a more interesting tone by changing USE_FM 0 to USE_FM 1
//
// The tone for a given step is put through the 'AmpEnvelope' which reduces the amplitude over a short duration (attuenuating the signal)
//

vec2 mainSound( int samp, float time )
{
    float seq[SEQ_LENGTH] = float[SEQ_LENGTH] (KEY_0_A, KEY_0_C, KEY_0_D, KEY_0_E, KEY_0_A, KEY_0_A, KEY_0_C, KEY_0_A);

    float stepMax = bpm2ms(BPM);
    float stepTime = mod(time, stepMax);    

    stepCounter = int(mod(time / stepMax, float(SEQ_LENGTH)));

    float f = seq[stepCounter];
    float a = 1.5;
    float envelope_curve = -8.;

    // set USE_FM to 0 or 1 above
#if USE_FM
    f *= 0.5; // lower octave
    envelope_curve = -12.; // sharper curve
    float m = FMSin(stepTime);
    f = m*m - (f *.8); // some funky shit
    a += 4.; // VERY VERY WAY MORE STRONGER... volume
#if USE_LFO
    f = (m*m)*0.5; // changing sound, more wub
#endif    
#endif

    float s = SinWave(f, a, stepTime);

    // set USE_LFO to 0 or 1 above
#if USE_LFO
    s *= LFO(stepTime);
    envelope_curve = -3.;
#endif

    float e = AmpEnvelope(s, envelope_curve, stepTime);

    return vec2(e);
}

