float nSpikes = 3.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - (fragColor.xy = iResolution.xy) ) / fragColor.y ;

    float t = mod(iTime * 0.2, PI*2.0);

    uv = rotate(uv, t, vec2(0.0));

    vec2 polarUv = cart2pol(uv);

    vec3 hsb = vec3(1.0);

	hsb.y = 0.6;

    hsb.z = (1. - polarUv.x) * 0.5 + 0.5;

    hsb.x = -fract(iTime * 0.1);

    t = sin(iTime * 1.0);

    float s = 0.5 + t * sin(polarUv.y * nSpikes) * 0.1;

    float c = easySmoothstep(s, 0.05, polarUv.x);

    if (c < 0.5) {
		hsb.x += polarUv.x * 0.2;
	}
	else {
		hsb.x += polarUv.x * 0.1;
		hsb.x = mod(hsb.x + 0.2, 1.0);
	}

    if (c >= 0.5) {
        hsb.z *= smoothstep(0.5, 0.6, c);
    }
    else {
        hsb.z *= smoothstep(0.5, 0.4, c);
    }

    vec3 col = hsb2rgb(hsb);

    fragColor = vec4(col,1.0);
}

