//a faster and insignificantly less precise 3d line segment distance.
//https://www.shadertoy.com/view/ssyXzy
float angularSeg16(vec3 d, vec3 a, vec3 b)
{
    b -= a;
    float e = dot(d, a),
      f = dot(d, b),
	  w = dot(b, b) - f * f;
    vec3 x = a + b * (abs(w) < 1e-7 ? .5 : clamp((e * f - dot(b, a)) / w, 0., 1.)); // closest point on segment
    float s = dot(d, x);
    return s < 0. ? 3e38 // behind camera -> infinity
        : dot(x, x) - s * s; // AngularDistanceSquaredDotted
}
bool segmentMask(vec3 d,vec3 a,vec3 b //viewRayDir, segmentStart,segmenrEnd
){float tiny=.001 ;
    float g =angularSeg16(d, a, b)+tiny;
    g = sqrt(g); // un-square squared signed distance
    g -= .022+tiny; // thickness
    return (g <= .01)
    ;}

