const float M_PI = 3.14159265358979323846264338327950288;

float mandel (float cx, float cy) {
	float x = 0.;
	float y = 0.;
	int i;
	for (i = 0; i < 64; i++) {
		if ((x*x + y*y) > 4.) {
		  break;
		}
		float xtemp = x*x - y*y + cx;
		y = 2.*x*y + cy;
		x = xtemp;
	}
	return sqrt(float(i) / 64.);
}

// 2d rotation matrix: rot(angle) * vector will rotate the vector by angle clockwise
mat2 rot(float angle)
{
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

bool isInCircle(vec2 centre, float radius, vec2 point)
{
    // return true if point is closer to centre than radius
    return length(point - centre) < radius;
}

// ---------------- helpers above ----------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coordinates from centre - centre is (0, 0)
    vec2 uv = (fragCoord - vec2(iResolution.x, iResolution.y) / 2.) / iResolution.y;

    float speedup = 0.25;

    vec2 t = 6. * (uv - vec2(1.5 * iResolution.x / iResolution.y * (fract(iTime * speedup) - 0.5),
                             0.2 * sin(iTime)));
    t = rot(cos(iTime) * 0.2) * t;
    vec4 col = vec4(mandel(t.x, t.y), 0.1, 1, 1);

    float bubbleR = 0.1;
    float bubbleSize = bubbleR + bubbleR / 2. * sin(iTime / 5. + cos(uv.x * 5.));
    for (int i = 0; i < 20; i++)
    {
        float weirdTime = iTime * float(i + 1) / 20.;
        vec2 bubbleCentre = vec2(0.5 * sin(iTime + float(i * i * i)),
                                 1.5 * (weirdTime - floor(weirdTime) - 0.5));
        if (isInCircle(bubbleCentre, bubbleSize, uv))
        {
            col += vec4(0.1, 0.1, 0.1, 1);
        }
    }
    // Output to screen
    fragColor = col;
}

// Based on https://blog.demofox.org/2020/05/25/casual-shadertoy-path-tracing-1-basic-camera-diffuse-emissive/
// and https://blog.demofox.org/2020/06/06/casual-shadertoy-path-tracing-2-image-improvement-and-glossy

// https://64.github.io/tonemapping/#uncharted-2
vec3 Uncharted2(in vec3 color) {
    color *= 2.0;

    float A = 0.15, B = 0.50, C = 0.10;
    float D = 0.20, E = 0.02, F = 0.30;
    color = (((A * color + C * B) * color + D * E) / ((A * color + B) * color + D * F)) - E / F;

    //float whiteMax = 4.0;
    //color /= (((A * whiteMax + C * B) * whiteMax + D * E) / ((A * whiteMax + B) * whiteMax + D * F)) - E / F;
    color *= 1.9335;

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    fragColor.rgb /= fragColor.a; // Average
    fragColor.rgb = Uncharted2(fragColor.rgb); // Tonemap
    fragColor.rgb = pow(fragColor.rgb, vec3(0.4545)); // Gamma correct
}

