HitInfo traceRay(in Ray ray) {
    HitInfo hit = HitInfo(-1.0, vec3(0.0), vec3(0.0), vec2(0.0), -1);

    // Ground plane
    addRayHit(hit, planeIsect(ray, mat4(1.0), 1));

    // Big sphere light
    addRayHit(hit, sphereIsect(ray, Translate(vec3(0.0, 1.0, -5.0)), 2));

    // Silver metallic spheres
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-2.0, 0.5, -5.0)) * Scale(vec3(0.5)), 3));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(2.0, 0.5, -5.0)) * Scale(vec3(0.5)), 3));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(0.0, 0.15, -4.05)) * Scale(vec3(0.15)), 3));

    // Green metallic spheres
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-2.0, 0.4, -3.0)) * Scale(vec3(0.4)), 4));
    addRayHit(hit, sphereIsect(ray, Translate(vec3(-1.0, 0.4, -3.0)) * Scale(vec3(0.4)), 5));
